css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news24.com") {
	div#socialbar-container, div#header div.mobile, div#header div#dropdown, div#sponsored_holder, div#bw-wrapper, div#footer, div#article_toolbox_bot, div#other_stories, div#article_toolbox_topright, div#the_accordion, div#inside_news, div.srch24form, div#divKeywordsListing, div#timeout_box, div#sareunite_box, div#dating_box, div#buybook_box, div#sponsor_box, div#google_box, div#games_multi_box, div.follow_news, div.newsletter_signup, div#flirtaroo_box, div#leaderboard, .ad300X600, .ad300X250, .ad468X60, .right_banners, .adfix, div#promotion_box, div#most_box, div.facebook_block, .article .col300, #latest_comment_box, #mynews_box, div#right_column div.spacer, img#imgSponsor, div.ad626X35, div#TaraBanner, div#TrafficSynergy, div#dtLastUpdated {
		display: none !important;
	}
	
	div#weather_box, div#indicators_box {
		margin-top: 1em !important;
	}

	div.main_wrap {
		margin-top: -4em !important;	
	}

	#header .main_logo {
		left: 10px !important;
		top: 15px !important;	
	}

	div.feature_head.first {
		left: 260px !important;
		top: 28px !important;
	}

	div.feature_head.second {
		left: 620px !important;
		top: 28px !important;
	}

	div.main_wrap {
		margin-top: -4em;
		background-image: none !important;
		border: none !important;
	}
	
	div.content_wrap, div.vert_line {
		background-image: none !important;	
	}

	#most_box, #mynews.mynews_page #most_box, #weather_box, #indicators_box, #mynews_box, #accordion, #google_box, #latest_comment_box, #links_box, #blogged_box, #related_box, #other_box, #videoday_box, .topitem_box, #score_box {
		border-top: 1px solid #c6c6c6 !important;
		border-left: 1px solid #c6c6c6 !important;
	}
}
