css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ogame.org"),
domain("ogame.com.br"),
domain("ogame.cz"),
domain("ogame.de"),
domain("ogame.dk"),
domain("ogame.com.es"),
domain("ogame.fr"),
domain("ogame.gr"),
domain("ogame.com.hr"),
domain("ogame.hu"),
domain("ogame.it"),
domain("ogame.jp"),
domain("ogame.lt"),
domain("ogame.lv"),
domain("ogame.nl"),
domain("ogame.no"),
domain("ogame.pl"),
domain("ogame.com.pt"),
domain("ogame.ro"),
domain("ogame.rs"),
domain("ogame.ru"),
domain("ogame.se"),
domain("ogame.si"),
domain("ogame.sk"),
domain("ogame.com.tr"),
domain("ogame.tw"),
domain("ogame.us") {

div.planet {
  width: 125px !important;
}

div.planetHead {
  width: 118px !important;
}

div.planetname {
  width: 105px !important;
}

div.planetImg {
  width: 116px !important;
}

div.planetImg > img {
  width: 116px !important;
  height: 116px !important;
}

div.planetData li.textLeft {
  float: left !important;
  width: auto !important;
}

div.planetData li.textRight {
  width: auto !important;
  float: right !important;
}

div.planetData li.textCenter {
  width: auto !important;
  float: none !important;
}

div.row {
  width: 124px !important;
}

div.values {
  width: 118px !important;
}

#wrapTL {
  height: 169px !important;
}

#tab-left {
  margin-top: 10px !important;
}

div.header {
  padding-top: 0px !important;
}

div.planetData li.textLeft, div.planetData li.textRight, div.planetData li.textCenter {
  font-size: 0.8em !important;
}

div.planetname, div.header, div.values {
  font-size: 1.1em !important;
}

}
