css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("javaspecialists.eu") {

#sharedock, #fadeinbox, #_atssh, .main > table:nth-child(1) > tbody > tr:nth-child(1), .rightcol_bg, iframe {
  display: none;
}

.nav_bg, .nav, .nav2, .top_bg, .bot_bg, th.footbar,
.main > table:nth-child(2) table, .main_bg
{
  width: auto !important;
}

.top_bg, .bot_bg {
  background: none !important;
  height: 1ex !important;
}

.left, .main > table:nth-child(2) > tbody > tr > td {
  height: auto !important;
}

.main > table:nth-child(2) > tbody > tr > td > table:nth-child(4) {
  width: 100% !important;
}

.main_bg {
  background: none !important;
  padding: 1ex !important;
}

.newsinfo {
  margin: 0 !important;
  padding: 1ex !important;
  font-size: 72% !important;
}

.newsinfo input {
  margin: 0 !important;
  padding: 0 !important;
  font-size: 80% !important;
}

}
