css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("krautchan.net") {


/* *{font-family: Comic Sans MS !important;}*/

body{
padding: 0px !important;
margin: 0px !important;
}


/*RSS-Container*/
div[style="float: right;"]{
/*position: fixed;*/
background-color: gray;
text-align: center;
padding-top: 3px;
margin: 3px;
width: 120px;
color: black;
-moz-border-radius-bottomleft: 4px;
-moz-border-radius-bottomright: 4px;
border: 2px solid gray;
font-size: 0.8em;
}

div[style="float: right;"]:hover{
padding-top: 14px;
}

div[style="float: right;"] > a{
color: black !important;
text-decoration: none !important;
}

div[style="float: right;"] > a:hover{
color: gray !important;
}

/*Navigation*/
div[style="float: left;"]{
position: fixed !important;

padding: 1px;
width: 100%;
height: 18px;
text-align: center;
color: darkgray;

-moz-box-shadow: 0 4px 6px rgba(0, 0, 0, 0.6);
background: -moz-linear-gradient(top, rgba(73,73,73,1) 0%, rgba(0,0,0,1) 100%);
border-bottom: 1px solid #000000;

z-index: 1000;
}

div[style="float: left;"] > a {
color: gray !important;
text-decoration: none !important;
}

div[style="float: left;"] > a:hover {
color: white !important;
}

/*Seitenzahlennavigation*/

div[style="position: absolute; left: 5px;"]{
border: 2px solid gray;
top: 0px;
left: 2px !important;
height: 22px;
position: fixed !important;
background-color: gray;
color: white;
font-size: 0.9em;
-moz-border-radius-bottomleft: 4px;
-moz-border-radius-bottomright: 4px;
}

div[style="position: absolute; left: 5px;"]:hover{
top: 20px; 
}

div > table > tbody > tr > td > a:hover{
color: white !important;
}
td {
border-spacing: 0px;
border-collapse: collapse;
}
table {
border: 0px !important;
}

a{
color: #313370 !important;
text-decoration: none !important;
}

