css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dmoz.org") {

/* Home page */

#catalogs {
margin-top: 15px !important;
}

#catalogs span a, samp a {
text-decoration: none !important;
}
#catalogs span a:hover, samp a:hover {
color: #669933 !important;
}

#catalogs span{
background: #EEE !important;
padding: 2px 0 2px 2px !important;
}

.catalog {
border-top: 2px solid #CCC !important;
padding-top: 12px !important;
margin-top: 12px !important;
}



/* Common parts */

#hd-bar {
background-color:#EEE !important;
}

.searchNod {
height:24px !important;
width:300px !important;
padding: 5px 0 5px 5px !important;
}

#bd-cross .center {
background: #D7FFAF !important;
margin: 0 !important;
padding: 10px 0 !important;
}

/* Other pages */

div.navigate {
margin: 15px 0 0 0 !important;
padding: 0 0 0 1% !important;
width: 99% !important;
}
div.descript {
margin: 0 1% 0 0 !important;
background:#F0F0F0 !important;
height:30px !important;
padding:4px 15px !important;
text-align:center !important;
}

ul.directory li a {
font-size:1.33em;
font-weight:bold;
text-decoration: none !important;
}
ul.directory li a:hover {
color: #669933 !important;
}

.alphanumeric a {
text-decoration:none !important;
}


.fieldcap ul.directory-url {
border-left: 15px solid #D7FFAF !important;
width: 70% !important;
}
ul.directory-url li a {
text-decoration: none !important;
}



.fieldcap legend {
border-top: 5px solid #D7FFAF !important;
padding: 5px 0 !important;
width: 100% !important;
}



/* Languages */


ul.language {
overflow: auto !important;
max-height: 400px !important;
width: 60% !important;
}
ul.language a {
text-decoration: none !important;
}


}
