css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Hide Navbar */
#main-window[stylish-url="about:config"] #navigator-toolbox > *:not([id="TabsToolbar"]):not([id="toolbar-menubar"]) {
visibility: collapse !important; 
}

@-moz-document url("about:config") {

#warningScreen,
#config { 
background: #111 !important;
}

#warningBox {
background: black!important;
color: #ccc !important;
border: 0 !important;
}

#warningTitle {
border-bottom: 0 !important;
}

#exclam {
list-style-image: url("data:image/png;base64,") !important;
}

#warningButton,
#warningButton:focus {
-moz-appearance: none !important;
color: #ddd !important;
background: rgba(0,0,0,.5) !important;
box-shadow: 0 0 4px rgba(161,161,161,.9) inset !important;
border-radius: 4px !important;
border: 1px solid transparent !important;
}

#warningButton:hover {
color: yellow !important;
background: rgba(0,0,0,.9) !important;
}

#filterRow label[value="Filter:"]  {
color: #ddd !important;
}

#textbox.compact {
-moz-appearance:none !important;
background: #000 !important;
-moz-border-radius: 5px !important;
border: 0 !important;
color: #eee !important;
margin-right: 20px !important
}

#configTree  {
-moz-appearance:none !important;
color: #ddd !important;
font-size: 14px !important;
border: 1px solid #000 !important;
margin:5px 20px 26px 20px !important;
background: #000 !important
}

treecol,
.treecol-image {
-moz-appearance: none !important;
color: #ddd !important;
background: rgba(0,0,0,.3) !important;
box-shadow: 0 0 4px rgba(161,161,161,.9) inset !important;
border: 0 !important;
padding: 0 2px 0 11px !important;
} 

.treecol-text {
text-align: center !important;
}

treecol:hover {
color: lightblue !important;
}

treecol[sortDirection] {
color: royalblue !important;
}

.treecol-sortdirection {
width: 9px  !important;
margin-right:3px !important;
}

.tree-columnpicker-icon {
list-style-image: url("data:image/gif;base64,R0lGODlhDgAMAIAAAP///////yH5BAEAAAEALAAAAAAOAAwAAAIbjI+pa+D/EjizSQgl3agG71lM12VU9DkjtyoFADs=") !important;
margin-left:-8px !important;
}

treechildren {
margin-left: 6px !important;
}

.tree-rows {
background: #000 !important;
}

treechildren::-moz-tree-row(hover) {
  background-color: #111 !important;
}

treechildren::-moz-tree-row(selected),
treechildren::-moz-tree-row(selected,hover){
  background-color: #333 !important;
}

treechildren::-moz-tree-cell-text(hover) {
  color: yellow !important;
}

treechildren::-moz-tree-cell-text(selected),
treechildren::-moz-tree-cell-text(selected,hover){
  color: lightblue !important;
}

#configTreeBody::-moz-tree-cell-text(user) {
  color: tomato !important;
}

/*--Scrollbars by makondo--*/

scrollbar * { 
-moz-appearance: none !important;
background: none !important; 
}

scrollbar { 
-moz-appearance: none !important;
background: none !important;
border: none !important; 
}

scrollcorner { 
-moz-appearance: none !important;
background: none !important; 
width: 0 !important;
height: 0 !important; 
}

scrollbar thumb { 
-moz-appearance: none !important; 
min-height: 50px !important;
width: 0 !important;
border: none !important;
background:  transparent url("data:image/gif;base64,") center no-repeat !important; 
opacity: .2 !important; 
}

scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"] {
margin-left: 0px !important; 
margin-right: 1px !important; 
}

scrollbar[orient="vertical"] > scrollbarbutton[type="increment"] { 
margin-right: 1px !important; 
}

scrollbarbutton {
border: none !important;
background: transparent url(data:image/png;base64,) center no-repeat !important;
opacity: .2 !important; 
}

}
