css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

#breadcrumbs {
font-size:1em !important;
margin: 0 0 0 -20px !important;
color: #5573A0 !important;
}
#breadcrumbs a {
padding: 10px 0px 20px 0px !important;
}

#search-terms {
width: 250px !important;
height: 25px !important;
padding: 5px !important;
font-size: 1.1em !important;
margin: 10px 10px 10px 30px !important;
}
#search-submit {
width: 100px !important;
height: 25px !important;
font-size: 1em !important;
}

#other-links a {
font-size: 1.3em !important;
margin: 0 6px !important;
}
#other-links {
margin: 6px 40px 0px 0px !important;
}

h1 {
color: #FFF !important;
padding: 5px 0 5px 20px !important;
margin-top: 37px !important;
width: 100% !important;
}

a {
text-decoration: none !important;
color: #5573A0 !important;
}
a:visited {
color: #9043CC !important;
}
a:hover {
color: #000 !important;
}

#search-options {
margin-top: 25px !important;
color: #666 !important;
}
#search-options input[name="search_terms"] {
width: 250px !important;
height: 25px !important;
padding: 5px !important;
font-size: 1.1em !important;
margin: 0 5px !important;
}
#search-options select {
height: 25px !important;
font-size: 1.1em !important;
margin: 0 5px !important;
}
#search-options input[type="submit"] {
width: 255px !important;
height: 25px !important;
font-size: 1.1em !important;
margin: 0 0 0 600px !important;
}

#style-list {
padding: 10px 20px !important;
height: 315px !important;
}
#style-list ul, li {
padding-top: 1px !important;
}

.pagination {
font-size: 1.25em !important;
}
.pagination a {
padding: 3px !important;
}

/* Styles pages */

#style-meta {
color: #666 !important;
font-size: 1em !important;
padding: 8px 0 8px 8px !important;
border-left: 3px solid #AAA !important;
}

#install {
color: #FFF !important;
}

.screenshot {
max-width:700px !important;
}

/* Exceptions */

#SiteContainer #Header ul li a {
color: #FFF !important;
}
#SiteContainer #Header .TabOn a {
color: #000 !important;
}

}
