css:
@-moz-document url-prefix("https://accounts.google.com/ServiceLogin?service=mail"),
url-prefix("https://accounts.google.com/ServiceLogin?passive=1209600&continue=https%3A%2F%2Faccounts.google.com"),
url-prefix("https://gmail.com"){

/* Main Style */
body {
background: #fff;
position: absolute;
height: 100%;
min-width: 100%;
}

.wrapper {
position: relative;
min-height: 100%;
}

/*---- Header ----*/

.google-header-bar {
display: none !important;
}

/*---- Sign In ----*/

.sign-in {
float: right;
position: fixed !important;
width: 424px !important;
height: 260px !important;
top: 50% !important;
left: 50% !important;
margin-top: -240px !important;
margin-left: -225px !important;
}

.signin-box h2 strong {
display: inline-block;
position: absolute;
right: 0;
top: 1px;
height: 42px !important;
width: 48px !important;
background: transparent url('https://www.gstatic.com/accounts/services/default/googlemail.png') no-repeat !important;
}

.signin-box h2 {
font-size: 16px;
line-height: 17px;
height: 16px;
margin: 0 0 1.2em;
position: relative;
color: #999 !important;
}

.signin-box, .accountchooser-box {
margin: 12px 0 0;
padding: 20px 25px 15px;
background: #fff !important;
border: 1px solid #E5E5E5;
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
-moz-border-radius: 6px !important;
-webkit-box-shadow: 0 8px 6px -6px #E5E5E5 !important;
-moz-box-shadow: 0 8px 6px -6px #E5E5E5 !important;
box-shadow: 0 8px 6px -6px #E5E5E5 !important;
}

.signin-box input[type="text"], .signin-box input[type="password"] {
width: 100%;
height: 32px;
font-size: 15px;
direction: ltr;
border-radius: 6px !important;
-webkit-border-radius: 6px !important;
-moz-border-radius: 6px !important;
}

.signin-box .email-label, .signin-box .passwd-label {
font-weight: bold;
margin: 0 0 .5em;
display: block;
-webkit-user-select: none;
-moz-user-select: none;
user-select: none;
color: #555 !important;
}

/*---- Product promo ----*/
.product-info {
margin: 0 385px 0 0;
display: none !important;
}

.mail .mailPromo {
border: 1px solid 
#EBEBEB;
margin-top: 25px;
padding: 10px 10px 0px 10px;
display: none !important;
 }

/* Footer */
.footer ul {
display: none !important;
 }
}


/* Logout Screen */
@-moz-document url-prefix("https://accounts.google.com/ServiceLogin?service=mail"){
body {
position: absolute;
height: 100%;
min-width: 100%;
background: #fff !important;
}
}

/* Logged in Screen */
@-moz-document url-prefix("https://mail.google.com/mail/"){

.AT {
display: none !important;
}

}
