img[alt="sticky"], img[alt="closed"] {height: 0 !important; width: 0 !important; padding: 9px; vertical-align: -1px;}
img[alt="sticky"] {background: url("http://i.imgur.com/anSWD.png") no-repeat;}
img[alt="closed"] {background: url("http://i.imgur.com/mkRCN.gif") no-repeat;
}

.filetitle, .replytitle {
color: #d0cc40 !important;
font-weight:bold !important;
font-size: 15px;
}

.doubledash {
visibility: hidden !important;
}

th {
 background: #121212 !important;
 padding: .2em !important;
 font-size: small;
 width: 200px !important;
}

#hd {
display: none;
}

.spoiler
  { 
   
    background:url("http://i.imgur.com/1ctDd.png") !important;
    color: transparent !important; 
}

.spoiler[style^="color: rgb(255, 255, 255)"]
