css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("incisozluk.info"), domain("incisozluk.org") {
body.bgleft > small, body.bgleft > center {
  display: none !important;
}
body.bgtop img {
  max-width: 160px;
  max-height: 48px;
}
input[type=button], input[type=submit] {
  background-image: -moz-linear-gradient(top, #555, #666);
  -moz-border-radius: 3px;
  border: 1px solid #333 !important;
  margin: 2px;
  padding: 3px 7px;
}
body {
  background: #bbb -moz-linear-gradient(top, #999, #bbb, #bbb) no-repeat !important;
}
a:link {
  color: #0016B5 !important;
}
td.but, a.but {
  border: none !important;
  background-image: -moz-linear-gradient(top, #555, #666);
  -moz-border-radius: 3px;
}
td.but, .nav td {
  height: 22px !important;
  padding: 0 5px !important;
}
td.but a, a.but {
  padding: 0 5px !important;
  color: #fff !important;
  cursor: pointer !important;
}
body > br + table[width="100%"], body > title + table[width="100%"] {
  margin: 0 0 0 10px !important;
  background-image: -moz-linear-gradient(top, #bbb, #bbb, #ccc) !important;
  -moz-border-radius: 5px;
  display: inline-block;
  width: auto !important;
}
body > br + table[width="100%"] td, body > title + table[width="100%"] td {
  width: auto !important;
  height: auto !important;
}
body > br + table[width="100%"] h1, body > br + table[width="100%"] h3, body > title + table[width="100%"] h1, body > title + table[width="100%"] h3 {
  margin: 0;
  font-size: 1.1em !important;
  padding: 8px 12px 10px 14px;
}
body > br + table[width="100%"] + div, body > title + table[width="100%"] + div {
  width: auto !important;
  position: relative;
}
#AutoNumber1[align=right] {
  position: absolute;
  top: -30px;
  left: -80px;
}
#AutoNumber1[align=right] br, #AutoNumber1 select {
  display: none !important;
}
body > table + div + p.eol {
  float: right;
  margin: 0 5px;
}
body > table + div + p.eol > font {
  display: none;
}
body > ol, ol + font > ol {
  margin-top: 10px;
  clear: both;
  padding-left: 10px;
  position: relative;
}
body > ol li, ol + font > ol li {
  background-image: -moz-linear-gradient(top, #ddd, #ccc, #ccc) !important;
  margin: 5px 0;
  padding: 10px 10px 30px;
  -moz-border-radius: 7px;
}
body > ol > div[id], ol + font > ol > div[id] {
  position: absolute;
  bottom: 6px;
  right: 6px;
  background-color: rgba(255, 255, 255, 0.7);
  -moz-border-radius: 7px;
  padding: 0 5px;
  opacity: 0.3;
}
body > ol > div[id]:hover, ol + font > ol > div[id]:hover {
  opacity: 1;
  background-color: #e5e5e5;
}
body > ol li a b, ol + font > ol li a b {
  font-weight: normal;
}
body.bgleft tr[onmouseover] {
  background-image: -moz-linear-gradient(top, #ddd, #ccc, #ccc) !important;
  margin: 5px;
  padding: 5px;
  -moz-border-radius: 3px;
  display: block;
  max-width: 200px;
  word-wrap: break-word;
}
body.bgleft tr.active[onmouseover] {
  background-image: -moz-linear-gradient(top, #444, #444, #222) !important;
}
body.bgleft tr.active[onmouseover] a, body.bgleft tr.active[onmouseover] {
  color: #fff !important;
  outline: none;
}
body.bgleft tr[onmouseover] td {
  width: auto !important;
}
body {
  padding: 0 15px;
}
select {
  font-size: 10px;
  padding: 1px;
}
font {
  word-wrap: break-word;
}
table[height="80%"] td {
  max-width: 120px;
}
ol {
  list-style-type: decimal !important;
}
.resim-ac span {
  display: inline-block;
  cursor: pointer;
  padding: 0px 4px;
  margin: 2px 4px;
  background: #eee;
  -moz-border-radius: 3px;
  color: #555;
  position: relative;
  z-index: 2;
}
}
