css:
@-moz-document url-prefix('http://thecolorless.net/chat') {

/* --------------------------------------------------------------  
   Reset default browser CSS.
   Based on work by Eric Meyer:
   http://meyerweb.com/eric/tools/css/reset/index.html
-------------------------------------------------------------- */

html,body,div,span,applet,object,iframe,h1,h2,h3,h4,h5,h6,p,blockquote,pre,a,abbr,acronym,address,big,cite,code,del,dfn,em,font,img,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td {
border:0;
outline:0;
font-size:100%;
vertical-align:baseline;
background:transparent;
margin:0;
padding:0;
}

body {
background:#111!important;
color:#fff!important;
line-height:14px!important;
font-size:12px!important;
font-family:Helvetica, Arial, sans-serif!important;
overflow:auto!important;
}

ol,ul {
list-style:none;
}

blockquote,q {
quotes:none;
}

blockquote:before,blockquote:after,q:before,q:after {
content:none;
}

:focus {
outline:0;
}

ins {
text-decoration:none;
}

del {
text-decoration:line-through;
}

table {
border-collapse:collapse;
border-spacing:0;
}

a img {
border:none;
}

#wrapper {
position:relative;
}

#header {
background:#222!important;
width:60%!important;
height:50px!important;
position:relative;
top:0!important;
z-index:100!important;
text-shadow:#fff 0 0 0!important;
clear:both!important;
float:left!important;
}

#name-area {
overflow:visible;
clear:none!important;
height:18px!important;
position:relative;
display:inline!important;
}

#header a {
display:block!important;
float:left;
margin-right:5px;
color:#999!important;
text-decoration:underline;
}

#header a:hover {
text-decoration:underline!important;
}

#send-message-area {
width:100%!important;
}

#home-link {
float:left!important;
margin-left:5px!important;
display:block!important;
}

#sendie {
float:left!important;
background:#fff;
display:inline!important;
border:0 solid #5a5a5a!important;
color:#5a5a5a;
width:85%;
margin-top:5px;
margin-bottom:5px;
-webkit-border-radius:0!important;
-moz-border-radius:0!important;
border-radius:0!important;
font-size:12px!important;
font-weight:600!important;
font-family:Helvetica, Arial, sans-serif!important;
clear:both!important;
padding:5px!important;
}

#characters-left {
display:inline!important;
float:left!important;
}

#content {
width:100%!important;
position:relative;
margin:0 auto;
}

#chat-wrap {
width:100%!important;
height:100%!important;
overflow:auto!important;
overflow-y:scroll;
padding-bottom:50px;
background:#111;
margin-top:60px!important;
position:relative;
z-index:1;
top:0!important;
}

#chat-area {
width:100%!important;
overflow:visible;
z-index:100;
padding:0;
}

.message,.status {
margin-bottom:10px;
display:inline!important;
clear:none!important;
}

.message {
background:#111!important;
background-position:83px 33px;
overflow:hidden;
width:auto!important;
padding-top:10px;
display:inline!important;
clear:right!important;
margin-left:10px!important;
}

.status {
margin-bottom:20px;
display:none!important;
clear:none!important;
}

.user {
width:auto!important;
line-height:18px;
float:left!important;
display:inline-block!important;
clear:left!important;
}

.user:after {
content:" "!important;
margin-left:3px!important;
}

.avatar {
border:0!important;
display:inline!important;
background:#111!important;
clear:none!important;
width:16px!important;
height:16px!important;
float:left!important;
left:0!important;
margin-left:5px!important;
}

.username {
display:inline!important;
text-align:left!important;
position:relative;
color:#fff!important;
text-decoration:none;
float:left!important;
clear:none!important;
font-size:120%!important;
font-weight:800!important;
margin-left:10px!important;
}

.inline-username {
color:#fff;
text-decoration:none;
clear:left!important;
}

.message .blurb {
display:block!important;
margin-left:0!important;
margin-right:0;
background:#111 center!important;
-moz-border-radius:0!important;
-webkit-border-radius:0!important;
border-radius:0!important;
font-weight:700!important;
float:left!important;
min-height:1px!important;
border:0!important;
margin-bottom:0!important;
max-width:100%!important;
text-shadow:rgba(0,0,0,0) 0 0 0!important;
white-space:0!important;
word-wrap:break-word;
clear:none!important;
padding:2px!important;
}

.message .blurb:after {
display:none!important;
clear:none!important;
}

.message .blurb a {
color:#00aeff!important;
}

.orange a {
color:#d58f19!important;
}

.darkblue a {
color:#3c629b!important;
}

.blue a {
color:#5d9dc2!important;
}

.red a {
color:#920003!important;
}

.magenta a {
color:#95005c!important;
}

.green a {
color:#1d8140!important;
}

.grey a {
color:#717171!important;
}

.mud_green a {
color:#777137!important;
}

.purple a {
color:#3c1773!important;
}

.lime_green a {
color:#5ab800!important;
}

.is_reply * {
-webkit-box-shadow:0 0 0 #ebebeb!important;
-moz-box-shadow:0 0 0 #ebebeb!important;
box-shadow:0 0 0 #ebebeb!important;
font-size:120%!important;
}

.is_reply .blurb {
text-shadow:1px 1px 6px #fff!important;
}

#notice {
background:#2b2b2b;
overflow:auto;
float:right!important;
}

#notice #wrapper {
width:1045px;
margin:0 auto;
}

#notice #content {
width:20%!important;
float:right!important;
position:relative;
text-shadow:#000 0 -1px 1px;
margin:0!important;
}

#notice h2 {
font-size:24px;
font-family:Arial, Helvetica, 'Helvetica Neue', Verdana, sans-serif;
font-style:normal;
font-weight:400;
letter-spacing:-1px;
margin-bottom:24px;
}

#notice p {
font-size:15px;
max-width:400px;
}

#go-away {
position:absolute;
top:24px;
right:130px;
}

#footer {
position:fixed;
top:0;
left:auto!important;
right:0;
width:40%!important;
background:#222!important;
text-align:center;
color:#999;
font-size:9px;
text-transform:uppercase;
z-index:100;
float:right!important;
height:40px!important;
padding:10px 0;
}

#footer a {
color:#bbb;
}

#whois-online {
position:fixed!important;
left:auto!important;
right:0!important;
top:40px!important;
bottom:auto!important;
margin-left:auto!important;
margin-right:0!important;
z-index:200;
display:block!important;
padding:20px;
}

#whois-online-container {
background:#111112;
width:75px!important;
height:auto!important;
border:1px solid #222223;
-webkit-border-radius:10px;
-moz-border-radius:10px;
border-radius:10px;
-webkit-box-shadow:0 0 20px #000;
-moz-box-shadow:0 0 20px #000;
box-shadow:0 0 20px #000;
position:fixed!important;
left:auto!important;
right:0!important;
top:auto!important;
bottom:0px!important;
margin-left:auto!important;
margin-right:0!important;
float:right!important;
padding:20px;
}

#close-whois-online {
color:#cb0419;
}

#whois-online .inline-username:hover {
text-decoration:underline;
}

#color-select {
display:block!important;
float:left!important;
margin-right:0!important;
background:#555556;
color:#fff;
cursor:pointer;
text-shadow:none;
-webkit-border-radius:0!important;
-moz-border-radius:0!important;
border-radius:0!important;
position:relative!important;
left:auto!important;
overflow:hidden!important;
}

#color-select ul {
list-style-type:none!important;
display:inline!important;
margin:0!important;
padding:0!important;
}

#color-select li {
display:hidden!important;
float:left!important;
background:#555556;
text-align:left;
padding:3px!important;
}

#color-select li:last-child {
-webkit-border-top-left-radius:0!important;
-moz-border-radius-topleft:0!important;
border-top-left-radius:0!important;
}

#color-select li:hover {
background-color:#666667;
}

#color-select #active-color {
display:inline!important;
padding-right:0!important;
background:url(http://imgur.com/EcmWl.png) no-repeat center right;
}

#active-color span {
font-weight:700;
}

.button {
display:hidden!important;
}

div::-webkit-scrollbar,div::-webkit-scrollbar-button:start:decrement,div::-webkit-scrollbar-button:end:increment,div::-webkit-scrollbar-button:vertical:increment,div::-webkit-scrollbar-track:enabled,div::-webkit-scrollbar-thumb:vertical,div::-webkit-scrollbar-thumb:horizontal,#you-are,#new-msg-post {
display:none!important;
}

.timestamp,#color-change,#color-select:hover li {
display:inline!important;
}

#new-msg-post{
display:none!important;
}

}
