css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
 * Shared
*/
@-moz-document url-prefix(http://), 
               url-prefix(https://)
{

/*
 * Low priority, fix common troubles, but let pages override.
*/
 button:not(:empty),
 input[type="file"],
 input[type="password"],
 input[type="text"],
 input:not([type]),
 input[type="button"]:not(:empty),
 input[type="reset"],
 input[type="submit"],
 select,
 textarea 
  {
    margin: 2px ;
    outline-width: 1px ;
    -moz-appearance: none !important; /* This must be forced. */
    line-height: 1em ;
    font-size: 9pt ;
    font-family: sans-serif ;
  }

/*
 * Black shadow top left
*/
 html:root > body input[type="file"]:focus,
 html:root > body input[type="password"]:focus,
 html:root > body input[type="text"]:focus,
 html:root > body input:not([type]):focus,
 html:root > body textarea:focus, 

 html:root > body input[type="file"]:hover,
 html:root > body input[type="password"]:hover,
 html:root > body input[type="text"]:hover,
 html:root > body input:not([type]):hover,
 html:root > body textarea:hover
  {
    background-image:
      url(data:image/png;base64,)
      !important;
    background-repeat: no-repeat !important ;
    -moz-border-radius: 0 !important ;
  }

}

/*
 * Clear-text, unencrypted forms
*/
@-moz-document url-prefix(http://) 
{

/*
 * Default
*/
 html:root > body button:not(:empty),
 html:root > body input[type="button"]:not(:empty),
 html:root > body input[type="file"],
 html:root > body input[type="password"],
 html:root > body input[type="reset"],
 html:root > body input[type="submit"],
 html:root > body input[type="text"],
 html:root > body input:not([type]),
 html:root > body select,
 html:root > body textarea 
  {
    border: 1px solid #bbb !important;
    color: #fff !important;
    outline-width: 1px !important;
    outline-style: solid !important;
    outline-color: #000 !important;
  }
 html:root > body button:not(:empty),
 html:root > body input[type="button"]:not(:empty),
 html:root > body input[type="reset"],
 html:root > body input[type="submit"],
 html:root > body option:hover
  {
    background-color: #282827 !important;
  }
 html:root > body input[type="password"],
 html:root > body input[type="text"],
 html:root > body input:not([type])
  {
    background-color: #848483 !important;
  }
 html:root > body option,
 html:root > body select,
 html:root > body textarea
  {
    background-color: #282827 !important;
  }

/*
 * Hover (mouse target if click occurs)
*/
 html:root > body button:hover:not(:empty),
 html:root > body input[type="button"]:hover:not(:empty),
 html:root > body input[type="reset"]:hover,
 html:root > body input[type="submit"]:hover
  {
    border: 1px solid #fff !important;
    background-color: #666 !important;
    color: #000 !important;
  }
 html:root > body input[type="file"]:hover,
 html:root > body input[type="password"]:hover,
 html:root > body input[type="text"]:hover,
 html:root > body input:not([type]):hover,
 html:root > body textarea:hover
  {
    border: 1px solid #fff !important;
    background-color: #333 !important;
  }
 html:root > body select:hover
  {
    outline-color: #fff !important;
  }
 html:root > body button:hover:not(:empty),
 html:root > body input[type="radio"]:hover,
 html:root > body input[type="reset"]:hover,
 html:root > body input[type="submit"]:hover,
 html:root > body select:hover
  {
    cursor: pointer !important;
  }

/*
 * Active (mouse clicked down, but not released yet)
*/
 html:root > body button:active:not(:empty), 
 html:root > body input[type="button"]:active:not(:empty),
 html:root > body input[type="file"]:active,
 html:root > body input[type="password"]:active,
 html:root > body input[type="reset"]:active,
 html:root > body input[type="submit"]:active,
 html:root > body input[type="text"]:active,
 html:root > body input:not([type]):active,
 html:root > body select:active
  {
    background-color: #000 !important;
    border: 1px inset #bbb !important;
  }
 html:root > body option:active,
 html:root > body select:active
  {
    outline-color: #bbb !important;
  }

/*
 * Checked (selected options in lists)
*/
 html:root > body option:checked
  {
    background-color: #992 !important ;
    outline: 0 !important;
    border: 0 !important;
  }

/*
 * Focus (last active, keyboard target)
*/
 html:root > body button:focus:not(:empty),
 html:root > body input[type="button"]:focus:not(:empty),
 html:root > body input[type="file"]:focus,
 html:root > body input[type="password"]:focus,
 html:root > body input[type="reset"]:focus,
 html:root > body input[type="submit"]:focus,
 html:root > body input[type="text"]:focus,
 html:root > body input:not([type]):focus,
 html:root > body option:focus,
 html:root > body select:focus,
 html:root > body textarea:focus
  {
    background-color: #333 !important ;
    outline-width: 1px !important;
    outline-style: dashed !important;
    outline-color: #333 !important;
  }

/*
 * Disabled (inactive, unusable)
*/
 html:root > body input:disabled 
  {
    border: 0 !important;
  }


/*
 * Grey convex
*/
 html:root > body button:not(:empty),
 html:root > body input[type="button"]:not(:empty),
 html:root > body input[type="reset"],
 html:root > body input[type="submit"],

 html:root > body option:hover
  {
    background-image: url(data:image/png;base64,) !important;
    background-repeat: repeat-x !important ;
    -moz-border-radius: 0 !important ;
  }

/*
 * Grey concave
*/
 html:root > body input[type="password"],
 html:root > body input[type="text"],
 html:root > body input:not([type])
  {
    background-image: url(data:image/png;base64,) !important;
    background-repeat: repeat-x !important ;
    -moz-border-radius: 0 !important ;
  }

}


/*
 * Encrypted forms
*/
@-moz-document url-prefix(https://) 
{

/*
 * Default
*/
 html:root > body button:not(:empty),
 html:root > body input[type="button"]:not(:empty),
 html:root > body input[type="file"],
 html:root > body input[type="password"],
 html:root > body input[type="reset"],
 html:root > body input[type="submit"],
 html:root > body input[type="text"],
 html:root > body input:not([type]),
 html:root > body select,
 html:root > body textarea 
  {
    border: 1px solid #bbb !important;
    color: #fff !important;
    outline-width: 1px !important;
    outline-style: solid !important;
    outline-color: #232922 !important;
  }
 html:root > body button:not(:empty),
 html:root > body input[type="button"]:not(:empty),
 html:root > body input[type="reset"],
 html:root > body input[type="submit"],
 html:root > body option:hover
  {
    background-color: #232922 !important;
  }
 html:root > body input[type="password"],
 html:root > body input[type="text"],
 html:root > body input:not([type])
  {
    background-color: #5d6d5a !important;
  }
 html:root > body select,
 html:root > body textarea
  {
    background-color: #232922 !important;
  }
/*
 * Default, Exception
 *
 * A form in an https page which posts to an http unencrypted target.
*/
 html:root > body form[action^="http:"] button:not(:empty),
 html:root > body form[action^="http:"] input[type="button"]:not(:empty),
 html:root > body form[action^="http:"] input[type="file"],
 html:root > body form[action^="http:"] input[type="password"],
 html:root > body form[action^="http:"] input[type="reset"],
 html:root > body form[action^="http:"] input[type="submit"],
 html:root > body form[action^="http:"] input[type="text"],
 html:root > body form[action^="http:"] input:not([type]),
 html:root > body form[action^="http:"] select,
 html:root > body form[action^="http:"] textarea
  {
    background-color: #622 !important;
    border: 1px solid #a44 !important;
    color: #fff !important;
    outline-width: 1px !important;
    outline-style: solid !important;
    outline-color: #aa1 !important;
  }

/*
 * Hover
*/
 html:root > body button:hover:not(:empty),
 html:root > body input[type="button"]:hover:not(:empty),
 html:root > body input[type="reset"]:hover,
 html:root > body input[type="submit"]:hover
  {
    border: 1px solid #fff !important;
    background-color: #5d6d5a !important;
    color: #000 !important;
  }
 html:root > body input[type="file"]:hover,
 html:root > body input[type="password"]:hover,
 html:root > body input[type="text"]:hover,
 html:root > body input:not([type]):hover,
 html:root > body select:hover,
 html:root > body textarea:hover
  {
    border: 1px solid #fff !important;
    background-color: #5d6d5a !important;
  }
 html:root > body button:hover:not(:empty),
 html:root > body input[type="radio"]:hover,
 html:root > body input[type="reset"]:hover,
 html:root > body input[type="submit"]:hover,
 html:root > body select:hover
  {
    cursor: pointer !important;
  }

/*
 * Active
*/
 html:root > body button:active:not(:empty), 
 html:root > body input[type="button"]:active:not(:empty),
 html:root > body input[type="file"]:active,
 html:root > body input[type="password"]:active,
 html:root > body input[type="reset"]:active,
 html:root > body input[type="submit"]:active,
 html:root > body input[type="text"]:active,
 html:root > body input:not([type]):active,
 html:root > body select:active
  {
    background-color: #000 !important;
    border: 1px inset #bbb !important;
  }

/*
 * Checked (selected options in lists)
*/
 html:root > body option:checked
  {
    background-color: #992 !important ;
    outline: 0 !important;
    border: 0 !important;
  }

/*
 * Focus
*/
 html:root > body button:focus:not(:empty),
 html:root > body input[type="button"]:focus:not(:empty),
 html:root > body input[type="file"]:focus,
 html:root > body input[type="password"]:focus,
 html:root > body input[type="reset"]:focus,
 html:root > body input[type="submit"]:focus,
 html:root > body input[type="text"]:focus,
 html:root > body input:not([type]):focus,
 html:root > body textarea:focus, 
 html:root > body select:focus
  {
    background-color: #333 !important ;
    outline-width: 1px !important;
    outline-style: dashed !important;
    outline-color: #5d6d5a !important;
  }

/*
 * Disabled
*/
 html:root > body input:disabled 
  {
    border: 0 !important;
  }


/*
 * Green convex
*/
 html:root > body form:not([action^="http:"]) button:not(:empty),
 html:root > body form:not([action^="http:"]) input[type="button"]:not(:empty),
 html:root > body form:not([action^="http:"]) input[type="reset"],
 html:root > body form:not([action^="http:"]) input[type="submit"],
 html:root > body form:not([action^="http:"]) option:hover
  {
    background-image: url(data:image/png;base64,) !important;
    background-repeat: repeat-x !important ;
    -moz-border-radius: 0 !important ;
  }

/*
 * Green concave
*/
 html:root > body form:not([action^="http:"]) input[type="password"],
 html:root > body form:not([action^="http:"]) input[type="text"],
 html:root > body form:not([action^="http:"]) input:not([type])
  {
    background-image: url(data:image/png;base64,) !important;
    background-repeat: repeat-x !important ;
    -moz-border-radius: 0 !important ;
  }

}
