#logo a {
	background-image: url(http://tweakers.net/ext/f/QeWn3tvI6chixX3gzquo2Fg6/full.png);
}
#logo a[href="http://gathering.tweakers.net/"] {
	background-image: url(http://tweakers.net/ext/f/hMry9lVPHuKbYiMPJsNhwGxg/full.png);
}
#mainNav, #header {
	background-image: url(http://tweakers.net/ext/f/pqe8LcfxnxSMQbQvbtVwsN8w/full.png);
}
#siteNav, #siteNav fieldset, #siteMenu, #siteMenu li, .notracker #siteMenu li, .trackerright #siteMenu li {
	background-image: url(http://tweakers.net/ext/f/vPaCwIsfZDQPMoradwJjDsTy/full.png);
}
#groupNav fieldset div ul li {
	background-image: url(http://tweakers.net/ext/f/nHPfRD3mvEHOhzWXabXFYYpn/full.png);
}
#groupNav fieldset div ul li.active, #groupNav fieldset div ul li.active a,
#groupNav fieldset div ul li.activesubitems, #groupNav fieldset div ul li.activesubitems a {
	color: #B0FF00;
}
