css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("carnage.bungie.org") {

@charset "utf-8";
/* halo.bungie.org forum styles */

/*
General purpose blue: rgba(62,105,202,1)
Pinkish Purple: rgba(213, 65, 129,1)
Read list link blue: #8ea7e0
*/

/* colour! */
body {
  
}

/* Global width */
map[name=forumtopmap] + table,
div#glob_wrapbbs
{
width:960px !important;
  margin:0 auto !important;
}
table[width="510"] { width:100% !important; }

/* Typography */
map[name=forumtopmap] + table,
div#glob_wrapbbs,
div#glob_wrapbbs div#ind_topinf table,
div#glob_wrapbbs div#ind_msglist table,
div#glob_wrapbbs div#misc_msgform table,
div#glob_wrapbbs div#msg_form table,
div#glob_wrapbbs div#msg_respons div table
{
  font-family:Lucida Grande, Calibri, Tahoma, sans-serif !important;
  font-size:1em !important;
  line-height:1.5em !important;
}

/*map[name=forumtopmap] + table { background-color:rgb(62,105,202); }*/

map[name=forumtopmap] + table a,
div#glob_wrapbbs a
{
  color:rgba(62,105,202,0.9) !important;
  font-weight:bold !important;
}

map[name=forumtopmap] + table a:hover,
div#glob_wrapbbs a:hover
{
  color:rgba(62,105,202,1) !important;
  border-color:rgba(62,105,202,0.8);
}

/* Tiling glyph strips */
map[name=forumtopmap] + table tr:nth-child(2) td[valign="top"][align="left"][colspan="3"] { background-color:white !important; }
map[name=forumtopmap] + table tr:nth-child(2) td[valign="top"][align="left"][colspan="3"] img { margin:0 auto; display:block; }
map[name=forumtopmap] + table tr:nth-child(3) td[valign="top"][align="left"][colspan="3"] img { display:none; }
map[name=forumtopmap] + table tr:nth-child(3) td[valign="top"][align="left"][colspan="3"]
{
  background-image:url(http://carnage.bungie.org/haloforum/images/glyphstripdown.jpg) !important;
  background-position:top;
  background-repeat:repeat-x;
  height:55px;
}
map[name=forumtopmap] + table tr td[valign="top"][align="left"][width="510"][colspan="3"] img { display:none; }
map[name=forumtopmap] + table tr td[valign="top"][align="left"][width="510"][colspan="3"]
{
  background-image:url(http://carnage.bungie.org/haloforum/images/glyphstripup.jpg) !important;
  background-position:bottom;
  background-repeat:repeat-x;
  height:55px;
}
map[name=forumtopmap] + table tr td[valign="top"][align="left"][width="510"][colspan="3"] img[src="/haloforum/images/halobluenew.forum.gif"]
{
  display:block !important;
}

/* Hide a bunch of empty spacer rows */
map[name=forumtopmap] + table tr td { background-color:transparent !important; }
map[name=forumtopmap] + table tr td[colspan="5"] { display:none !important; }
map[name=forumtopmap] + table tr td[valign="top"][align="center"],
map[name=forumtopmap] + table tr td[valign="top"][rowspan="10"],
map[name=forumtopmap] + table tr td[colspan="3"][align="center"],
map[name=forumtopmap] + table tr td[colspan="2"],
map[name=forumtopmap] + table tr td[colspan="3"][align="center"][valign="middle"][bgcolor="#777777"][height="1"],
map[name=forumtopmap] + table tr td[valign="middle"][align="center"][colspan="3"] img,
map[name=forumtopmap] + table tr td[valign="middle"][align="center"][colspan="3"] br { display:none !important; }
map[name=forumtopmap] + table tr td[valign="top"][align="left"][width="510"],
map[name=forumtopmap] + table tr td[valign="top"][align="left"][colspan="3"],
map[name=forumtopmap] + table tr td[valign="middle"][align="center"][colspan="3"] { display:table-cell !important; }
map[name=forumtopmap] + table tr td[valign="middle"][align="center"][colspan="3"] b { display:block; margin-top:-50px; color:transparent; }
map[name=forumtopmap] + table tr td[valign="middle"][align="center"][colspan="3"] b a
{
  color:rgba(255,255,255,0.8) !important;
  border-color:rgba(255,255,255,0.6);
  text-shadow:rgba(0,0,0,0.4) 0px 1px 1px;
}
map[name=forumtopmap] + table tr td[valign="middle"][align="center"][colspan="3"] b a:hover { color:white !important; border-color:rgba(255,255,255,0.8); }

/* FAQ link */
map[name=forumtopmap] + table tr td[align="center"] a span.big { display:none; }

/* Search forms */
map[name=forumtopmap] + table tr td span.mainplustwo { display:none; }
map[name=forumtopmap] + table tr td form { display:none; }
map[name=forumtopmap] + table tr td form[action="http://halo.bungie.org/newssearch.html"] { display:none; }
map[name=forumtopmap] + table tr td form[action="http://halo.bungie.org/updatesearch.html"] { display:none; }

/* Challenges */
table tr td div.chcontainer
{
  position:absolute;
  margin-left:-960px !important;
}
table tr td div.chcontainer > div
{
  clear:both !important;
  float:none !important;
  position:absolute;
  margin-left:32px !important;
  opacity:0;
  -webkit-transition-duration: 0.75s;
  -webkit-transition-property: opacity, width;
  -moz-transition-duration: 0.75s;
  -moz-transition-property: opacity, width;
  width:0 !important;
  overflow:hidden;
  background-color:rgba(255,255,255,0.8);
}

table tr td div.chcontainer a:hover + div {
  -webkit-transition-duration:0.5s;
  -moz-transition-duration:0.5s;
  opacity:1;
  width:400px !important;
}

table tr td div.chcontainer > div ul.ch li.chn
{
  height:29px !important;
  border-bottom:1px solid rgba(0,0,0,0.1);
}
table tr td div.chcontainer > div ul.ch li.chn:last-child { border-bottom:none; height:30px !important; }
table tr td div.chcontainer > div ul.ch li.chn div.challenge
{
  background-color:transparent !important;
  font-size:0.8em;
}
table tr td div.chcontainer > div ul.ch li.chn div.challenge img { display:none; }
table tr td div.chcontainer > div ul.ch li.chn div.challenge img + strong
{
  float:left;
  width:362px;
  height:30px;
  text-transform:uppercase;
  background-repeat:no-repeat;
  background-position:2px top;
  padding-left:34px;
}
table tr td div.chcontainer > div ul.ch li.chn div.challenge img[src="/haloforum/images/challenges/0.png"] + strong
{
  background-image:url(http://carnage.bungie.org/haloforum/images/challenges/0.png);
}
table tr td div.chcontainer > div ul.ch li.chn div.challenge img[src="/haloforum/images/challenges/1.png"] + strong
{
  background-image:url(http://carnage.bungie.org/haloforum/images/challenges/1.png);
}
table tr td div.chcontainer > div ul.ch li.chn div.challenge img[src="/haloforum/images/challenges/2.png"] + strong
{
  background-image:url(http://carnage.bungie.org/haloforum/images/challenges/2.png);
}
table tr td div.chcontainer > div ul.ch li.chn div.challenge img[src="/haloforum/images/challenges/3.png"] + strong
{
  background-image:url(http://carnage.bungie.org/haloforum/images/challenges/3.png);
}
table tr td div.chcontainer > div ul.ch li.chn div.challenge img[src="/haloforum/images/challenges/4.png"] + strong
{
  background-image:url(http://carnage.bungie.org/haloforum/images/challenges/4.png);
}
table tr td div.chcontainer > div ul.ch li.chn div.challenge strong em { float:right; text-transform:none; font-style:normal !important; }
table tr td div.chcontainer > div ul.ch li.chn div.challenge p.ch
{
  margin:-16px 0 0 34px !important;
  float:left !important;
}

/* Side links */
map[name=forumtopmap] + table tr td ul.main { display:none; }

/* main content block */
div#glob_wrapbbs
{
  width:960px;
  margin:-30px auto 0 auto !important;
}

div#glob_wrapbbs div.glob_navbar { width:100% !important; padding:0 !important; margin:5px 0 10px 0 !important; background:transparent !important;}
div#glob_wrapbbs div.glob_navbar a
{
  color:rgba(255,255,255,0.8) !important;
  border-color:rgba(255,255,255,0.6);
  text-shadow:rgba(0,0,0,0.4) 0px 1px 1px;
  text-decoration:none !important;
}
div#glob_wrapbbs div.glob_navbar a:hover { color:white !important; border-color:rgba(255,255,255,0.8); }


div#glob_wrapbbs div.glob_navbar br { display:none; }

br[clear="left"] { display:none; }

div#glob_wrapbbs div#glob_header { display:none; }
div#glob_wrapbbs div#ind_topinf table { width:100% !important; border-collapse:collapse; }
div#glob_wrapbbs div#ind_topinf table th { text-align:left; font-size:0.9em; padding:10px 0 !important; }
div#glob_wrapbbs div#ind_topinf table td
{
  background:rgba(62,105,202,1) !important;
  text-transform:uppercase;
  color:rgba(255,255,255,0.9);
  font-size:0.9em;
  width:50%;
  padding:5px !important;
}
div#glob_wrapbbs div#ind_topinf table td:nth-child(1)
{
  -moz-border-radius-topleft:5px;
  -webkit-border-top-left-radius:5px;
  border-top-left-radius:5px;
}
div#glob_wrapbbs div#ind_topinf table td:nth-child(2)
{
  -moz-border-radius-topright:5px;
  -webkit-border-top-right-radius:5px;
  border-top-right-radius:5px;
}
div#glob_wrapbbs div#ind_topinf table td br { display:none; }
div#glob_wrapbbs div#ind_topinf hr { display:none; }

/* Message list */

div#glob_wrapbbs div#ind_msglist table,
div#glob_wrapbbs div#msg_respons div table
{
  width:100%;
  border-collapse:collapse;
}
div#glob_wrapbbs div#ind_msglist table td,
div#glob_wrapbbs div#msg_respons div table td { padding:4px !important; font-weight:normal !important; font-size:0.9em; }
div#glob_wrapbbs div#ind_msglist table tr:nth-child(odd) td,
div#glob_wrapbbs div#msg_respons div table tr:nth-child(odd) td { background:rgba(62,105,202,0.05) !important; }
div#glob_wrapbbs div#ind_msglist table tr:nth-child(even) td,
div#glob_wrapbbs div#msg_respons div table tr:nth-child(even) td { background:rgba(62,105,202,0.2) !important; }
div#glob_wrapbbs div#ind_msglist table tr:last-child td,
div#glob_wrapbbs div#msg_respons div table tr:last-child td { border-bottom:2px solid rgba(62,105,202,1) !important; }

div#glob_wrapbbs div#ind_msglist table tr td:nth-child(2),
div#glob_wrapbbs div#msg_respons div table tr td:nth-child(2)
{
  max-width:140px;
  white-space:nowrap;
  text-overflow:ellipsis;
  overflow:hidden;
}

div#glob_wrapbbs div#ind_msglist table tr td:nth-child(3),
div#glob_wrapbbs div#msg_respons div table tr td:nth-child(3) { width:130px; }

div#glob_wrapbbs div#ind_msglist table td b,
div#glob_wrapbbs div#msg_respons div table td b { color:transparent; width:1em; margin-right:2px; display:inline-block; }
div#glob_wrapbbs div#ind_msglist table td b:before,
div#glob_wrapbbs div#msg_respons div table td b:before { content:"â˜…"; color:rgba(250,127,31,1); font-size:0.9em; }

/* message list topic links */
div#glob_wrapbbs div#ind_msglist table td a,
div#glob_wrapbbs div#msg_respons div table td a
{
  -webkit-transition-property:none;
}
div#glob_wrapbbs div#ind_msglist table td a:visited,
div#glob_wrapbbs div#msg_respons div table a:visited { color:#8ea7e0 !important; }

div#glob_wrapbbs div#ind_msglist table tr:nth-child(odd) td a:visited,
div#glob_wrapbbs div#msg_respons div table tr:nth-child(odd) td a:visited { border-color:#f6f8fd !important; }
div#glob_wrapbbs div#ind_msglist table tr:nth-child(even) td a:visited,
div#glob_wrapbbs div#msg_respons div table tr:nth-child(even) td a:visited { border-color:#ecf0fa !important; }

/* Pagination controls */
div#glob_wrapbbs div#ind_paginate { width:100% !important; }

/* New thread form */
div#glob_wrapbbs div#misc_msgform,
div#glob_wrapbbs div#msg_form { width:100% !important; }
div#glob_wrapbbs div#misc_msgform hr,
div#glob_wrapbbs div#misc_msgform p,
div#glob_wrapbbs div#msg_form hr,
div#glob_wrapbbs div#msg_form p { display:none; }
div#glob_wrapbbs div#misc_msgform span.big,
div#glob_wrapbbs div#msg_form span.big { font-size:1em !important; }
div#glob_wrapbbs div#misc_msgform form table:nth-child(2),
div#glob_wrapbbs div#msg_form form table:nth-child(3),
div#glob_wrapbbs div#misc_msgform form table:nth-child(2) td div,
div#glob_wrapbbs div#msg_form form table:nth-child(3) td div { width:100% !important; }
div#glob_wrapbbs div#misc_msgform form table:nth-child(2) td div img[alt="glyphstrip"],
div#glob_wrapbbs div#msg_form form table:nth-child(3) td div img[alt="glyphstrip"] { display:none; }
div#glob_wrapbbs div#misc_msgform form table:nth-child(2) td div table,
div#glob_wrapbbs div#msg_form form table:nth-child(3) td div table { width:100% !important; margin:10px 0; }
div#glob_wrapbbs div#misc_msgform form table:nth-child(2) td div table td[align="right"],
div#glob_wrapbbs div#msg_form form table:nth-child(3) td div table td[align="right"]
{
  text-align:left !important;
  width:46px;
}
div#glob_wrapbbs div#misc_msgform form table:nth-child(2) td div > br,
div#glob_wrapbbs div#msg_form form table:nth-child(3) td div > br { display:none; }
div#glob_wrapbbs div#misc_msgform form table td[bgcolor="#e6cfd3"],
div#glob_wrapbbs div#msg_form form table td[bgcolor="#e6cfd3"]
{
 display:none;
}
div#glob_wrapbbs div#misc_msgform form table td[bgcolor="#e6cfd3"] br:first-child,
div#glob_wrapbbs div#msg_form form table td[bgcolor="#e6cfd3"] br:first-child { display:none; }
div#glob_wrapbbs div#misc_msgform form table td[bgcolor="#e6cfd3"] span.big,
div#glob_wrapbbs div#misc_msgform form table td[bgcolor="#e6cfd3"] h3,
div#glob_wrapbbs div#msg_form form table td[bgcolor="#e6cfd3"] span.big,
div#glob_wrapbbs div#msg_form form table td[bgcolor="#e6cfd3"] h3 { font-size:1em !important; margin:0 !important; }
div#glob_wrapbbs div#misc_msgform form table td[bgcolor="#e6cfd3"] span.big b,
div#glob_wrapbbs div#msg_form form table td[bgcolor="#e6cfd3"] span.big b { text-transform:uppercase; }
div#glob_wrapbbs div#misc_msgform form table td[bgcolor="#e6cfd3"] h3,
div#glob_wrapbbs div#msg_form form table td[bgcolor="#e6cfd3"] h3 { text-transform:uppercase; }
div#glob_wrapbbs div#misc_msgform form table td[bgcolor="#e6cfd3"] h3 a,
div#glob_wrapbbs div#msg_form form table td[bgcolor="#e6cfd3"] h3 a { color:rgba(255,255,255,0.8) !important; border-color:rgba(255,255,255,0.6) !important; }
div#glob_wrapbbs div#misc_msgform form table td[bgcolor="#e6cfd3"] h3 a:hover,
div#glob_wrapbbs div#msg_form form table td[bgcolor="#e6cfd3"] h3 a:hover { color:white !important; border-color:white !important; }

/* Message Composer */
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) th,
div#glob_wrapbbs div#msg_form form table:nth-child(5) th { background:transparent !important; text-transform:uppercase; text-align:left; }

div#glob_wrapbbs div#misc_msgform form table:nth-child(4) td input,
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) td textarea,
div#glob_wrapbbs div#msg_form form table:nth-child(5) td input,
div#glob_wrapbbs div#msg_form form table:nth-child(5) td textarea
{
  font-family:Lucida Grande, Calibri, Tahoma, sans-serif !important;
font-size:0.9em !important;
line-height:1.5em !important;
  padding:2px;
  border:1px solid rgba(0,0,0,0.3) !important;
  background-color:rgba(255,255,255,0.8);
  -moz-border-radius:2px;
  -webkit-border-radius:2px;
  border-radius:2px;
}
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) td textarea,
div#glob_wrapbbs div#msg_form form table:nth-child(5) td textarea { padding:4px; }
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) td input:hover,
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) td textarea:hover,
div#glob_wrapbbs div#msg_form form table:nth-child(5) td input:hover,
div#glob_wrapbbs div#msg_form form table:nth-child(5) td textarea:hover { border-color:rgba(62,105,202,0.7) !important; }
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) td input:focus,
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) td textarea:focus,
div#glob_wrapbbs div#msg_form form table:nth-child(5) td input:focus,
div#glob_wrapbbs div#msg_form form table:nth-child(5) td textarea:focus { border-color:rgb(62,105,202) !important; background-color:white; outline:none; }

div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(2) td[align="right"],
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(3) td[align="right"],
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(4) td[align="right"],
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(7) td[align="right"],
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(8) td[align="right"],
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(10) td[align="right"],
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(11) td[align="right"],
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(2) td[align="right"],
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(3) td[align="right"],
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(4) td[align="right"],
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(7) td[align="right"],
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(8) td[align="right"],
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(10) td[align="right"],
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(11) td[align="right"] { padding-right:10px !important; }

div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(2) td input,
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(3) td input,
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(4) td input,
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(7) td input,
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(8) td input,
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(10) td input,
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(2) td input,
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(3) td input,
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(4) td input,
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(7) td input,
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(8) td input,
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(10) td input { width:400px; }

div#glob_wrapbbs div#misc_msgform form table:nth-child(4) tr:nth-child(11) td:nth-child(2),
div#glob_wrapbbs div#msg_form form table:nth-child(5) tr:nth-child(11) td:nth-child(2) { height:27px; }

div#glob_wrapbbs div#misc_msgform form table:nth-child(4) td.misc_msgform_b,
div#glob_wrapbbs div#msg_form form table:nth-child(5) td.misc_msgform_b { background:transparent !important; padding-bottom:20px !important; }
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) td.misc_msgform_b input,
div#glob_wrapbbs div#msg_form form table:nth-child(5) td.misc_msgform_b input
{
  width:150px;
  height:2em;
  margin:0px 5px !important;
  text-transform:uppercase;
  font-weight:bold;
  font-size:1.1em !important;
  background-color:rgba(62,105,202,0.7) !important;
  border:none !important;
  -moz-border-radius:3px;
  -webkit-border-radius:3px;
  border-radius:3px;
  cursor:pointer;
}
div#glob_wrapbbs div#misc_msgform form table:nth-child(4) td.misc_msgform_b input:hover,
div#glob_wrapbbs div#msg_form form table:nth-child(5) td.misc_msgform_b input:hover { background-color:rgba(62,105,202,1) !important; }

/* Message View */
div#glob_wrapbbs div#msg_wrap
{
  width:100% !important;
  padding-top:10px !important;
}
div#glob_wrapbbs div#msg_wrap div.msg_headln
{
  width:auto !important;
  background:rgba(62,105,202,1) !important;
  color:rgba(255,255,255,1);
  padding:5px !important;
  -webkit-border-top-left-radius:5px;
  -webkit-border-top-right-radius:5px;
  -moz-border-radius-topleft:5px;
  -moz-border-radius-topright:5px;
  border-top-left-radius:5px;
  border-top-right-radius:5px;
}
div#glob_wrapbbs div#msg_wrap div.msg_userln
{
  width:auto !important;
  background:rgba(62,105,202,0.2) !important;
  color:black;
  padding:5px !important;
  -webkit-border-bottom-left-radius:5px;
  -webkit-border-bottom-right-radius:5px;
  -moz-border-radius-bottomleft:5px;
  -moz-border-radius-bottomright:5px;
  border-bottom-left-radius:5px;
  border-bottom-right-radius:5px;
}
