css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("webchat.freenode.net") {

a:link{color:deepSkyBlue !important;}
a:visited{color:darkRed !important;}
a:hover{color:red !important;}
a:active{color:green !important;}

.hyperlink-whois{color:white !important;}
/*
.qwebirc .linestyle1 {background-color:#202020 !important; color: white !important;}
.qwebirc .colourline {background-color:#202020 !important; color: green ;}
*/

.qwebirc-qui .bottomboundpanel{color:red !important;}
.qwebirc-qui .lines {
  color: lightGray !important;
  background: black !important;
}
.qwebirc-qui .topic {
  background-color: black !important;
  border-bottom: 1px dashed darkGray !important;
}
.qwebirc-qui .topic .emptytopic{color: gray !important;}
.qwebirc-qui .topic{color: lightGray !important;}

.qwebirc-qui .outertabbar {
  border-bottom: 1px solid gray !important;
  background: dimGray !important;
}
.qwebirc-qui .tabbar {color: red !important;}
.qwebirc-qui a.tab {
  border: 1px solid black !important;
  color: black !important;
  /*background: gray !important;*/
}
.qwebirc-qui .tab:hover {
  background: lightGray !important;
  border: 1px solid black !important;
}
.qwebirc-qui .tab-selected {
  background: darkGray !important;
  border: 1px solid black !important;
  color: black !important;
}
.qwebirc-qui div.input {
  border-top: 1px solid dimGray !important;
  background: #202020 !important;
}
.qwebirc-qui a.tab-hilight-activity {
  color: lightGreen !important;
}
.qwebirc-qui a.tab-hilight-speech {
  color: deepSkyBlue !important;
}
.qwebirc-qui a.tab-hilight-us {
  color: red !important;
}

.qwebirc-qui .nicklist {
  border-left: 1px solid gray !important;
  background: black !important;
}
.qwebirc-qui .nicklist a {
  border-top: 1px solid dimGray !important;
  border-bottom: 0px solid dimGray !important;
  color: lightGray !important;
}
.qwebirc-qui .nicklist a.selected {
  color: white !important;
  background: gray !important;
  border-bottom: red 1px solid !important;
}
.qwebirc-qui .nicklist a.selected-middle {
  border-top: red 1px solid !important;
}

.qwebirc-qui .dropdownmenu {
  border: 1px solid lightGray !important;
  background: gray !important;
}
.qwebirc-qui .dropdownmenu a {
  color: lightGray !important;
}
.qwebirc-qui .dropdownmenu a:hover {
  background: darkGray !important;
  color: white !important;
}

.qwebirc-qui hr.lastpos{border-top: 1px solid darkRed !important;}

.qwebirc .colourline .Xc0{color:white !important;}
.qwebirc .colourline .Xc1{color:black !important;}
.qwebirc .colourline .Xc2{color:darkBlue !important;}
.qwebirc .colourline .Xc3{color:darkGreen !important;}
.qwebirc .colourline .Xc4{color:red !important;}
.qwebirc .colourline .Xc5{color:darkRed !important;}
.qwebirc .colourline .Xc6{color:purple !important;}
.qwebirc .colourline .Xc7{color:orange !important;}
.qwebirc .colourline .Xc8{color:yellow !important;}
.qwebirc .colourline .Xc9{color:green !important;}
.qwebirc .colourline .Xc10{color:teal !important;}
.qwebirc .colourline .Xc11{color:cyan !important;}
.qwebirc .colourline .Xc12{color:blue !important;}
.qwebirc .colourline .Xc13{color:fuchsia !important;}
.qwebirc .colourline .Xc14{color:darkGray !important;}
.qwebirc .colourline .Xc15{color:gray !important;}
.qwebirc .colourline .Xbc0{background-color:white !important;}
.qwebirc .colourline .Xbc1{background-color:black !important;}
.qwebirc .colourline .Xbc2{background-color:darkBlue !important;}
.qwebirc .colourline .Xbc3{background-color:darkGreen !important;}
.qwebirc .colourline .Xbc4{background-color:red !important;}
.qwebirc .colourline .Xbc5{background-color:darkRed !important;}
.qwebirc .colourline .Xbc6{background-color:purple !important;}
.qwebirc .colourline .Xbc7{background-color:orange !important;}
.qwebirc .colourline .Xbc8{background-color:yellow !important;}
.qwebirc .colourline .Xbc9{background-color:green !important;}
.qwebirc .colourline .Xbc10{background-color:teal !important;}
.qwebirc .colourline .Xbc11{background-color:cyan !important;}
.qwebirc .colourline .Xbc12{background-color:blue !important;}
.qwebirc .colourline .Xbc13{background-color:fuchsia !important;}
.qwebirc .colourline .Xbc14{background-color:darkGray !important;}
.qwebirc .colourline .Xbc15{background-color:gray !important;}
.qwebirc .colourline .Xb{font-weight:bold;}
.qwebirc .colourline .Xu{text-decoration:underline;}
.qwebirc .warncolour{background-color:#ff9090;}
.qwebirc .infocolour{background-color:#9090ff;}

.qwebirc-aboutpane .header {
  background:black !important;
  border-bottom:1px solid dimGray !important;
}
.qwebirc-faqpane .header {
  background:black !important;
  border-bottom:1px solid dimGray !important;
}
.qwebirc-feedbackpane .header {
  background:black !important;
  border-bottom:1px solid dimGray !important;
}
.qwebirc-optionspane div.hue-slider{
border:1px solid gray !important;
}
.qwebirc-optionspane div.hue-slider .knob {
  background:lightGray !important;
  border:1px solid black !important;
}
#recaptcha_image{border:1px solid #c8d2dc;}
.qwebirc-recaptcha-incorrect{color:red;}


}
