css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linux.org.ru") {
	* {
		background: none !important;
	}

	body {
		color: yellow !important;
		background-color: #7CFC00 !important;
	}

	a {
		color: yellow !important;
	}
	.messages .msg {
	background:none repeat scroll 0 0 #00FF7F !important;
	}
	option {
		color: yellow !important;
		background-color: #00ff00 !important;
	}

	/* wiki */

	div#nav-menu, div#nav-search {
		background-color: #7CFC00 !important;
	}
	div#nav-search input {
		color: white !important;
		background-color: #7CFC00 !important;
	}
	div.tab-item {
		background-color: #7CFC00 !important;
	}
	div#contents {
		background-color: #32cd32 !important;
	}

	/* forum */

	table.nav td, div.nav {
		background-color: #00ff00 !important;
	}
	table.message-table {
		background-color: #00ff00 !important;
	}
	table.message-table thead th, table.message-table tfoot td {
		background-color: #00ff00 !important;
	}
	table.message-table tbody td {
		background-color: #7CFC00!important;
	}
	table.message-table tbody tr:hover td {
		background-color: #00ff00 !important;
		}
	table.message-table tbody td a:visited {
		color: #00ffff !important;
	}
	table.message-table tbody td a:hover {
		color: #00ffff !important;
	}

	/* thread */

	div.msg {
		background-color: #7CFC00 !important;
	}
	div.msg:target {
		border: 2px solid #00ffff !important;
	}

	/* post */

	div#recaptcha_area {
		background-color: #ffffff !important;
	}

	/* main page */

	div.boxlet {
		background-color: #00FF7f !important;
	}
	div.boxlet_content {
background-color: 7cfc00 !important;
}
	div.news div {
		background-color: #7CFC00 !important;
	}
	table tbody td {
	border-color:#00ff00 !important;
	}
	a:visited {
	color:#00ffFF !important
	}
	table tbody td * {
	color: yellow !important;
	}
	.forum table th {
	border-color:#00ff00 !important;
}
}
