css:
@-moz-document domain('tumblr.com'), url-prefix('http://www.tumblr.com/') , url-prefix("https://www.tumblr.com/") {







#dashboard_index{background:#2A2D30 repeat-x 50% 0% !important;}







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#000000!important;}







ol#posts li.notification{background-color: #000000 !important;



border-bottom: 1px solid #2F465C !important;



color: #A8BCCB !important;}







#left_column .dashboard_options_form {



   background: url('http://assets.tumblr.com/images/white_20.png')#000000;



}







#right_column a#dashboard_switch_blog_menu_current:hover {



   background: transparent;



}







#nav .nav_item.active{



   background-color:#353738



}







#left_column #posts .post .permalink{



   background-color:#2A2D30;



}







#nav .nav_item.active, #nav .nav_item .nav_menu, #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel{



   background-color:#353738;



}







}
