/*

 * Google / Google Images - Dark Gray Wood Design(Update!)

 *

 * Author      : Sarge101

 * Updated     : September 13th, 2010

 * Description : Modifies the colors, logos, and a few other images on Google / Google Images

 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file

 */



@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document

  url(http://www.google.com/),

  url(http://www.google.com/intl/en/),

  url-prefix(http://images.google.com/),



  url-prefix(http://www.google.com/),

  url-prefix(http://images.google.com/webhp),



  url(http://www.google.com.au/),

  url-prefix(http://www.google.com.au/),



  url(http://www.google.co.uk/),

  url-prefix(http://www.google.co.uk),



  url(http://www.google.ca/),

  url-prefix(http://www.google.ca/),



  url-prefix(http://video.google.com.au/),

  url-prefix(http://video.google.com/),

  url-prefix(http://video.google.co.uk/),

  url-prefix(http://video.google.ca/)

{



body {

  color: #fff !important;

  background: #0D0D0D url(http://imgur.com/MGQ7L.jpg) !important;

  background-repeat: repeat-y !important;

  background-attachment: fixed !important;

  overflow-x: hidden !important;

  background-position: center !important;

}



/* Transparency Fixes

----------------------------------------------*/

#fctr, #pmocntr, #sbl, #tba, #tbe, .fade, #gog, #tbd, #atd, #hidden_modes, #hmp, #sc-block .sc, .lhshdr, .rshdr, .tpblk, .tphdr, #tbbcc, #rgsh_s, .lsb:active, tr#p_pl td{

  background: none !important;

}



.gbh, .gbd, #mbEnd{

  display:none;

}



/* link fixes

----------------------------------------------*/

a, .tbpc, .tbpo, #tbd a, #atd a, a:link, a:visited, a:hover, a:active, .link, a font[color="#008000"], .spell, a.l span, .fl, .fl a, .flt, .gl a:link, a.mblink, .mblink b, #po-on-message, #guser, #not-tip-span, #or-tip-span, #phrase-tip-span {

  color: #ccc !important;

}



/* Top Nav Bar Fixes

----------------------------------------------*/

#guser{

  background: #333 !important;

  border-bottom: 1px solid #444 !important;

  padding-top: 5px !important;

  padding-right: 5px !important;

  margin-top: -3px !important;

  margin-right: 0px !important;

}

#gbar{

  margin-top: 3px !important;

  padding-right: 5px !important;

  padding: 2px !important;

} 

#gog{

  padding: 0px !important;

}

#gbi, #gbg, .gac_m, #po-box, #po-box a, #ss-box {

  background: #282828 !important;

  border-color: #444 !important;

  -moz-box-shadow: 4px 4px 10px rgba(0,0,0, 0.5) !important;

}

a.gb2:hover, .gac_b .gac_c, .gac_b .gac_d, #po-box a:hover, #ss-box a:hover {

  background: #444 !important;

}



/* Search field / recommendations

----------------------------------------------*/

.gac_m, .gac_id{

  background: #333 !important;

  color: #fff !important;

}

.gac_id, .gac_od, tr#p_pl td{

  border: #222 !important;

}

#foot{

  margin:auto !important;

}



/* Search results fixes

----------------------------------------------*/

/* big link */

#res .g .r a {

  color: #f0f0f0 !important;

}



/* visited sub page links  */

/* big link visited */

#res .g a:visited, #res .s .slk a:visited  {

  opacity: 0.5 !important;

}



/* link description */

#res .g .s {

  color: #c9c9c9 !important;

}



/* URL text */

.a, cite, .cite, .cite:link, .gl a {

  color: #6e6e6e !important;

}



/* dashes between Cached and Similar links */

/* cached and similar links */

#res .g .s .gl a, .gl ,.gl a:link {

  color: #999 !important;

}



/* visited cached and similar links */

#res .g .s .gl a:visited {

  opacity: 0.6 !important;

}



/* sub page links (on first link result) */

#res .s .slk a {

  color: #ddd !important;

  opacity: 0.8 !important;

}



/* larger link title */

#res .l { font-size: 1.1em !important; }



/* more line spacing on descriptions */

#res .s { line-height: 1.4 !important; }



/* image thumbnails */

#res .g img {

  border: none !important;

  -moz-box-shadow: 4px 4px 10px rgba(0,0,0,0.4) !important;

}



/* File type (ex: [PDF]) next to link */

.g .w { color: #ff6 !important; }



/* "video results" time info */

/* "File Format" */

.f,#res .g .ts font[color="#666666"] { color: #ffc !important; }



/* "more options" */

.tbo #tbd {

  background: transparent !important;

  border-color: #666 !important;

}



/* source code listing */

#res tt { color: #fff !important; }



/* hr breaks */

.s hr{

  color: #444 !important;

}



/* Side Navbar fix

----------------------------------------------*/

#showmodes{

  border-bottom: 1px solid #777 !important;

  margin-right: 5px !important;

}

#leftnav, #leftnav h2{

  background: #333 !important;

  border-color: #444 !important;

}

#center_col{

  border-left: 1px solid #444 !important;

  margin-right: 0px !important;

} 

#sc-block .sc a {

  border-color: #444 !important;

} 

#rhs, #tads{

  display: none !important;

}

.msel{

  background-color: #444 !important;

  border-color: #444 !important;

}

#tbd, #swr, #leftnav h2 {

  margin-left: 0px !important;

}

#leftnav h2 {

  padding-left: 0px !important;

}



/* Google Advanced Search Fixes

----------------------------------*/

.block, .page-title, td[bgcolor*="#d5ddf3"]{

  background-color: #333 !important;

  border-color: #333 !important;

}

.qbuilder-env{

  background-color: #333 !important;

  border: none !important;

}

.outer-box{

  border: 2px solid #222 !important;

}



table[style*="border-top: 1px solid rgb(51, 102, 204);"]{

  border-top: 1px solid #444 !important;

}

label, h3, td.page-title, b.gb1{

  color: #fff !important;

}



/* Google images Fixes

----------------------------------*/

.rg_hv, #il, #il_m, #il_fi, .close_lk {

  background-color: #333 !important;

}



/* Google video Fixes

----------------------------------*/

.mod_title{

  background-color: #333 !important;

  border-color: #444 !important;

}



/* Widescreen Fix

----------------------------------------------*/

#cnt, .s {

  max-width: 100% !important;

  width:100% !important;

}



/* Image Sheet Fix

----------------------------------------------*/

.csb, .ss, #logo span, .play_icon, .mini_play_icon, .micon, .licon, .close_btn, #tbp, .lsb, .mbi, .close_btn, a#logo img[src*="/images/srpr/nav_logo14.png"]{

 background-image: url(http://imgur.com/YMBRi.png) !important;

}



div#logo, img#logo, img[src*="intl/en_ALL/images/logos/video_logo_lg.gif"]{

  background-image: url(http://imgur.com/obp2r.png) !important;

}



a#logo {

 font-size: 0px !important; 

 text-decoration: none !important

}



a#logo img[src*="/images/srpr/nav_logo14.png"]{

  width: 0 !important;

  height: 106px !important;

  padding-left: 164px !important;

}



img#logo, img[src*="intl/en_ALL/images/logos/video_logo_lg.gif"]{

  width: 0 !important;

  height: 100px !important;

  padding-left: 276px !important;

}



.tpblk{

  background-image:url(http://imgur.com/MGQ7L.jpg) !important;

  background-repeat: repeat-y !important;

  background-attachment: fixed !important;

  overflow-x: hidden !important;

  background-position: center !important;

}}

/*----------------------------------------------------------*/

/* Google Instant & Logo Fixes for US Google

-----------------------------------------------------------*/

@-moz-document

  url(http://www.google.com/),

  url(http://www.google.com/webhp?hl=en),

  url-prefix(http://www.google.com/#),

  url-prefix(http://www.google.com/#sclient),

  url-prefix(http://www.google.com/search?),

  url-prefix(http://www.google.com/webhp?)

{



#cnt{

 max-width:1181px !important;

}



a#logo img[src*="/images/nav_logo16.png"]{

  width: 0px !important;

  padding-left: 198px !important;

  background-image: url(http://imgur.com/yMEWK.png) !important;

  background-position: 0px 40px !important;

}



a img[src*="/images/logo_sm.gif"]{

  width: 0px !important;

  padding-left: 178px !important;

  background-image: url(http://imgur.com/yMEWK.png) !important;

  background-position: 0px -6px !important;

}



/* Image Sheet Fix

----------------------------------------------*/

.csb, .ss, #logo span, .play_icon, .mini_play_icon, .micon, .licon, .close_btn, #tbp, .lsb, .mbi, .close_btn{

  background-image: url(http://imgur.com/0ReU5.png) !important;

}



a#logo img[src*="/images/nav_logo16.png"]{

  background-position: -2px 16px !important;

}}
