css:
@-moz-document
  url(http://www.google.com.eg/),
  url-prefix(http://www.google.com.eg/webhp),
  url-prefix(http://www.google.com.eg/search?),
  url-prefix(http://www.google.com.eg/#hl=),
  url-prefix(http://www.google.com.eg/#q=),

  url-prefix(http://www.google.com.eg/imghp),
  url-prefix(http://www.google.com.sa/search?),
  url-prefix(http://www.google.com.sa/#hl=),
  url-prefix(http://www.google.com.sa/#q=)
{
#cnt,#gog, #guser, body {  background: #F2EAD0 url("http://img638.imageshack.us/img638/25/abstractbackground04.gif") repeat !important; }

#cpf > A {display: none !important;}

[src="/intl/en_uk/images/logo.gif"],
div#logo,img#logo
{
  width: 0px !important;
  height: 110px !important;
  margin-top: 4% !important;
  padding-left:276px;
  background-image: url("http://i667.photobucket.com/albums/vv37/ahmedcsed/smartpng.png") !important;
}

#leftnavc{
   display:none!important;
}

#center_col {
  margin-left: 0px !important;
  border-left: none !important;
}

center div[nowrap=""] { display:none!important; }

.g font[color="#666666"] { display:none!important; }

center a[href="/intl/ar/ads/"] { display:none!important; }

center a[href="/intl/ar/about.html"] { display:none!important; }

div.nj,
#ghead,
#sbl,
center#fctr,
.fade,
#ghead:hover,
#sbl:hover,
center#fctr:hover,
span.fade:hover
{opacity: 1 !important;background: #F2EAD0 url("http://img638.imageshack.us/img638/25/abstractbackground04.gif") repeat  !important;}

html body span div#cpf
{display:none!important;}

#cnt { max-width: 95% !important; padding: 0 !important; }
#rso { width: 100% !important; }
#res { width: 100% !important; padding: 0 !important; }
#res.med > div { -moz-column-count: 2 !important; margin: 0 !important; }
#rso { -moz-column-count: 2 !important; margin: 0 !important; }
#brs, div.s[style="margin-top: 1em;"] { display: none !important; }

#rhsline { display: none !important; }

#res .g .s cite {
  color: #500000 !important;
  }

input, input[name="q"], input[type="submit"], select, button:not([class="ws"]), a[href="javascript:;"], .goog-button-base, .dropdown:not(img) {
   -moz-appearance: none !important;
   background: #F2EAD0 !important;
   border: 1px solid #FFAA00 !important;
   -moz-border-radius: 6px 0 !important;}

.gl { display: none !important; }

#sdb, #ssb, #prs, #eprs, #std {background: #F2EAD0  url("http://img638.imageshack.us/img638/25/abstractbackground04.gif") repeat  !important;}

#nav {

font-size: 140% !important; margin-top: 5px !important; margin-bottom: -20px !important; }

#nav .cur { font-size: 120% !important; }

#nav td { vertical-align: middle !important; padding: 0.30em !important; }

#nav .csb { display: none !important; }



div[class="n"] span b, div[class="n"] span.b {

color: #909090 !important; margin: 5px !important; }

div[class="n"] tr > td a, div[class="n"] tr > td span.i {

font-size: 120% !important; margin-right: 8px !important; }



#foot {

padding-left: 70px !important;

padding-right: 70px !important;

margin: 10px 100px 10px 100px !important;

min-width: 700px !important;}

#foot a.pn {margin-left: -25px !important; margin-right: -20px !important;}

img[src*="/images/logos/ssl_logo_beta_49.gif"], img[src$="blogs_logo.gif"], img[src$="translate_logo.gif"], img[src$="groups_logo.gif"], img[src*="scholar_logo_md"], img[src*="books_logo"],[class*="SP_logo"], img[src*="blog_search_logo"], A[title="Go to Google Home"], #logo > A > IMG, #logo > img, #logo span, img[src*="logo_videos.png"], img[src$="logo_sm.gif"],  img[src$="logo_google_suggest_sm.gif"] , img[src$="maps_logo_small_blue.png"], img[src$="/images/isr_g.png"], img[src$="video_logo.png"], #srp-logo img {

	width: 0px !important;
	height: 51px !important;
	padding-left: 0px !important;
	padding-right: 128px !important;
        margin-top: -1px !important;
        top: 0px !important;
	background: url("http://i667.photobucket.com/albums/vv37/ahmedcsed/smart-sm.png") no-repeat !important;}
#logo {overflow: visible !important;}
a#logo, a#srp-logo, #lgpd + div > div[style^="color: rgb(102, 102, 102); font-size: 16px"] { font-size: 0 !important; }
#sff {padding-left: 50px !important;}

#sft {margin-top: 0px !important;}

a#glogo { font-size: 0 !important; }

#navbar *, #pagination *, .gooooogle *, #ps-navbar *, #lhid_pager * {

background-image: none !important; }



[src*="nav_first.gif"], [src*="nav_current.gif"], [src*="nav_page.gif"],[src*="nav_next.gif"], [src*="nav_previous.gif"] {visibility: hidden !important; }



tt[style*="color: rgb(0, 0, 0)"] { color: #FFF !important; }

.g {
  border: solid 2px #FFAA00 !important;
	-moz-border-radius: 15px 0 !important;
	padding: 10px !important;
	height: 110px;
	background: #F2EAD0  url("http://img638.imageshack.us/img638/25/abstractbackground04.gif") repeat  !important;
	
  overflow: auto !important;
	margin: 1em 0 !important;
}
.g:hover {
	border: dashed 2px #FFAA00 !important;
	-moz-border-radius: 15px !important;
	background: #EDF0F1 !important;
}
 /* applies font to all generic HTML tags */
    *   {
        font-family: Tahoma, Sans !important;
        font-size: 10pt !important;
        text-decoration: none !important;
    }

    /* fixes picasa gallery view */
    .goog-icon-list-icon-img, .goog-icon-list-icon, .goog-icon-list-icon-link {
        width: 100px !important;
        height: auto !important;
        margin-right: 10px !important;
    }
    .goog-icon-list-icon-meta {
        width: 100px !important;
        height: 15px !important;
        margin-right: 10px !important;
    }

    /* fixes width of collapsible pane in Google Maps */
    div#ispane div.spane_body div.wpc {
        width: 200px !important;
    }
    #panelarrow2 {
        height: 18px !important;
        overflow: hidden !important;
    }
    .bar .leaf_links {
        margin-top: -5px !important;
    }

    /* fixes width of sender and date columns in GMail */
    col.xX {
        width: 83px !important;
    }
    col.yY {
        width: 171px !important;
    }

    /* minor UI tweaks */

    /* specifies width of web search results column */
    div.s {
        max-width: 100% !important;
    }  
}
