css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.facebook.com") {

#blueBar {
 background-color: #777777 !important;
 background-image: url(http://lh4.ggpht.com/_2ITFERA-18E/TMLl7UagPBI/AAAAAAAAAB8/CUKC3uTxB88/s800/headerbar.jpg) !important;
}

#headNav {
 background-color:#444444 !important;
 background-image: url(http://lh4.ggpht.com/_2ITFERA-18E/TMLl7UagPBI/AAAAAAAAAB8/CUKC3uTxB88/s800/headerbar.jpg) !important;
 border-color: #111111 !important;
}

#pageLogo a {
 background: url(http://lh6.ggpht.com/_2ITFERA-18E/TMLl7cn2fLI/AAAAAAAAAB4/4EdFnYN178w/s800/fbookicons.png) no-repeat scroll -21px 0 !important;
}

#pageLogo a:hover, #pageLogo a:focus, #pageLogo a:active {
background-position:-21px -31px !important;
}

.jewelToggler {
 background:url("http://lh6.ggpht.com/_2ITFERA-18E/TMLl7cn2fLI/AAAAAAAAAB4/4EdFnYN178w/s800/fbookicons.png") no-repeat scroll 0 0 transparent !important;
}

#jewelRequest {
 background-position: -24px -105px !important;
}

#jewelMail {
 background-position: -24px -73px !important;
}

#jewelNotif {
 background-position: -24px -137px !important;
}

#jewelRequest:hover, #jewelRequest:focus, #jewelRequest:active {
 background-position: -51px -105px !important;
}

.openToggler #jewelRequest {
 background-position: -105px -106px !important;
}

#jewelMail:hover, #jewelMail:focus, #jewelMail:active {
 background-position: -51px -73px !important;
}

.openToggler #jewelMail {
 background-position:-105px -74px !important;
}

#jewelNotif:hover, #jewelNotif:focus, #jewelNotif:active {
 background-position: -51px -137px !important;
}

.openToggler #jewelNotif {
background-position:-105px -138px !important;
}

#jewelCase .jewelBox {
 background-color:#f1f1f1 !important;
}

#jewelCase .jewel {
 border-color:#333333 #444444 -moz-use-text-color !important;
}

#content a {
 color: #44445f !important;
}

#pageNav a:hover, #pageNav a:focus, #pageNav a:active {
 background-color: #9f9f9f !important;
}

.openToggler #navAccountLink, .no_js #navAccount:hover #navAccountLink {
 color: #efefef !important;
}

#navAccount ul a, #navAccount ul .logoutButton input {
 color: #5f5f5f !important;
}

body {
 background-color: #ececec !important;
}

.uiTypeahead {
 background-color: #ececec !important;
}

#footerContainer {
 background-color:#ECECEC !important;
}

.uiSideNav .item, .uiSideNav .subitem {
 border-bottom:1px solid #bdbdbd !important;
}

.fbNubFlyoutTitlebar {
 background-image: url(http://lh4.ggpht.com/_2ITFERA-18E/TMLl7UagPBI/AAAAAAAAAB8/CUKC3uTxB88/s800/headerbar.jpg) !important;
}

}
