@-moz-document domain('4chan.org') {
	html,body{
    background:#0E0E0E url(data:image/gif;base64,R0lGODlhGAAMAJEAABkZGRoaGg4ODg8PDyH5BAAAAAAALAAAAAAYAAwAAAJG3DBipxtrXjozJiUQlCqIbzWU0ZFQ84iSAw0e2q4GAgrvHC+luul25RK5erye0QLRsETKViK10MB0zVWJqhuSAKBnDIMsAAA7) repeat 0 0;
}

div > img
{
    border:1px solid #404040!important;
    border-radius:2;
}

td.reply,th[bgcolor="#e04000"]
{
    background:#202020 url(data:image/gif;base64,R0lGODlhGAAMAKEEABwcHB0dHSYmJicnJywAAAAAGAAMAAACRkwUYKc7a146MyYFEJRqgG81lNGRUPOIkgMFHtquBgIC7xwvpbrpduUSuXq8ntEC0bBEylYitdDAdM1ViaobkgSgZwyDLAAAOw==) repeat 2px 0;
    border:1px solid #404040;
        border-radius:2px;
}

td.replyhl
{
    background:#202020 url(data:image/gif;base64,R0lGODlhGAAMAKEEADQ0NDU1NT09PT4+PiwAAAAAGAAMAAACRkwUYKc7a146MyYFEJRqgG81lNGRUPOIkgMFHtquBgIC7xwvpbrpduUSuXq8ntEC0bBEylYitdDAdM1ViaobkgSgZwyDLAAAOw==) repeat 0 0;
    border:1px solid #909090;
    border-radius:2px;
    color:#FFF;
}

td.postblock
{
    background-color:#202020;
    background-image:url(data:image/gif;base64,R0lGODlhGAAMAKEEABwcHB0dHSYmJicnJywAAAAAGAAMAAACRkwUYKc7a146MyYFEJRqgG81lNGRUPOIkgMFHtquBgIC7xwvpbrpduUSuXq8ntEC0bBEylYitdDAdM1ViaobkgSgZwyDLAAAOw==);
    border:1px solid #404040;
}

body,a,blockquote,span.filesize,td.reply,td.postblock,div.logo
{
    color:#FFF!important;
}

span.navtop,span.abbr
{
    color:#FFF;
}

span.omittedposts
{
    color:#CCC;
}

hr,img#recaptcha_tagline,img#recaptcha_logo
{
    display:none!important;
}

input.inputtext,textarea.inputtext,td.reply,td.postblock
{
    border-radius:2px;
}

th[bgcolor=#e04000],input[type=text]
{
    background-image:url(data:image/gif;base64,R0lGODlhGAAMAKEEABwcHB0dHSYmJicnJywAAAAAGAAMAAACRkwUYKc7a146MyYFEJRqgG81lNGRUPOIkgMFHtquBgIC7xwvpbrpduUSuXq8ntEC0bBEylYitdDAdM1ViaobkgSgZwyDLAAAOw==);
    border:1px solid #404040!important;
    border-radius:2px!important;
    color:#FFF!important;
}

th[bgcolor=#e04000] > font
{
    color:#FFF;
}

td,tr
{
    color:#FFF;
}

.inputtext,input#recaptcha_response_field,input[style="border: 1px solid #3c3c3c; width: 302px;"],table.pages
{
    background-color:#202020;
    background-image:url(data:image/gif;base64,R0lGODlhGAAMAKEEABwcHB0dHSYmJicnJywAAAAAGAAMAAACRkwUYKc7a146MyYFEJRqgG81lNGRUPOIkgMFHtquBgIC7xwvpbrpduUSuXq8ntEC0bBEylYitdDAdM1ViaobkgSgZwyDLAAAOw==);
    border:1px solid #404040!important;
    border-radius:2px;
    color:#FFF!important;
}

th[bgcolor=#e04000]
{
    background-color:#202020;
    background-image:url(data:image/gif;base64,R0lGODlhGAAMAKEEABwcHB0dHSYmJicnJywAAAAAGAAMAAACRkwUYKc7a146MyYFEJRqgG81lNGRUPOIkgMFHtquBgIC7xwvpbrpduUSuXq8ntEC0bBEylYitdDAdM1ViaobkgSgZwyDLAAAOw==);
    border:1px solid #404040;
    border-radius:2px;
    color:#FFF;
}

th > font
{
    color:#FFF;
}

img[md5]
{
    -webkit-transition:.6s all;
    border:none!important;
    height:auto!important;
    width:auto!important;
}

a
{
    -webkit-transition-duration:.5s;
    -webkit-transition-property:text-shadow;
    color:#FFF !important;
    text-decoration:none !important;
}

a:hover
{
    text-shadow:0 0 3px #CCE;
}

input
{
    background-color:#202020;
    background-image:url(data:image/gif;base64,R0lGODlhGAAMAKEEABwcHB0dHSYmJicnJywAAAAAGAAMAAACRkwUYKc7a146MyYFEJRqgG81lNGRUPOIkgMFHtquBgIC7xwvpbrpduUSuXq8ntEC0bBEylYitdDAdM1ViaobkgSgZwyDLAAAOw==);
    border:1px solid #404040;
    border-radius:2px;
    color:#FFF;
}

input[type=file]
{
    color:#FFF;
        background:none!important;
        border:none!important;
}

#header,#navbot,#navbotr,.pages,td
{
    color:#FFF!important;
}

.pages td b
{
    color:#909090;
}
img[alt="sticky"] 
{
        height: 0 !important;
        width: 0 !important;
        padding-left: 18px;
        padding-top: 18px;
        background: url(data:image/png;base64,) no-repeat;
        vertical-align:-1px;
}
img[alt="closed"] 
{
        height: 0 !important;
        width: 0 !important;
        padding-left: 18px;
        padding-top: 18px;
        background: url(data:image/png;base64,) no-repeat;
        vertical-align:-1px;
}

.filetitle,.replytitle
{
        color: #FFFFFF;
}

.postername,.commentpostername
{
        color: #117743;
}

.postertrip
{
        color: #228854;
}

.linkmail
{
        color: #FFFFFF;
}

.unkfunc
{
    color:#789922;
}
-webkit-file-upload-button
{
        background-color:#202020;
    background-image:url(data:image/gif;base64,R0lGODlhGAAMAKEEABwcHB0dHSYmJicnJywAAAAAGAAMAAACRkwUYKc7a146MyYFEJRqgG81lNGRUPOIkgMFHtquBgIC7xwvpbrpduUSuXq8ntEC0bBEylYitdDAdM1ViaobkgSgZwyDLAAAOw==);
    border:1px solid #404040;
    border-radius:2px;
    color:#FFF;
}

html
{
    background-image:url(data:image/gif;base64,R0lGODlhGAAMAJEAABkZGRoaGg4ODg8PDyH5BAAAAAAALAAAAAAYAAwAAAJG3DBipxtrXjozJiUQlCqIbzWU0ZFQ84iSAw0e2q4GAgrvHC+luul25RK5erye0QLRsETKViK10MB0zVWJqhuSAKBnDIMsAAA7);
    display:table;
    height:100%;
    width:100%;
}

body
{
    background:none;
    display:table-cell;
    margin:auto;
    vertical-align:middle;
}

a,a:visited
{
    -webkit-transition:text-shadow .5s;
    color:#FFF!important;
}

a:hover
{
    text-decoration:none!important;
    text-shadow:0 0 3px #CCE;
}

#hd,#ft,.yui-g,ul+ul+ul
{
    display:none;
}

#doc
{
    background:#202020 url(data:image/gif;base64,R0lGODlhGAAMAKEEABwcHB0dHSYmJicnJywAAAAAGAAMAAACRkwUYKc7a146MyYFEJRqgG81lNGRUPOIkgMFHtquBgIC7xwvpbrpduUSuXq8ntEC0bBEylYitdDAdM1ViaobkgSgZwyDLAAAOw==) repeat 2px 0;
    border:1px solid #404040;
    border-radius:2px;
    min-width:680px!important;
    width:680px!important;
}

.boxbar
{
    background:none!important;
    color:#FFF!important;
    font-size:10px;
}

#announce
{
    background:none;
    border:none;
    border-radius:5px;
    color:#eee;
    margin-bottom:12px;
}

.top-box
{
    background:none;
    border:none;
    border-radius:5px;
    color:#eee;
}

.menubutton
{
    background-image:url(data:image/png;base64,%3D%3D)!important;
}

#filtermenu
{
    left:-160px!important;
    top:-100px!important;
}

.yuimenu .bd
{
    background:#eee url(data:image/gif;base64,R0lGODlhGAAMAKEEABwcHB0dHSYmJicnJywAAAAAGAAMAAACRkwUYKc7a146MyYFEJRqgG81lNGRUPOIkgMFHtquBgIC7xwvpbrpduUSuXq8ntEC0bBEylYitdDAdM1ViaobkgSgZwyDLAAAOw==) repeat 2px 0;
    border:1px solid #404040!important;
    border-radius:2;
}

.yuimenuitem-selected
{
    backround:none!important;
}
img[alt="404"]
{
        border:none!important;
}
}

@-moz-document domain('images.4chan.org') {
	html 

{
        background:url(data:image/gif;base64,R0lGODlhGAAMAJEAABkZGRoaGg4ODg8PDyH5BAAAAAAALAAAAAAYAAwAAAJG3DBipxtrXjozJiUQlCqIbzWU0ZFQ84iSAw0e2q4GAgrvHC+luul25RK5erye0QLRsETKViK10MB0zVWJqhuSAKBnDIMsAAA7);
        display:table;
        height:100%;
        width:100%;
}

body

{
        display:table-cell;
        vertical-align:middle;
}

img

{
        display:block;
        margin:auto; -webkit-transition:0.6s all;
}

#hd

{
        position:absolute;
        top:0px;
        text-align:center;
        width:750px;
}
}
