css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("freep.com") {

div[id^=towerad],
div[id^=advert-wrapper],
div[class^=articleflex-container],
form[name^=frmOPWidget],
span[id^=iTt],
div[id^=main_left],
div[id^=topad_wrapper],
div[id^=right_ad],
div[id^=rightcol_ad],
div[id^=footer],
div[id^=intfooter],
div[id^=rt_section3],
div[class^=rt_section1_header],
table[id^=articlead],
#rt_section1>table,
p[style="text-align: center; font-size: 9px;"],
div[style="border-top: 1px dotted rgb(102, 102, 102); margin: 10px 0pt; padding-top: 10px;"],
div[style="border: 1px solid rgb(210, 228, 246); background: rgb(239, 245, 251) none repeat scroll 0%; margin-top: 20px; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;"],
table#articlead>tbody>tr>td[style="text-align: center;"],
body>div>div>div>div>div>div>p[style="margin: 10px 0pt 0pt; padding: 0pt; font-size: 10px; color: rgb(102, 102, 102); text-align: right;"]
{display: none !important;}

#main_right { float: left !important ; width: 100% !important; }
#main_colleft { width: 590px !important; }
div[class^=sectionhome_right] { width: 490px !important; }
#subcol_right { float: left !important; width: 100% !important; }

}


@-moz-document domain("ap.org") {

div[id^=topad],
#apcontent>table>tbody>tr>td>p[style="text-align: center; font-size: 9px; color: rgb(51, 51, 51);"],
div[id^=intfooter]
{display: none !important;}


}
