css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.bridgebase.com/forums/") {

body .maintitle{
background:#1D3652 none repeat scroll 0 0 !important;
}
.header{
 background: #1D3652 none repeat scroll 0 0 !important;
color: #FFFFFF !important;
}
div#board_index{
  margin: 0 auto !important;
  width: 960px !important;
}
div#index_stats{
 width: 0 !important;
 float: none !important;
 display: none !important;
}
div#categories{
width: 960px !important;
}
table.ipb_table tr td{
padding-top: 2px !important;
padding-bottom: 2px !important;

}
div#content{
  width: 960px !important;
margin: 0 auto !important;

padding: 0 !important;
}
.post, .entry-content{
line-height: 120% !important;
}

div.signature{
color: #111111 !important;
padding-bottom: 0 !important;
}
.post_wrap h3{
position: relative !important;
top: 0px !important;
padding: 4px;
color: #000000 !important;
 background: #e0e0e0 none repeat scroll 0 0 !important;
}
.post_wrap h3 span a{
color: #000000 !important;
}
div.author_info{
background: #ffffff none repeat scroll 0 0 !important;
padding-top: 0 !important;
padding-bottom: 0 !important;
margin: 0 !important;
color: #000000 !important;
height: 145px !important; 
overflow: hidden !important;

}
ul.user_fields {
color: #000000 !important;
overflow: hidden !important;
}
ul.post_controls{
display: block !important;
float: right !important;
background-image: none !important;
/* padding-top: 0px !important; */
position: relative !important; 
top: -25px !important; 
margin: 0 !important;
padding: 0 !important;
background:#ffffFF none repeat scroll 0 0 !important;
clear: both !important;
}
div.post_controls{
background-image: none !important;
padding-top: 0 !important;
padding: 0 !important;
margin: 0 !important;
margin-bottom: -25px !important;
background:#ffffFF none repeat scroll 0 0 !important;
background-color: #ffffff !important;
}
div.rep_bar{
display: none !important;
height: 0 !important;
padding: 0 !important;
margin: 0 !important;
clear: none !important;
}
div.post_body{
padding: 0 !important;

background: #ffffff none repeat scroll 0 0 !important;
}
div.post_wrap{
min-height: 180px !important; 
background: #ffffff none repeat scroll 0 0 !important;
border: #aaa 1px solid;
border-bottom: none;
padding: 0 !important;
margin: 0 !important;
margin-top: 0 !important;
position: relative !important;
top: 0 !important;
float: left;
width: 960px;
}

span.fc{
max-height: 30px !important;
overflow: hidden !important;
color: #000000 !important;
}
.topic > hr{
/* display: none !important; */

margin: 0 !important;
padding: 0 !important;
}
div.post_block{
border-bottom: none !important;
/* clear: both !important; */
padding: 0 !important;
margin: 0 !important;

}
span.ft, spac.fc{color: #000000 !important;}
li.active, li.active a{

 background: #1D3652 none repeat scroll 0 0 !important;
}
div#primary_nav{
border-bottom:5px solid #1D3652 !important;
}
div.topic{
padding: 0 !important;
margin: 0 !important;

}
}
