css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.nikkei.com/tech/business) {

#PAGE_TOP,
#UNIVERSAL_BAR,
#GLOBAL_MENU,
#LOCAL_MENU,
#RETURN_TO_PAGETOP,
#ABOUT_NIKKEI,
#CONTENTS_SUB,
.JSID_baseBreaking,
.cmn-expand_banner,
.cmn-submarine_pr,
.cmn-pr_list,
.cmn-topic_path + div,
.cmn-large_headline,
.cmn-top_news,
.cmn-section + .cmn-large_headline,
.cmn-top_news + .cmn-section,
.cmn-medium_headline,
.cmn-medium_headline + .cmn-section,
.cmn-sc_news_flash,
.cmn-article_keyword,
.cmn-useful_tools
{ display: none !important; }

body,
#CONTENTS,
#CONTENTS_BODY,
#CONTENTS_OUTER,
#CONTENTS_INNER,
#CONTENTS_MAIN
{
    width: auto !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
    padding: 0 !important;
}


}
