css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Tedious Days More, More and More */
@-moz-document url-prefix(http://blog.livedoor.jp/doubleh_com/) {

.footer-outer,
#sub,
#extra,
#ds_rs,
.ad_rs_a,
#ad2,
#article-options
{ display: none !important; }

body,
#container,
.blog-title-outer,
.blog-title-outer-2,
#content,
#content-inner,
#main,
.column-inner,
.column-inner-2,
.article-outer,
.article-outer-2,
.article-outer-3,
.article-body,
.entry-content,
.article-header,
.article-body-inner,
.related-articles,
.article-tags,
.article-footer
{
    width: auto !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
    font-size: large !important;
}

.blog-title-outer,
.blog-title-outer-2
{
    background: #2222aa !important;
    min-height: 1px !important;
}

#content
{
    margin: 0 0 0 !important;
}


}
