css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") {


.damncrc-icon-roomname {
	display: none !important;
}
.tabbar a[style*="white"]{color:#f1f2cf !important}
.tabbar a[style*="maroon"]{color:#328e41 !important}
.damncrc-chat a{ color: #628263 !important; }
.damncrc-chat a:hover{ color: #75a778 !important; }


.damncrc-chat .other-hl{ background-color: #D7ECF7!important; }
.damncr-title a{ color: #628063 !important; }
.damncr-title a:hover{ color: #3a4a3b !important; }
.damncrc-topic a{ color: #628063 !important; }
.damncrc-topic a:hover{ color: #3a4a3b !important; }
.damncr-infoctr{ border-color: #D7ECF7 !important; }
.damncrc-chat .other-hl{ background-color: #b9fae5!important; }
.damncrc-chat .self-hl{ background-color: #abbba6 !important; }
.damncrc-chat .self-hl span{ background: transparent !important; }
.damncrc-chat .other-hl span{ background: transparent !important; }



.tabbar strong {
-moz-border-radius-topleft: 6px !important; -moz-border-radius-bottomright: 0px !important; -moz-border-radius-bottomleft: 0px !important; -moz-border-radius-topright: 6px !important;
background-color:#95a29b!important;
}
.damncrc-chat-window { max-width:100%;overflow-x:hidden !important; }
.damncrc-chat-window:hover { overflow:auto !important; } 



.sidebar dl.simple dt {
line-height:1em!important;
color:#086a5d !important;
}

dl.simple dd, dl.flat dd {
line-height:1em!important;

}
body.fullscreen #head {
padding-top:0px!important;
padding-bottom:0px!important;
}

#bar-deviant {
line-height:.8em!important;
height:20px;
}

.damncrc-input input, .damncrc-input textarea {
-moz-border-radius-topleft: 10px !important; -moz-border-radius-bottomright: 10px !important; -moz-border-radius-bottomleft: 10px !important; -moz-border-radius-topright: 10px !important;
padding:1px 0px 1px 2px!important;
}

.damncr-members {
padding-left:2px!important;
padding-right:2px!important;
}

body.fullscreen #body {
padding-top:0px!important;
padding-bottom:0px!important;
padding-right:0px!important;
padding-left:0px!important;
}

.damn .alt2 {
padding-top:0px!important;
}
}
