css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.skysports.com/story/") {
div.ss-ad-banner, 
div.ss-ad-tower, 
* div.ss-r-3c div.ss-c2, 
* div.ss-r-layout3 div.ss-c2,
div.ss-wrap div.ss-head div.ss-head-nav,
div.ss-wrap .ss-form-search,
div.ss-wrap ul.ss-menu,
div.ss-wrap ul.ss-menu-sub,
div.ss-wrap div.ss-head,
div.ss-wrap div#aoc-globalnav
{
    display: none !important;
}

div.ss-wrap div.ss-content div.ss-r-3c div.ss-c1
{
    width: 1000px !imporant;
}
div.ss-c1 * p,
div.ss-art-body p
{
    font-family: Georgia, Garamond, sans-serif;
}

}

@-moz-document url-prefix("http://www.skysports.com/football/match_report/") {
div.ss-ad-banner, 
div.ss-ad-tower, 
* div.ss-r-3c div.ss-c2, 
* div.ss-r-layout4 div.ss-c2,
div.ss-wrap div.ss-head div.ss-head-nav,
div.ss-wrap .ss-form-search,
div.ss-wrap ul.ss-menu,
div.ss-wrap ul.ss-menu-sub,
div.ss-wrap div.ss-head,
div.ss-wrap div#aoc-globalnav
div.ss-tabs
{
    display: none !important;
}

div.ss-wrap div.ss-content div.ss-r-3c div.ss-c1
{
    width: 1000px !imporant;
}
div.ss-c1 * p,
div.ss-art-body p
{
    font-family: Georgia, Garamond, sans-serif;
}


}
