css:
@-moz-document domain('o-go.ru') {

/* Общий сброс */

html, body, table, b, span, div, strong, a, i, li, ol, ul, h1, h2, h3, h4, h5, h6 {
    font-family: Arial !important;
    }

html, body, table, b, span, div, strong  {
    margin: 0px !important; 
    padding: 0px !important;
    font-size: 14px !important;
    color: #111111 !important;
}

a {color: #065588 !important;}

table {border-spacing: 0 !important;}

td[rowspan="3"], td[height="11px"], img[src="images/world.png"], b[class*="r"] {display: none;}

/* Хедер */

form > table[width="920px"] {margin-top: -35px !important;}
form > table[width="920px"] td[width="110px"] {
    position: relative; 
    height: 0px !important}

form > table[width="920px"] td[width="300px"] {width: 100px !important}
form > table[width="920px"] td[align="center"] {text-align: left !important}
input[name="s"], input[value="Поиск"] {
    position: relative; 
    top: 45px; 
    left: 50px; 
    display: block !important; 
    padding: 0 !important; 
    font-size: 16px !important;
    font-family: Arial !important;
    }

a.menu_logo[href="http://vsemulti.ru"] {font-weight: normal !important;}
a.menu_logo[href="http://o-go.ru/catalog/"] {font-weight: bold !important;}


/* Пагинатор списка релизов */

.pager1_caption {font-size: 1em !important;}
a[class*="pager1"] {margin: 0 0.2em !important;}
a[class="pager1_current"] {font-weight: bold;}

a[class="pager1_prev_str"] {
    font-weight: bold; 
    font-size: 1em !important;
    }

td.info > div[style="padding-left:5px"] {margin-bottom: 1.5em !important}


/* Список релизов */

.reliz_header {font-family: Arial !important;}

table.reliz_header table td span {
    font-size: 0.95em !important;
    color: #333333 !important;
    }

a.reliz_header {
    font-weight: bold !important; 
    font-size: 1.1em !important}

span.reliz_header {
    font-size: 1.2em !important;
    font-weight: bold;
    }
    
table.ratingsButtons td , table.ratingsButtons td {font-size: 0.95em !important;}

td[height="60px"] td[align="center"] {text-align: left;}

div [style="margin: 15px auto; position: reletive; width: 800px; height: 78px;"] {display: none;}

table [width="100%"][cellspacing="0"][cellpadding="0"][border="0"] {display: none;}
table.stat {display: table ! important;}

.text_img * {font-size: 12px !important;}
.text_img > a {display: none;}      /* Зачем мне знать, в каком городе сделан релиз ?  */

span.hit  * {font-weight: normal;}
a.hit strong {color: #065588 !important;}

/* Релиз */

div.spoilerHref {height: auto !important; width: auto !important; padding: 0 2px !important;}

.spoilerHeaderLink, .spoilerText, .info, #cutText, .bb, font, color {
    font-size: 16px !important; 
    color: #111111 !important; 
    }

.spoilerHeaderLink, .spoilerHeaderLink strong {font-weight: normal !important;}


/* Обновлялка комментов */

div#updatefix {top: 49% !important; height: 27px !important; padding-top: 30px !important;}

/* Комментарии */

a.SpoilerA {
    height: 100% !important;
    font-size: 1em !important;
    }

div.comment_rating_zero     {color: #777777 !important;}
div.comment_rating_red      {color: #dd0000 !important;}
div.comment_rating_green, .comment_colnew    {color: #00aa00 !important;}


.comment_hideHeader {color: #777777 !important;}


}
