css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") {

#bar-deviant a{ color:#ffffff !important; }
#bar-deviant{ color:#aaff00 !important; background-color: #000000 !important; border: 0px !important; }
.alt0{ background-color: #000000 !important; }
.alt1{ background-color: #000000 !important; }
.aside-left, .damncr .userinfo .avatar{ background-color: #000000 !important; border: 0px !important; }
.damn-lo-row-top{ background-color: #000000 !important; border: 0px !important; }
.damncr-infoctr { color: #aaff00 !important; border: 0px !important; border: 0px !important; }
.damncr-infoctr a:hover{ color: #ffffff !important; }
.damncr-infoctr a{ color: #ffffff !important; }
.damncr-title a:hover{ color: #ffffff !important; }
.damncr-title a{ color: #ffffff !important; }
.damncr-title{ background-color: #000000 !important; color: #aaff00 !important; border: 0px !important; }
.damncrc-chat .join, .damncrc-chat .part{  color: #999999 !important; }
.damncrc-chat .other-hl{ background-color: #999999 !important; }
.damncrc-chat .self-hl{ background-color: #666666 !important; }
.damncrc-chat .userinfo-inner .avatar, .damncrc-chat .userinfo-inner .bodyarea{ background: none !important; background-color: #000000 !important; border: 0px !important; }
.damncrc-chat .userinfo-inner div.read a:hover{ color: #ffffff !important; }
.damncrc-chat .userinfo-inner div.read a{ color: #ffffff !important; }
.damncrc-chat .userinfo-inner div.read{ color: #aaff00 !important; background-color: #000000 !important; border: 0px !important; }
.damncrc-chat a:hover{ color: #ffffff !important; }
.damncrc-chat a{ color: #ffffff !important; }
.damncrc-chat span{ background: transparent !important; color:#aaff00 !important; border: 0px !important; }
.damncrc-chat{ background: #000000 !important; color: #aaff00 !important; border: 0px !important; }
.damncrc-error{ background-color: #000000 !important; color: #aaff00 !important; border: 0px !important; }
.damncrc-topic a:hover{ color: #ffffff !important; }
.damncrc-topic a{ color: #ffffff !important; }
.damncrc-topic{ background-color: #000000 !important; color: #aaff00 !important; border: 0px !important; }
.main span { color:#ffffff !important; border: 0px !important; }
.sidebar dl.simple dt { color:#ffffff !important; border: 0px !important; }
.tabbar a[style*="maroon"]{ background-color: #000000 !important; color:#aaff00 !important; border: 0px !important; }
.tabbar a[style*="white"]{ background-color: #000000 !important; color:#ffffff !important; border: 0px !important; }
.tabbar strong{ background: #000000 !important; color: #999999 !important; border: 0px !important; }
div#body { background-color: #000000 !important; border: 0px !important; }
dl.simple dd, dl.flat dd { line-height:1.2em !important; color:#aaff00 !important; border: 0px !important; }
input.button:hover{ color: #aaff00 !important; }
input.button{ -moz-border-radius: 0 !important; background-color: #000000 !important; border: 0px !important; color: #aaff00 !important; font-weight: bold !important; font-size: 10px !important; text-transform: uppercase !important; padding: 3px !important; margin: 0px !important; }





}
