css:
/*
 * FORUM Ubuntu-fr (Version Classique)
 *
 * Auteur                 : EauLand
 * Mise a jour            : 05 Aout 2011
 * Base sur               : Style Ubuntu (Style par defaut)
 * Teste sur              : Firefox 3.6.x et superieur
 * Incompatibilite        : Chrome
 * Resolution conseillee  : 1024 x 600 ou +
 *
 * Changelog
 * -----------------------------------------------------------------
 * 1.1.3  (Mise a jour suite a quelques modifications mineures du site)
 * 1.1.2  (optimisations)
 * 1.1.1  (rajout des liens deconnexion et profil quand l'utilisateur est connecte)
 * 1.1.0  (correction des erreurs en css, suppression des css inutiles, amelioration de la sidebar gauche)
 * 1.0.0  (structure modifiee)
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.ubuntu-fr.org") {
/*
  Initialisation
  -----------------------------------------------------------------
 */
#page {
  border: none !important;
}
body,
#page,
#header *,
#navigation * {
  margin: 0 !important;
  padding: 0 !important;
  text-decoration: none !important;
  list-style-type: none !important;
}
/*
  Structure
  -----------------------------------------------------------------
 */
html, body, #page {
  min-height: 500px !important;
  background-color: transparent !important;
  background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFAQMAAAC3obSmAAAABlBMVEV6YUOBZkcZxVfjAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH1AwSESkNXYisnQAAABJJREFUeNpj+MDwguEGww6GCgAVqgPhaGRGXAAAAABJRU5ErkJggg==") !important;
  background-position: 0 0 !important;
  background-repeat: repeat !important;
}
#wrapper {
  padding-top: 20px !important;
  padding-left: 200px !important;
}
#navigation {
  width: 180px !important;
  margin-left: -5px !important;
  margin-top: 20px !important;
}
#accessibar {
  display: none;
}
#search select,
#search input,
form#login_top input {
  outline: none;
  resize: none;
}
/*
  Entete
  -----------------------------------------------------------------
 */
#header {
  height: 110px !important;
  background: #d9bb7a url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAABuCAIAAABp1e5RAAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAALEwAACxMBAJqcGAAAAAd0SU1FB9oKGgIvCWIhbhQAAAAiSURBVBjTY7i5u4qJgYFhFJOHmUU5uZgYGBiYdLQk6WkvAIRXA4HhS4U9AAAAAElFTkSuQmCC") top left repeat-x !important;
}
#logo {
  height: 78px;
}
#logo h1 {
  position: absolute;
  width: 100%;
  background: transparent url("data:image/png;base64,") top right no-repeat;
  min-height: 80px !important; 
}
#logo a {
  display: block;
  font-size: 13px !important;
  font-family: sans-serif;
  color: #000 !important;
  padding-left: 15px !important;
  padding-top: 60px !important;
  background-image: url("data:image/png;base64,") !important;
  background-position: 15px 0;
  background-repeat: no-repeat !important;
}
#logo a:hover {
  color: #000 !important;
  text-decoration: underline !important;
}
#search {
  position: absolute;
  right: 0;
  top: 1px;
  display: block !important;
  width: 397px !important;
  background-color: transparent !important;
  border-width: 0px !important;
}
#search fieldset {
  height: 20px !important;
  padding-top: 7px !important;
  padding-left: 30px !important;
  background-image: url("data:image/png;base64,");
  background-position: 3px 0;
  background-repeat: no-repeat;
}
#search input[type=text] {
  width: 196px !important;
  background-image: none !important;
  color: #353535 !important;
  background-color: #fff !important;
}
#search select {
  width: 126px !important;
  height: 18px !important;
  margin-left: 6px !important;
  margin-top: -7px !important;
  border-right-width: 0px !important;
  background-color: #fff !important;
}
#search select option {
  background: #ebebeb !important;
  color: #353535 !important;
}
#search select option:hover {
  /*background: #5fb9ed !important;*/
  background: #ccc !important;
}
#search input.button {
  border-width: 0px !important;
  text-indent: -10000px;
}
#brdwelcome .conl {
  position: absolute;
  right: 7px;
  margin-top: 87px !important;
}
#brdwelcome .conl a {
  color: #000 !important;
}
#brdwelcome .conl strong {
  padding-right: 10px !important;
}
/*
  Menu
  -----------------------------------------------------------------
 */
#navigation ul,
#navigation ul li,
#navigation ul li a {
  background-color: transparent !important;
  background-image: none !important;
  border: none !important;
}
#menu-accueil,
#menu-doc,
#menu-planet {
  position: absolute;
  top: -44px;
  background-image: none !important;
}
#menu-accueil *,
#menu-doc *,
#menu-planet * {
  font-weight: bold !important;
  background-image: none !important;
}
#menu-accueil span,
#menu-doc span,
#menu-planet span {
 display: block !important;
 height: 28px !important;
 line-height: 28px !important;
 vertical-align: middle !important;
 padding-left: 34px !important;
 background-image: url("data:image/png;base64,") !important;
 background-repeat: no-repeat !important;
}
#menu-accueil span:hover,
#menu-doc span:hover,
#menu-planet span:hover {
  color: #fbfbfb !important;
}

#menu-accueil span {background-position: 0 3px !important;}
#menu-doc span     {background-position: 0 -70px !important;}
#menu-planet span  {background-position: 0 -221px !important;}

#menu-accueil span:hover {background-position: 0 -32px !important;}
#menu-doc span:hover     {background-position: 0 -105px !important;}
#menu-planet span:hover  {background-position: 0 -256px !important;}

#menu-accueil {left: -5px;}
#menu-doc     {left: 100px;}
#menu-planet  {left: 250px;}

/*
  Sidebar Gauche
  -----------------------------------------------------------------
 */
form#login_top {
  position: absolute;
  width: 185px !important;
  top: 319px;
  left: -20px;
}
form#login_top * {
  color: #000 !important;
}
form#login_top fieldset {
  height: 130px !important;
  width: 180px !important;
  padding-top: 40px !important;
  padding-right: 28px !important;
  background: transparent url("data:image/png;base64,") 28px 0 no-repeat;
}
form#login_top input[type="text"],
form#login_top input[type="password"] {
  text-indent: 2px;
  display: block !important;
  border-radius: 0px !important;
  -moz-border-radius: 0px !important;
  -khtml-border-radius: 0px !important;
  -webkit-border-radius: 0px !important;
  margin-left: 48px !important;
  margin-bottom: 10px !important;
  width: 140px !important;
  height: 22px !important;
  background-color: #fff !important;
}
form#login_top input[type="text"] {
  margin-top: 5px !important;
}
form#login_top input[type="password"] {
  margin-bottom: 15px !important;
}
form#login_top input[type="submit"] {
  margin-top: -2px !important;
  display: inline !important;
  font-size: 1em !important;
font-family: Arial !important;
}
/* Le Menu du forum */
#active {
  margin-top: 0px !important;
}
#navigation {
  min-height: 155px !important;
  padding-bottom: 18px !important;
  background: #d9bb7a url("data:image/png;base64,") -4px -3px no-repeat;
  border-radius: 0 0 8px 8px;
  -moz-border-radius: 0 0 8px 8px;
  -khtml-border-radius: 0 0 8px 8px;
  -webkit-border-top-left-radius: 0;
  -webkit-border-top-right-radius: 0;
  -webkit-border-bottom-right-radius: 8px;
  -webkit-border-bottom-left-radius: 8px;
}
#menu-forum * {
  background-image: none !important;
}
#menu-forum {
  text-indent: -10000px !important;
}
#navigation li h2 {
  display: none !important;
}
#navigation .menu {
  margin-top: 2px !important;
}
#navigation .menu .inbox {
  padding-top: 15px !important;
}
#navigation .menu li a {
 text-indent: 18px !important;
 background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAAN1wAADdcBQiibeAAAAAd0SU1FB9oKGg4qOqmcAiMAAAAYSURBVDjLY2AYBcMAMOIQ/0+C2lEwChgAN2MBAscph+YAAAAASUVORK5CYII=") !important;
 background-position: 0 3px !important; 
 background-repeat: no-repeat !important;
}
#navigation .menu li a:hover {
  font-weight: normal !important;
  text-decoration: underline !important;
  color: #000 !important;
}
#navigation .menu .isactive a,
#navigation .menu .isactive a:hover {
  font-weight: normal !important;
}
#navigation a:before{
  /*content: '-';*/
  content: none !important;
}
/*
  Contenu
  -----------------------------------------------------------------
*/
#wrapper {
  padding: 0 !important;
  margin: 0 !important;
  margin-left: -20px !important;
  padding-top: 20px !important;
}
/*
  Pied de page
  -----------------------------------------------------------------
*/
.content .menu {
  display: none !important;
}
/*
  FIN
  -----------------------------------------------------------------
*/
}
