css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("habrahabr.ru") {
.corner, .avatar>A>B, .banner, #special_nokia, DIV[style="height:4px"] {
    display: none !important; }
body, .anonymous-div, .js-autosuggest-output>div:not(.active), .inner, .header, #header, .habrAjaxSettings {
    background-color: /*[[globalbgcolor]]*/ !important; }
.live, .tags, dd, .block, .pay, #write, .comments-header, .post>.blog, h2>.blog, h2, .job-price, .karma, .habraforce, .locked, .even, .habrablock, #post-wrapp, .comment_item, DIV[class*="bad"], .comments, .post_title, .topic, .content>DIV:first-child {
    background-color: transparent !important; }
.js-autosuggest-output>.active, code, .is_new {
    background-color: /*[[code-bgcolor]]*/ !important; }
.autocomplete {
    background-color: #c0c0c0 !important; }
IMG[src$=".png"], IMG[src$=".PNG"] {
    background-color: white !important; }
body, .main_menu>.active, .current>.disabled, .habrauser, .username, time, #comment_text, div.count, .day, .month, .panel-nav-top>.current>a, .anonymous-div, .description, dl, .title, .detail, .charge, .bottom>a, .top>a, .blog_title, .topic, DIV[class*="bad"], .searched-item, H2, #global_notify .inner {
    color: /*[[textcolor]]*/ !important; }
.add_post>a, #write>a>img {
    border-radius: 10px !important;
    border-bottom-right-radius: 0 !important; }
#write, .item:not(.active) {
    border-color: *[[globalbgcolor]]*/ !important; }
.logo[href="http://habrahabr.ru/"] {
    background-position: 0 0 !important;
    width: 240px !important;
    height: 130px !important; }
TD.item, .page-menu>tbody>tr>td:nth-child(2).current {
    border-right: 1px solid /*[[globalbgcolor]]*/ !important; }
TD.item:last-child {
    border-right: 2px solid /*[[globalbgcolor]]*/ !important; }
.page-submenu {
    border-left: 0 !important;
    border-right: 1px solid /*[[globalbgcolor]]*/ !important;
    border-bottom-left-radius: 9px !important;
    border-bottom-right-radius: 9px !important;
    margin-left: 35px !important; }
#sub-nav.logged, .message-page-menu>LI>B, .poll-item>DL, .comments, .comment_item  {
    border-color: transparent !important; }
.submenu.right {
    border-right: 2px solid /*[[globalbgcolor]]*/ !important; }
.page-submenu>ul, .page-submenu, .page-menu>tbody>tr>td>div, .page-menu>tbody>tr>td, .live, .bg-bott-r, .bg-bott-l, H3, .page-submenu, .page-submenu>*, .page-menu>*>*>*>*:not(#write-mess), .page-menu>*>*>*, LI, B, .bg-top-r, #post-wrapp, .bg-bott-l>fieldset>table {
    background-image: none !important; }
.page-menu>tbody>tr>td {
    border-right: 1px solid /*[[globalbgcolor]]*/ !important;
    border-top-left-radius: 9px !important;
    border-top-right-radius: 9px !important; }
.message-page-menu>LI {
    border-top-left-radius: 9px !important;
    border-top-right-radius: 9px !important;}
.page-menu>tbody>tr>td:not(.current) {
    border-bottom: 1px solid /*[[globalbgcolor]]*/ !important; }
.menu>tr>td>.item:last-child, .sidebar_right, #sidebar {
    width: 300px !important; }
.submenu.right>.item {
    text-align: center !important;
    min-width: 150px !important; }
#wrapper {
    width: 100% !important; }
#inner, #layout {
    max-width: none !important; }
#main-content, .content_left {
    width: -moz-calc( 100% - 300px ) !important;
    width: calc ( 100% - 300px ) !important;
    width: -webkit-calc( 100% - 300px ) !important; } 
.profile-header {
    width: 100% !important; }
#layout {
    padding: 0 !important;
    margin-left: 35px !important; }
DIV[class*="bad"]:hover, DIV[class*="bad"] {
    -moz-transition: opacity .75s !important;
    transition: opacity .75s !important;
    -webkit-transition:  opacity .75s !important;
    opacity: 1 !important; }
.message.bad.bad5:not(:hover) {
    opacity: 0.25 !important; }
.message.bad.bad4:not(:hover) {
    opacity: 0.4 !important; }
.message.bad.bad3:not(:hover) {
    opacity: 0.55 !important; }
.message.bad.bad2:not(:hover) {
    opacity: 0.7 !important; }
.message.bad.bad1:not(:hover) {
    opacity: 0.85 !important; }
.logo[href="http://habrahabr.ru/"], .panel-personal, .panel-tools, .panel-nav-top, #sidebar, .sidebar_right {
    position: absolute !important; }
#sidebar, .sidebar_right {
    right: 0 !important; }
.panel-personal, .userpanel.silver {
    left: 105px !important;
    top: 0 !important;
    padding: 0 !important; }
.panel-tools, .search:not(#main-page)  {
    left: 353px !important;
    top: 3px !important;
    width: 271px !important; }
#search_form>input:first-child {
    padding: 2px !important; }
.panel-tools {
    margin-left: -2px !important;
    top: -20px !important; }
.panel-nav-top, .main_menu {
    padding: 0 !important;
    left: 102px !important;
    top: 100px !important;
    min-width: 0 !important; }
.panel-nav-top>li, .main_menu>a:not(:last-child) {
    font-size: 23px !important;
    margin-right: 15px !important; }
.logo[href="http://habrahabr.ru/"] {
    margin: 0 !important;
    background-position: 54% 51% !important;
    width: 100px !important;
    height: 110px !important;
    top: 0;
    left: 0; }
.header, #header {
    width: 100% !important;
    max-width: none !important;
    padding: 0 !important;
    min-height: 113px !important;
    top: 0;
    left: 0;
    z-index: 99 !important;
    border-bottom: 1px solid /*[[code-bgcolor]]*/ !important; }
/*[[fixed]]*/
.blog_header, .main_menu {
    position: relative !important; }
.blog_header {
    top: 130px !important; }
.main_menu {
    left: 102px !important;
    top: 84px !important; }
.habrauser-charge {
    margin-top: 5px !important; }
#comments {
    padding-top: 120px !important;
    margin-bottom: -120px !important;
    position: relative !important;
    top: -150px !important; }
a[name="habracut"] {
    margin-bottom: -120px !important;
    position: relative !important;
    top: -110px !important; }
BODY {
    padding-top: 130px !important; }
}
