css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("forums.somethingawful.com") {


/* RFA Fix */

body {
	background-color:#fff;
	background-image:none;
}
body, a { color: #550; }
a:hover { color: #660000; }
body, a:visited { color: #000; }
a:active { color: #0f0; }
#notregistered, #searchboxes { background: none; }
#forum, table { clear:left; }
#forum th, #forum th a, div.threadbar, div.forumbar { color: #fff; }
#thread div.quote { border-color: #aaa; }
#forum th, div.threadbar, div.forumbar { background: #069; }
/*#forum td.icon, #forum td.author, #forum td.views, #forum td.lastpost, #thread li.post, #subforums td.topics, #forum td.votes, #forum.asktell td.icon2 { background: #F1F1F1; }*/
/* base thread list column bg colors */
#forum td.icon, #forum td.author, #forum td.views, #forum td.lastpost, #thread li.post, #subforums td.topics, #forum td.votes, #forum.asktell td.icon2 { background: #dfdfdf; }
.threadlist td { background-color:#f1f1f1; }
/*#forum td.views, #forum td.lastpost { color:#f00; font-family:"courier new",fixed; font-style:italic; }*/
#forum td.views { color: #000; font: normal normal normal 13px Verdana, Arial, sans-serif; }
#forum td.lastpost { color: #000; font: normal normal normal 10px Verdana, Arial, sans-serif; }

#thread li.post.even, #subforums, #subforums td, #forum, #forum td, #filter, table#thread tr.even td, #forum.asktell td.icon, #thread.review td { background: #F1F1F1; }
div.threadbar, #subforums, #subforums td, #forum, #forum th, #forum td, #filter, div.forumbar, #thread table.post, #thread td { border: 1px solid #C1C1C1; }
/*div.threadbar, #subforums, #subforums td, #forum, #forum td, #filter { border: 1px solid #2df; }*/
#thread td.postbody { border-top:none}

#subforums { clear:left; }
#thread dl.userinfo dt.author { color: #000; font: 13px Verdana bold; letter-spacing:0px; }
#thread dd.registered { color: #060 ! important; }
#flag { margin-bottom: 1em; }
#forum th { padding: 2px 3px; }
#thread div.title { font: normal normal normal 10px Verdana, Arial, sans-serif; margin-top:1em; }
#thread div.title * { text-align: left; }
p.signature { border:0; }

.funbox { margin-left: 80px; }

tr.altcolor1 td { background-color:#f4f4f4; }
tr.altcolor2 td { background-color:#e8e8e8; }

table.standard { background:black; border:1px black solid; }
table.standard td { border:1px black solid; }
.standard h2, table.standard th { background:#069; color:white; border:0; }
table.standard td { background: #f4f4f4; }

table.standard tr td.altcolor1 { background:#f4f4f4; }
table.standard tr td.altcolor2 { background:##e8e8e8; }
.altcolor1, tr.altcolor1 td, td.altcolor1 { background-color:#f4f4f4; }
.altcolor2, tr.altcolor2 td, td.altcolor2 { background-color:#E8E8E8; }

.postbody table.funbox tr td.complete_shit { background:white; }

td.postdate { width:168px; }

#thread.review td { border-color:#999; }
#thread.review td { background:none; }
#thread.review dl { min-height:60px; }
#thread.review tr td.userinfo, #thread.review td.postdate { display:table-cell; }

.bbc-center { text-align:left; }

/* 6-30-2010 Change Fix */
#content a { color:#036; }
#content div.pages .curpage { color:#666; }
#content a:visited { color:#000; }
#content div#mp_bar a,
#content div.pages a,
#content div.breadcrumbs a,
#content div.breadcrumbs a:visited { color:#000; }

#content div.breadcrumbs a.bclast,
#content div.pages a { border-color:#555; }


td.postbody div.bbc-block { 
	background-color:transparent;
	background-image:none;
	padding:0;
	padding-left:0;
	border:none;
	border-bottom:1px #999 solid;
}

td.postbody div.bbc-block h4 { border:0; padding:8px 0 2px 0; border-top:1px #999 solid; font-size:13px; font-style:italic;}
.bbc-center { text-align:center; }

ul#navigation {
	background-image:none;
	background-position:center bottom;
	padding-bottom:0;
}

table.standard tr.postwarning td {
	background:none;
	color:none;
	font-weight:none;
        display: none;
        !important;
}

/* ad removal */
#ad_banner_user, .ad_banner_oma, #searchboxes, #nav_purchase, #copyright, #notregistered, #unregskyscraper, div#flag_container { display: none !important; }
div.oma_pal {
display: none !important; 
}
span.bbc-spoiler { color: #000000; background-color: #000; }

/*Forums Cancer Fix*/
td.postbody .cancerous {opacity:1.0 !important;}
}
