css:
/**********
Theme by Jeremy Boutin of http://www.rockdown.net
Modified by no1szy to addapt to new ESEA version.
**********/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("esea.net"), domain("esea.la"),  domain("esportsea.com") {

  /*onfocus, invert border shading*/
	input:not([type="button"]):not([type="reset"]):not([type="submit"]):not([type="checkbox"]):focus, textarea:focus, select:focus  {  
	-moz-border-radius: 0em !important;  
	background-color: #222 !important;
	border: #777 0px solid !important;
	color: #bbb !important;
	padding-left:4px !important;
	padding-top:4px !important;
	font-size: 10px !important;  } 
 

  /* Hover effect for inputs */
	input:hover,   input[type="textarea"]:hover,  input[type="select"]:hover,  textarea:hover  {
	border: #000 0px solid !important;
	padding-left:4px !important;
	padding-top:4px !important;
	font-size: 10px !important;  }

  /*rollover effect on the buttons*/
	input[type="submit"]:hover,input[type="reset"]:hover,button:hover   {    
	border: #000 1px solid !important;  }

  /* Header Background */
	html {background: #1c1c1c url('http://www.rockdown.net/eseal.png') repeat-x center top !important;}

	body {background: transparent url('http://www.rockdown.net/ESEAB3.png') no-repeat right top !important}


  /* F0nt Edits */
	body, td, th, select, input, h1, h2, h3, h4, h5, h6, div, span, label {
	font-family: Trebuchet MS !important;}

  /* Qu0te Editz */
	.comment-quote {
	background-color:#e1e1e1 !important; 
	padding-bottom:15px !important; 
	padding-top:5px !important;
	padding-left: 25px !important; 
	color: #606060 !important;
	background-image: url('http://www.rockdown.net/eseaq.png') !important;
	background-repeat: no-repeat !important;
	background-position:top left !important;}


div.layout-standard {
	background-image: url('http://rockdown.net/esealayout-standard_bg.png') !important;}


  /* Emoticons */
img[src$="/images/emoticons/1.gif"]{margin-bottom:-6px !important; height:0 !important;width:20px !important;padding-top:20px !important;background:url(http://rockdown.net/ESEAMotes/smile.gif) no-repeat !important}
img[src$="/images/emoticons/2.gif"]{margin-bottom:-6px !important; height:0 !important;width:20px !important;padding-top:20px !important;background:url(http://rockdown.net/ESEAMotes/biggrin.gif) no-repeat !important}
img[src$="/images/emoticons/3.gif"]{margin-bottom:-6px !important; height:0 !important;width:18px !important;padding-top:18px !important;background:url(http://rockdown.net/ESEAMotes/crying_anim02.gif) no-repeat !important}
img[src$="/images/emoticons/4.gif"]{margin-bottom:-6px !important; height:0 !important;width:20px !important;padding-top:20px !important;background:url(http://rockdown.net/ESEAMotes/angry.gif) no-repeat !important}
img[src$="/images/emoticons/5.gif"]{margin-bottom:-6px !important; height:0 !important;width:20px !important;padding-top:20px !important;background:url(http://rockdown.net/ESEAMotes/wink2.gif) no-repeat !important}
img[src$="/images/emoticons/6.gif"]{margin-bottom:-6px !important; height:0 !important;width:20px !important;padding-top:20px !important;background:url(http://rockdown.net/ESEAMotes/eh.gif) no-repeat !important}
img[src$="/images/emoticons/7.gif"]{margin-bottom:-6px !important; height:0 !important;width:20px !important;padding-top:20px !important;background:url(http://rockdown.net/ESEAMotes/cool02.gif) no-repeat !important}
img[src$="/images/emoticons/8.gif"]{margin-bottom:-6px !important; height:0 !important;width:20px !important;padding-top:20px !important;background:url(http://rockdown.net/ESEAMotes/tongue.gif) no-repeat !important}
img[src$="/images/emoticons/9.gif"]{margin-bottom:-6px !important; height:0 !important;width:20px !important;padding-top:20px !important;background:url(http://rockdown.net/ESEAMotes/worried_anim.gif) no-repeat !important}
img[src$="/images/emoticons/10.gif"]{margin-bottom:-6px !important; height:0 !important;width:20px !important;padding-top:20px !important;background:url(http://rockdown.net/ESEAMotes/ohno02.gif) no-repeat !important}

  /* Premium Icons */
img[src$="/images/global/icon_premium.gif"]{height:0 !important;width:14px !important;margin:0px !important; padding-top:14px !important;background:url(http://rockdown.net/ESEA1.png) no-repeat !important}
img[src$="/images/global/icon_platinum.gif"]{height:0 !important;width:14px !important;padding-top:14px !important;background:url(http://rockdown.net/ESEA2.png) no-repeat !important}
}

/**********
Fixes For Steamid, and all other pages off domain.
**********/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("steamid.esea.net"), domain("steamid.esportsea.com"), domain("file001.esportsea.com"), domain("file001.esea.net"), url("http://esea.net/global/find_user.php?form_name=message&field_name=recipients&multiple_users=true") {
	html {background: none !important;}
	body {background: none  !important}
        body.parent {color:#141414!important; color:#fff !important;}
}
