css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.yahoo.com") {
div#uh > div.brand > h1, div#theAd, div.upsell, div.links > span, div.yuhead-logo, div#yuhead-promo, ul#yuhead-com-links, #shellsidepane, #todo-resize {display:none !important}
div#menu-external-links {left:auto !important; right:0 !important}
div#yuhead-search {position:relative !important; top:-31px !important}
div.links > span#btn-external-links {display:block !important; position:relative !important; ; top:-14px !important; left:20px !important;}
div.links > span#btn-external-links a {width:12px !important; background-image:none !important; color:transparent !important; overflow:hidden !important}
body > div#menu-external-links {left:auto !important; right:10px !important}
div#uh > div.right > div#search {margin-top:3px !important;}
div#uh {height:37px !important}
div.nav-bar {top:37px !important}
div#main {top:67px !important}
div#shellnavigation, div#shellcontent {top:103px !important}
.panescroll #shellcontent {right:0 !important}
div.nav-bar > div.tabs {background: transparent url("data:image/png;base64,%3D") 20px 14px no-repeat !important}
}
