css:
@-moz-document domain('board.ogame.org'), url-prefix('http://board.ogame.org/') {

html {
background-color: #000000 !important;
background-image: none !important;
color: #99a8c7 !important;
}

body {
background-color: #121c28 !important;
width: 92% !important;
margin: 26px auto 0 auto !important;
background-image: none !important;
}

#footer {
border: none !important;
padding-top: 0 !important;
padding-bottom: 0 !important;
}

#toTopLink {
border: none !important;
}

div#main, div#footer, div#userPanel {
min-width: 92% !important;
}

.stylePicker {
display: none !important;
}

#userCardCredits {
border-left: 1px solid black !important;
}

#userCardCredits div {
border: none !important;
}

.containerHead {
margin-right: 0 !important;
}

.boxBuddies div div {
margin: 0 !important;
border: none !important;
}

.boxNews .content{
margin: -1px -2px -2px -2px !important;
}

.boxNewUser .container-1 .containerContent {
margin: -1px 0 -2px -3px !important;
}

.boxNewUser span {
padding: 1px !important;
color: #99a8c7 !important;
}

.boxBoardStats li p {
margin: 0 -5px -3px -6px !important;
}

.boxBoardStats span {
color: #99a8c7 !important;
}

#boardlist .boardlistInner,
div .container-1,
div .container-2 {
background-color: #1F273c !important;
border: 1px solid #000000 !important;
margin: -1px 0 0 0 !important;
}

#boardlist .containerHead {
background-color: #171e2f !important;
background-image: none !important;
border: 1px solid #000000 !important;
}



.mainMenu .firstActive a,
.mainMenu .lastActive a,
.mainMenu .active a {
color: #ffffff !important;
}

.mainMenu a,
.subHeadline {
color: #99a8c7 !important;
}

table.tableList {
margin: 0 !important;
padding: 0 !important;
border-collapse: collapse;
}

td {
border: 1px solid #000000 !important;
color: #99a8c7 !important;
}

td.columnTopic,
td.columnMarkTopics,
td.columnViews {
background-color: #1f273c !important;
}

td.columnIcon,
td.columnLastPost,
td.columnReplies{
background-color: #171e2f !important;
}

td.columnRating {
text-align: center;
}

th {
background-color: #0a101d !important;
background-image: none !important;
border: 1px solid #000000 !important;
}

th div a {
background-color: #0a101d !important;
color: #ffffff !important;
}

.userPanel a,
.breadCrumbs a {
color: #ffffff !important;
font-style: normal;
}

.userPanel a span,
.breadCrumbs a span {
text-decoration: none !important;
}

.userPanel a span:hover,
.breadCrumbs a span:hover {
text-decoration: underline !important;
}

.breadCrumbs {
background-color: transparent !important;
text-decoration: none !important;
}

.tableHead div a{
border-bottom: none !important;
}

ul ul .boardlistInner div.containerIcon {
margin: 10px 12px !important;
}

a:hover, a:focus {
font-weight: normal !important;
}

#mainMenu a:hover, #mainMenu a:focus, a.new:hover , a.new:focus, h3 a:hover, h3 a:focus, h2 a:hover, h2 a:focus, strong a:hover, strong a:focus, .new a:hover, .new a:focus {
font-weight: bold !important;
}

#mainMenu a:hover {
color: #ffffff !important;
}

h3 {
color: #99a8c7 !important;
font-weight: bold !important;
}

p,
.topic,
td.columnTopic div a {
color: #99a8c7 !important;
font-weight: normal !important;
}

td.columnTopic .new p a {
font-weight: bold !important;
}

div .contentFooter {
margin-top: 7px !important;
}

.prefix strong {
color: #ffffff !important;
font-weight: bold !important;
}


.container-2 table {
border-collapse: collapse;
margin: -2px 0 -3px -2px !important;
}

.container-2 table td {
padding: 2px !important;
}

table.container-1,
.userProfileContent table {
border-collapse: collapse;
margin: 0 1px -1px 1px !important;
}

#searchInput {
background-color: #ffffff !important;
color: #000000 !important;
border: 1px solid #000000 !important;
}

.userProfileContent table td {
padding: 2px !important;
margin-left: 3px !important;
}

#userProfileOptions,
#userProfileAdminOptions,
#userProfileBuddies,
#userProfileAvatar {
border: 1px solid #000000 !important;
margin-top: 15px !important;
}

#userProfileOptions a,
#userProfileAdminOptions a,
#userProfileBuddies a {
background-color: #1f273c !important;
color: #ffffff !important;
}

#userProfileOptions a:hover,
#userProfileAdminOptions a:hover,
#userProfileBuddies a:hover,
#userProfileOptions a:active,
#userProfileAdminOptions a:active,
#userProfileBuddies a:active {
text-decoration: underline !important;
}

#userProfileAvatar div div.container-1 {
border: none !important;
}

.userProfileContent .containerHead {
border: 1px solid #000000 !important;
margin: 0px 30px -1px 30px !important;
}

.messageInner {
background-color: #1f273c !important;
}

.message {
margin-bottom: -2px !important;
border: 1px solid #000000 !important;
}

.messageContent {
color: #99a8c7 !important;
margin-bottom: -3px !important;
margin-top: -2px !important;
margin-right: -3px !important;
}

.messageContentInner {
border: 1px solid #000000 !important;
margin-top: -1px !important;
}

.messageContentInner a {
color: #ffffff !important;
}

.messageAuthor,
.userAvatar,
.userCredits,
.userSymbols {
text-align: left;
}

.userCredits p,
.color-1 {
background-color: #1f273c !important;
}

.color-2 {
background-color: #171e2f !important;
}

.quoteBox {
background-color: #0a101d !important;
color: #99a8c7 !important;
border: 1px solid #000000 !important;
}

.quoteBox a, legend, label {
color: #99a8c7 !important;
}

.smallButtons a,
.largeButtons a {
color: #99a8c7 !important;
border: 1px solid transparent !important;
}

.smallButtons li,
.largeButtons li {
border: 1px solid #000000 !important;
}

.smallButtons a:hover,
.largeButtons a:hover,
.smallButtons a:active,
.largeButtons a:active {
color: #ffffff !important;
}

fieldset {
background-color: #171e2f !important;
}

.iframeHTML,
.iframeHTML body,
iframeBody {
background-color: #1f273c !important;
}

textarea.editorCodeView {
font-size: small !important;
background-color: #ffffff !important;
color: #000000 !important;
}

.inputText, select, textarea {
background-color: #ffffff !important;
color: #000000 !important;
}

.pageNavigation li a {
border: 1px solid transparent !important;
background-color: #171e2f !important;
color: #99a8c7 !important;
}

.pageNavigation li {
border-color: #000000 !important;
}

.pageNavigation li.active span {
background-color: #99a8c7 !important;
}

.pageNavigation li.children ul {
background-color: #0a101d !important;
border-color: #000000 !important;
}

.messageBody img,
.signature img
{
vertical-align: baseline !important;
}

.codeBox {
border: 1px solid black !important;
}

.codeBox td {
background-color: #1f273c !important;
}

.codeBox div {
margin: -1px !important;
}

.messageBody {
color: #99a8c7 !important;
}

.codeBox td {
border-style: none !important;
color: #99a8c7 !important;
}

td.codeLineNumbers {
background-color: #0a101d !important;
}

.userProfileBox .containerHead,
.subTabMenu .containerHead {
border-style: none !important;
}

.userProfileBox .containerHead {
margin: -1px -1px 5px -1px !important;
border-bottom: 1px solid black !important;
}

.containerHead {
background-color: #171e2f !important;
background-image: none !important;
border: 1px solid black !important;
border-bottom: none !important;
}

.pmFolders a,
div.pmUsage {
background-color: #1f273c !important;
border: 1px solid black !important;
margin: -1px 0 0 0 !important;
}

div.pmUsage {
margin: -2px 0 -2px 0 !important;
}

.pmFolders a {
color: #ffffff !important;
}

.pmFolders a:active,
.pmFolders a:hover {
text-decoration: underline !important;
}

.infoBox {
margin-top: -13px !important;
}

.content {
border-style: none !important;
}

.activeTabMenu a {
background-color: #171e2f !important;
}

.messageHeader {
border-bottom: 1px solid #99a8c7 !important;
}

.messageFooterRight,
.signature,
.editNote {
border-top: 1px solid #99a8c7 !important;
}

}
