css:
@namespace url(http://www.w3.org/1999/xhtml);

/* cinetopia - blog-/cover-view with titles */
/* http://userstyles.org/styles/39159       */
/* author: iÃ¾                               */
/* additional date-entry (like in tableview) can be added with userscript: */
/* http://userscripts.org/scripts/show/89334                               */

@-moz-document url-prefix('http://cinetopia.ws/') {

  div.wp-pagenavi a.nextpostslink, div.wp-pagenavi a.previouspostslink{
    padding-left: 10px!Important;
    padding-right: 10px!Important;
  }
  div.wp-pagenavi a.nextpostslink:after{
    content: " Next Â»";
  }
  div.wp-pagenavi a.previouspostslink:after{
    content: " Prev Â«";
  }

}

@-moz-document url-prefix('http://cinetopia.ws/?layout=blog'), url-prefix('http://cinetopia.ws/page/') {

  #wrapper { width: 1164px!Important; }

  #content_wrapper { width: 1129px!Important; }

  #main { width:1134px!Important; }

  #main_content { width:910px!Important; }

  #main_content > div[style] { 
    width:910px!Important;  
    margin-left:10px !Important;
  }

  .container { 
    width: 285px!important; 
    margin:5px 5px!important; 
  }

  .container a .box, .container a:hover .box {
    width: 175px!Important;
    display: block!important;
    border: 0 none transparent!Important;
    margin-left: 100px!Important;
    position:absolute!Important;
    margin-top:-135px!Important;
    padding:5px;
  }

  .container a:hover .box {
    color: #666;
  }

  .container a .boxDate {
    width: 90px;
    display: block;
    margin-left: 186px;
    position:absolute;
    margin-top:-21px;
    text-align:center;
    color: #dddddd;
    border-top: 1px solid gray;
    border-bottom: 1px solid black;
    border-left: 1px solid gray;
    padding: 2px 4px;
    background-color:#111111;
  }

  .container a span.noPic{
    width: 100px;
    height: 40px;
    display: block;
    background-color: #cccccc;
    text-align: center;
    padding: 50px 0;
    color: #ffffff;
    font-weight: bold;
  }

  #sidebar { 
    margin-left:27px!Important; 
    background-color:#fff; 
    -moz-border-radius: 0 5px 5px 0; 
    border-radius: 0 5px 5px 0; 
  }

}
