@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") {
body{
background: url("http://img.freecodesource.com/myspace-layouts/images/layouts/1239161073-907-0.gif") #fff top center fixed !important;
}
body>#blueBar{ background: none !important;}
.jewel, .jewelBox{ border: none !important; }
.jewelBox{ background-color: rgba(255,255,255,1) !important; }
#headNavOut{ background: none !important; border: none !important }
#pageNav a { color: #111 !important; }
#pageNav a:hover { color: #333 !important; background: none !important; }
#navAccount{border: none !important;}
#navAccount ul{ background-color: rgba(255,255,255,1) !important;  border: none !important; }
#contentCol,#left_column,#right_column{ background-color: rgba(255,255,255,1) !important;  border: none !important;}
.inputtext,.add_comment_text,.uiMorePager, .box_header{ background-color: rgba(255,255,255,1) !important; }
.ufi_section, .photo_table, .photo_container{background: none !important; border: none !important;}
#photoborder{ background-color: rgba(255,255,255,1) !important;  border: none !important; padding: 10px 0;}
.profile_sidebar_ads{ height: 30px; }
#pagelet_chbox{border: none !important;}
#pageFooter, #rightCol, #contentArea, .UIStory, #mainContainer, .profile_top_wash{background: none !important; border: none !important;}

#contentCurve{ display: none !important; }
.gray_box {background-color: rgba(255,255,255,1) !important;  border: none !important;}
#pagelet_tab_content{ padding: 10px; }
.uiSideNav a { border: none !important; }
.uiSideNav li.selected a{background-color: rgba(255,255,255,1) !important;}
.uiSideNav a:hover{background-color: rgba(255,255,255,1) !important;}
}

 { Background Properties }
 div.content { background-color:transparent; border:none; border-width: 0px;}
 body {
	background-color:rgb(0,0,0);
	background-image:url('http://img.freecodesource.com/myspace-layouts/images/layouts/1239161073-907-0.gif');
	background-attachment:fixed;
	background-position:center center;
	background-repeat:no-repeat;
	border-top-width:5px;
	border-bottom-width:5px;
	border-left-width:5px;
	border-right-width:5px;
	border-color:rgb(102,0,0);
	border-style:double;
	padding-left:0px;
	padding-right:0px;
 }


{ Text Properties }
 div.content, li, p, div {font-family:georgia ; font-size:12px ; color:rgb(102,0,0) ; font-weight:bold; font-style:italic; }
 .btext, h4, div.basicInfoDetails h2, h3.moduleHead span span {font-family:georgia ; font-size:18px ; color:rgb(255,255,255) ; font-weight:bold; font

-style:italic; }
 div.commentsModule p.datePosted, .blacktext11, .blacktext12, ul.moduleList li h4, ul.moduleList li strong, span.count, span.count, .text {font-

family:georgia ; font-size:14px ; color:rgb(102,0,0) ; font-weight:bold; font-style:italic; }

{ Link Properties }
 a:active, a:visited, a:link {font-family:georgia ; color:rgb(102,0,0) ; font-weight:bold; font-style:italic; }
 a:hover {font-family:georgia ; color:rgb(255,255,255) ; font-weight:bold; font-style:italic; }

{ Tweaks }
 a.text, table div font a, table div div {visibility:hidden;}
 .hidenav {display:none}
 div.profileWidth {margin-top:-30px ;}
 div.profileWidth div {filter:alpha(opacity=0); opacity:0.0001}
 div.profileWidth div.clearfix, hidenav hidenav hidenav,
 div.profileWidth div div {filter:none; opacity:0.9999}
 div.profileWidth div.clearfix {position:relative; top:30px}

 
div#header, div#googlebar, div#topnav { background-color:transparent; }
#topnav ul { border-right:1px transparent solid; }
#topnav ul li { border-left:1px transparent solid; }

div.basicInfoDetails h2 {
height:28px;
display:block;
}
