css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nethack.wikia.com") {
  /* make main article content adapt to screen size */
  #WikiaPage { width: auto !important; }
  #WikiaArticle {
    margin-left: 1em !important;
    margin-right: 320px !important;
  }
  #WikiaRail {
    right: 0;
    position: absolute !important;
  }
  #WikiaMainContent { width: 100% !important; }
  body.oasis-one-column #WikiaArticle { margin-right: 1em !important; }

  /* hide cruft */
  #WikiaSpotlightsModule,
  #WikiaArticleBottomAd,
  #WikiaFooter,
  #TOP_RIGHT_BOXAD,
  #TOP_LEADERBOARD,
  #HOME_TOP_LEADERBOARD,
  #LEFT_SKYSCRAPER_2,
  #INVISIBLE_1,
  #INVISIBLE_2 { display: none !important; }
}
