css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://s4.zetaboards.com/The_Daily_Happening/") 
{

body
{
    background: black !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important;
    background-image: url('http://i45.photobucket.com/albums/f60/bededog/kiss2.jpg') !important;
}

#top, #logo, #submenu_bar, #submenu, #wrap, .c_user, .c_post, .c_nosig, .c_sig, .c_postfoot td, #fast-reply dd, .row1 td, .row2 td
{
    background: rgba(83, 50, 80, .7) !important;
}

.c_username, .c_postinfo, blockquote dl, #submenu a, #nav, .h2wrap, .c_postfoot a, .c_view, .c_topicfoot, #fast-reply dt, button, [class^="c_cat"], .c_sort, .cat-pages span, .cat-pages a, .cat-pagesjump i, .cat-pagesjump input, .cat-topicpages a, #top_menu li, #menu_ucp li a, #menu_pm li a, .c_next
{
    background: rgba(137, 60, 72, .5) !important;
}

.user_info, blockquote, #fast-reply textarea, .c_next a
{
    background: rgba(20, 35, 65, .2) !important;
}

.c_next a:hover
{
    background: rgba(20, 35, 65, .6) !important;
    -moz-transition: all .3s ease-in;
}

#nav a:hover, #submenu a:hover, .c_postfoot a:hover, button:hover, .cat-pages span, .cat-pages a:hover, .cat-pagesjump i:hover, .cat-pagesjump input:hover, .cat-topicpages a:hover, #menu_ucp li a:hover, #menu_pm li a:hover, #top_menu li:hover
{
    background: rgba(215, 78, 101, 1) !important;
    -moz-transition: all .3s ease-in;
    cursor: pointer !important;
}

#main_content, #header_l, #header_r, #side_r, #header, #side_l, #main, .post_sep, .h2left, .h2right, .h2center, thead td, tbody th, #foot td, .c_foot, #top_menu
{
    background: transparent !important;
}

#nav a, .c_postfoot a, #submenu a, button, .cat-pages span, .cat-pages a, .cat-pagesjump i, .cat-pagesjump input, .cat-topicpages a, #menu_ucp li a, #menu_pm li a, #top_menu li, .c_next a
{
    -moz-transition: all .3s ease-out;
}

#top, #logo, #submenu, #submenu_bar, #topic_viewer td, .post_sep, .user_info, blockquote dl, #nav, tbody th, .c_topicfoot, #fast-reply dt, #fast-reply dd, #foot td, .row1 td, .row2 td, .c_foot, .c_view, .c_sort, .cat-pages span, .cat-pages a, .cat-pagesjump i, .cat-topicpages a, #top_menu, #menu_ucp li a, #menu_pm li a, .c_next
{
    border: 0px !important;
}

blockquote, #fast-reply textarea, button, .cat-pagesjump input
{
    border-color: #808080 !important;
}

.user_info, blockquote, #submenu a, #nav a, .h2wrap, .c_postfoot a, #fast-reply textarea, button, .cat-pages span, .cat-pages a, .cat-pagesjump i, .cat-pagesjump input, .cat-topicpages a, .c_next a
{
    -moz-border-radius: 10px !important;
}

#top, blockquote dl, .c_view, #fast-reply dt
{
    -moz-border-radius: 10px 10px 0px 0px !important;
}

.c_topicfoot, #wrap, .c_sort
{
    -moz-border-radius: 0px 0px 10px 10px !important;
}

.c_postfoot td:not([class="c_footicons"]), #menu_ucp
{
    -moz-border-radius-bottomleft: 10px !important;
}

.c_footicons, #menu_pm
{
    -moz-border-radius-bottomright: 10px !important;
}

.c_username
{
    -moz-border-radius-topleft: 10px !important;
}

.c_postinfo
{
    -moz-border-radius-topright: 10px !important;
}

#top
{
    padding-bottom: 5px !important;
}

.cornercanvas, #footer, #footer_l, #footer_r, #header, td[style]
{
    display: none !important;
}

blockquote
{
    padding: 0px !important;
}

#submenu
{
    padding-bottom: 10px !important;
}

#nav
{
    padding: 0px !important;
    height: 29px !important;
}

#nav li
{
    position: relative !important;
    top: 6px !important;
}

#nav a
{
    padding: 5px !important;
    text-decoration: none !important;
}

#submenu a, .cat-topicpages a, .cat-pages a, .c_next a
{
    text-decoration: none !important;
}

.h2wrap
{
    margin-left: 0px !important;
    margin-bottom: -1px !important;
}

.c_postfoot a img
{
    visibility: hidden !important;
}

.c_postfoot a[href^="http://s4.zetaboards.com/The_Daily_Happening/profile/"]
{
    background-image: url(data:image/png;base64,) !important;
}

.c_postfoot a[href^="http://s4.zetaboards.com/The_Daily_Happening/msg/"]
{
    background-image: url(data:image/png;base64,) !important;
}

.c_postfoot a[href^="http://s4.zetaboards.com/The_Daily_Happening/comm/"]
{
    background-image: url(data:image/png;base64,) !important;
}

.c_postfoot a[href^="http://s4.zetaboards.com/The_Daily_Happening/post/"]
{
    background-image: url(data:image/png;base64,) !important;
}

.c_postfoot a.go_top
{
    background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAADwAAAARCAYAAACBzs+aAAAAAXNSR0IArs4c6QAAAAZiS0dEAIAAgACARCiTTQAAAAlwSFlzAAALEwAACxMBAJqcGAAAAAd0SU1FB9oLAQAhBtXhc0sAAABRSURBVFjD7dQxFYAwDEXRHhSAgAqrDbSCAZCAgMtATZDkLn9+Q9JaKaVEgoEHI0Psjsvnxh49+ECfwR1n9OB1rrlbljsWpWXJ9oTTBZdSyq+83+89IFymRPYAAAAASUVORK5CYII=) !important;
}

#copyright
{
    margin: 0px !important;
}

#top_menu li
{
    margin-right: 0px !important;
}

}
