css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mangable.com") {

body,td,table{
  background: #000 !important;
  color: #448 !important; 
  border: none !important}

select,input{background:  #277 !important;
  color:  #499 !important;
  font-weight: bold !important; }

#image{
  max-height: 1000px !important;
  opacity: 0.7  !important;
  margin-top: 0px  !important;
  padding-top: 0px !important;
  border-radius: 20px !important;} 

#thebreadcrumb,#breadcrumbs,#series_box,#extra_detail,
#header_container,#keyboard,#manga_rock,#set1, #footer,
#footer,#navigation_container, #chapter_choose_container{
  display:none !important;}

#series_chapter, 
#series_chapter_1, #series_chapter_2{
  background: #266 !important;
  height:100px !important;
  border:none !important;
  padding:3px!important;
  margin:2px !important;
  -moz-border-radius:5px;}

#selector{
  background: #266 !important;
  color: #499 !important;
  height:25px !important;
  border:none !important;
  padding:1px!important;
  margin-top:0px !important;
  -moz-border-radius:10px;}

#main_container{
  padding:0px 0px 0px!important;
  background: #000 !important;
  color: #448 !important;}

}
