css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Source: http://userstyles.org/styles/38556 */

@-moz-document 
url-prefix("https://accounts.google.com/Login"), 
url-prefix("https://accounts.google.com/ServiceLogin"), 
url-prefix("https://www.google.com/accounts/Login"), 
url-prefix("https://www.google.com/accounts/ServiceLogin") {

/* Disable preview announcement */
.announce-bar{display: none !important}

div[style="color: #666666; font-size: 75%;"],
table[bgcolor="#C3D9FF"], 
table>tbody>tr>td>b, 
td[width="65%"], 
td[width="75%"], 
body>table>tbody>tr>td[bgcolor='#ffffff'],
.footer,
.form-noindent[cellpadding="0"], 
.form-noindent[cellpadding="6"], 
.form-noindent[cellpadding="8"],
.hrule,
#gaia_table #ga-fprow td,
#gaia_table>tbody>tr>td>div[style="color: rgb(102, 102, 102); font-size: 75%;"], 
#gaia_table>tbody>tr>td>font, 
#gaia_table>tbody>tr>td>table,
#rememberme-row
{display: none !important}

/* Disable signin button
#signIn{display: none !important}
*/

.form-noindent, .form-noindent *
{border:0 none !important; background-color:#ffffff !important;}

/* New format */
.google-footer-bar, 
.google-header-bar,
.product-info,
.remember,
li
{display: none !important}
.sign-in {float:none !important;}
.content {max-width:none !important;}

}
