css:
@namespace url(http://www.w3.org/1999/xhtml);

/*-----------------------------------------------
 	ニコニコ動画（原宿）スッキリ表示 2012-09-19
-------------------------------------------------*/

/*********
---消去---
**********/
/* 表示したい場合は該当項目をコメントアウトして下さい */

/*= 全体の項目 =*/
@-moz-document url-prefix("http://www.nicovideo.jp/"){

 /*ニコニコ動画Zeroへの勧誘*/
 #zero_lead,
 #zero_lead_index,
 
 /*他のサイトへのリンク*/
 /*.siteHeaderGlovalNavigation,*/

 /*プレミアム会員へのリンク*/
  #siteHeaderNotificationPremium,
  .userDetail .regist,
 
 /*ヘッダのユーザーアイコン*/
 #siteHeaderUserIconContainer,
 
 /*ヘッダのお知らせアイコン*/
 /*#siteHeaderNotificationContainer,*/
 
 /*ニコニ広告*/
 #uad,
 #nicoads,

 /*DMMちゃんねる*/
 #R18-Banner,

 /*宣伝コメント（ランキング）*/
 .thumb_uad_header,
 .thumb_uad_point,

 /*宣伝コメント（トップページ・検索ページ）*/
 /*.uad_comment,*/

 /*フッター*/
 #footerWrap,
 #PAGEFOOTER,
 
 /*検索オプション*/
 /*#b_message > tbody > tr > td:first-child,*/
 
 /*ポップアップボタン（全て）*/
 /*.SYS_box_item_buttons,*/

 /*ポップアップボタン（宣伝ボタンのみ）*/
 .SYS_box_item_buttons > p > a[href^="http://uad.nicovideo.jp"],
 #video_table div[id^="MENU_"]

 { display: none !important; }
 }

/*= 動画ページの項目 =*/
@-moz-document url-prefix(http://www.nicovideo.jp/watch/),
url-prefix(http://www.nicovideo.jp/playlist/mylist/){

 /*ニコニコ市場*/
 #ichiba_placeholder,
 #ichiba_placeholder + .font12,

 /*ユーザー情報*/
 /*.owner_prof,*/

 /*この動画はコミュニティに登録..*/
 /*.des_2 > .font12:first-child,*/

 /*宣伝ボタン*/
 #BTN_add_ads,
 #WATCHHEADER a[href^="http://uad.nicovideo.jp/ads/"],

 /*マイメモリーなど*/
 #player_bottom_share,
 #outside,

 /*使い方*/
 #player_bottom_textlink,
 #WATCHFOOTER .font12.mb8p4

 { display: none !important; }
 }


/*********
---全体---
**********/
@-moz-document url-prefix(http://www.nicovideo.jp/){

/*= ヘッダに統合する =*/
/*-全体-*/
.siteHeaderGlovalNavigation{
	position: absolute;
	left: 400px;
}
/*- ニコニコTOP -*/
#header h1,
#header > div{
	position: fixed !important;
	z-index: 9999 !important;
}
.nofix #header h1,
.nofix #header > div{
	position: absolute !important;
	z-index: 9999 !important;
}
#header > *{
	top: -5px;
	left: 25px;
}
#header h1#logo5th{
	padding-top: 4px !important;
}
#header > .headerIcon{
	top: 0;
	left: 0;
	width: 1px !important;
	padding-top: 3px !important;
	padding-left: 10px !important;
}
body > div[style*="relative;"]{
	position: static !important;
}
#navigations{
	width: 100% !important;
}
.logo_sp_banner{
	top: 32px !important;
	left: 10px !important;
}
#header{
	padding-top: 42px !important;
}
.nofix #header{
	padding-top: 16px !important;
}
/*-ニコニコ動画-*/
#PAGEHEADER,
#head_logo > *,
#head_search,
#search_tab,
#search_input,
#search_input p,
#search_input input,
#head_search_form,
#head_search_form > table{
	position: fixed !important;
}
.nofix #PAGEHEADER,
.nofix #head_logo > *,
.nofix #head_search,
.nofix #search_tab,
.nofix #search_input,
.nofix #search_input p,
.nofix #search_input input,
.nofix #head_search_form,
.nofix #head_search_form > table{
	position: absolute !important;
}
#PAGEHEADER{
	top: 0;
	left: 0;
	width: 1px !important;
	z-index: 9999 !important;
}
/*-ニコニコ動画- 検索バー*/
#head_search{
	top: 0;
	left: 0;
}
#head_search_form{
	top: 0;
	left: 105px;
}
#search_input{
	width: 235px !important;
	top: 0px;
	left:  105px;
}
.nofix #search_input{
	left:  0px;
}
#search_input p,
#search_input input{
	height: 18px !important;
	padding: 0 !important;
}
#search_input > .typeText{
	top: 3px;
	left: 105px;
}
.nofix #search_input > .typeText{
	left: 0px;
}
#search_input > .typeSubmit,
#search_input > .typeSubmit > input{
	width: 16px !important;
}
#search_input > .typeSubmit{
	top: 6px;
	left: 315px;
}
.nofix #search_input > .typeSubmit{
	left: 210px;
}
/*-ニコニコ動画- 検索タイプ*/
#search_tab{
	width: 55px;
	top: 5px;
	left: 340px;
}
.nofix #search_tab{
	left: 235px;
}
#search_tab a{
	width: 5px !important;
}
/*-ニコニコ動画- ロゴ*/
#head_logo p{
	padding: 0 !important;
}
#head_logo #hidariue{
	left: 10px;
	top: 9px;
}
#head_logo #nicodou{
	left: 25px;
	top: 6px;
	width: auto !important;
	height: auto !important;
}
#siteHeaderInner{
	width: auto !important;
	max-width: none !important;
	padding: 0 20px 0 5px !important;
	height: 26px !important;
}
#siteHeaderInner .siteHeaderGlovalNavigation,
#siteHeaderInner .siteHeaderMenuList{
	padding-top: 0 !important;
}
#PAGEBODY{
	padding-top: 7px !important;
}
.nofix #PAGEBODY{
	padding-top: 17px !important;
}
/*= ここまで =*/

/*ヘッダ背景*/
#siteHeader{
	background: rgba(65, 74, 92, 0.96) !important;
	box-shadow: -3px 0 3px #555;
}
#siteHeaderInner{
	background: none !important;
}

/*ニコニコ動画Zeroへの勧誘*/
#zero_lead{
	margin-top: 26px !important;
	margin-bottom: -26px !important;
	background: #aaa !important;
}
.nofix #zero_lead{
	margin: 0 !important;
}
#zero_lead a{
	color: #fff !important;
}

/*ヘッダーリンク*/
#siteHeaderInner > ul > li{
	height: 26px !important;
	line-height: 24px !important;
}
#siteHeaderInner > ul > li:hover{
	background: rgba(0, 0, 0,  0.3) !important;
}
#siteHeaderInner > ul > li > a,
#siteHeaderInner > ul > li > a > span{
	border: none !important;
	text-decoration: none !important;
}
#siteHeaderUserNickNameContainer{
	font-size: 11px !important;
}
.siteHeaderNotNotifications{
	margin: 0 5px 1px 0px !important;
}
#siteHeaderInner > ul > li > a{
	color: #fff !important;
	font-size: 10px !important;
}

/*ドロップダウンメニュー*/
#siteHeaderInner > ul > li > ul{
	top: 25px !important;
	border: 1px solid #aaa !important;
	padding-bottom: 0 !important;
	background-color: rgba(245,245,245, 0.93) !important;
}
#siteHeaderInner > ul > li > ul > li,
#siteHeaderInner > ul > li > ul > li > a{
	background: none !important;
	font-size: 11px !important;
	height: 26px !important;
	color: #333 !important;
	overflow: hidden !important;
}
#siteHeaderInner > ul > li > ul >li > a:hover{
	background-color: #7c8fa5 !important;
	color: #fff !important;
}
#siteHeaderRightMenuContainer > li:not(:last-child),
#siteHeaderLeftMenuContainer > li:not(:last-child):not(:nth-last-child(2)){
	border-bottom: 1px dotted #aaa !important;
}

/*= 他のサービスへのリンクをドロップダウンメニュー化する =*/
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation *{
	display: none !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation:hover *{
	display: block !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation:before{
	content: "link";
	letter-spacing: 1px;
	padding: 0 4px;
	position: absolute;
	display: block;
	line-height: 14px;
	height: 14px;
	text-align: center;
	font-size: 9px !important;
	color: #fff;
	background-color: #777;
	box-shadow: 1px 1px 1px #444;
	border-radius: 3px;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation{
	top: 6px;
	height: 16px;
	width: 44px;
	border-radius: 3px;
	text-shadow: 1px 1px #444;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation:hover{
	text-shadow: 0 0 5px #eee !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li:first-child{
	margin-top: 15px !important;
}
#siteHeader #siteHeaderLeftMenuContainer{
	display: block !important;
	background: none !important;
	border: none !important;
	width: 201px !important;
	left: 102px !important;
	top: -136px !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > #siteHeaderLeftMenu.siteHeaderOther > a{
	display: none !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li,
#siteHeader #siteHeaderLeftMenuContainer > li{
	width: 100px !important;
	height: 26px !important;
}
#siteHeader #siteHeaderLeftMenuContainer > li{
	overflow: hidden !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li > a,
#siteHeader #siteHeaderLeftMenuContainer > li > a{
	width: 100px !important;
	height: 26px !important;
	color: #333 !important;
	font-size: 11px !important;
	line-height: 26px !important;
	padding-top: 0px !important;
	padding-bottom: 0px !important;
	text-shadow: none !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li > a:hover,
#siteHeader #siteHeaderLeftMenuContainer > li > a:hover{
	color: #fff !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li > a,
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li > a > span{
	border: none !important;
}
/*background*/
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li{
	background-color: rgba(220,220,220, 0.93) !important;
}
#siteHeader #siteHeaderLeftMenuContainer > li{
	background-color: rgba(245,245,245, 0.93) !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li:not(.siteHeaderOther) > a:hover,
#siteHeader #siteHeaderLeftMenuContainer > li > a:hover{
	background-color: #7c8fa5 !important;
}
/*boder*/
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li{
	border-left: 1px solid #aaa !important;
	border-right: 2px solid #ccc !important;
	border-bottom: 1px dotted #aaa !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li:first-child{
	border-top: 1px solid #aaa !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li:last-child{
	border-bottom: 1px solid #aaa !important;
}
#siteHeader #siteHeaderLeftMenuContainer > li:nth-child(2n+1){
	border-right: 1px solid #ddd !important;
}
#siteHeader #siteHeaderLeftMenuContainer{
	border-style: solid !important;
	border-color: #aaa !important;
	border-width: 1px 1px 1px 0 !important;
}
/*= ここまで =*/

/*タイトルロゴ*/
#header h1 > a:first-child:before{
	content: "ニコニコTOP";
	visibility: visible !important;
}
#header h1{
	width: 80px !important;
	height: auto !important;
	text-align: left !important;
	text-indent: 0 !important;
	overflow: hidden;
	background-image: none !important;
}
#header h1 > a:first-child{
	display: inline !important;
	float: none !important;
	color: #fff !important;
	text-decoration: none !important;
	font-weight: bold !important;
	font-size: 11px !important;
	white-space: nowrap;
	visibility: hidden;
}
#head_logo a[target="_top"] > img{
	display: none;
	font-size: 0 !important;
}
#head_logo a[target="_top"]:before{
	content: "ニコニコ動画";
}
#head_logo a[target="_top"]{
	display: block !important;
	color: #fff !important;
	text-decoration: none !important;
	font-weight: bold !important;
	font-size: 11px !important;
	white-space: nowrap;
}
#header h1 > a:hover,
#head_logo a[target="_top"]:hover{
	text-shadow: 0 0 3px #eee;
}

/*ランダムリンク*/
.headerIcon:not(#web_pc_uneune) > a:before,
#hidariue > a:before{
	content: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAJCAYAAAAPU20uAAAAeklEQVR42mKpqKhgQAK7gdgFiIWA+D1IgIkBFdyD4vcwAZCCmUBcjqYABEKBeBUL1EglIE4D4rNoVoFNUAZiRiA2AWJjqMRZKJ8R2Q3lULs7oQrBprFAJTugCsKAeA8Q34Va4wpT8B4qsRrKnwWlBRn////PgA8ABBgA20QbPPB6nPYAAAAASUVORK5CYII=) !important;
}
.headerIcon:not(#web_pc_uneune) > a > img,
#hidariue > a > img{
	position: fixed;
	top: 23px;
	left: 10px;
	border: 8px solid rgba(230, 230, 230, 0.9) !important;
	border-radius: 3px;
	box-shadow: 0 0 3px #999;
}
.headerIcon > a:not(:hover) > img,
#hidariue > a:not(:hover) > img,
#web_pc_uneune > a > img{
	display: none;
}
#web_pc_uneune{
	width: 8px !important;
}
.headerIcon,
#hidariue{
	width: 8px !important;
	height: auto !important;
}
.headerIcon > a,
#hidariue > a{
	display: block !important;
	text-decoration: none !important;
}

/*検索フォーム*/
.miniSearchWord,
#search_united_form #search_united,
#head_search_form #bar_search{
	width: 200px !important;
	height: 15px !important;
	font-size: 12px !important;
	font-weight: normal !important;
	padding: 3px 23px 1px 9px !important;
	border: none !important;
	border-radius: 9px;
	box-shadow: 0 1px 1px #777, inset 0 1px 1px #000;
	background-image: none !important;
	background-color: #fff !important;
	-webkit-appearance: none;
	-webkit-box-sizing: content-box !important;
	-moz-appearance: none;
	-moz-box-sizing: content-box !important;
}
#search_united_form > table,
#head_search_form > table{
	border: none !important;
}
#head_search_form > div{
	border: none !important;
	background: none !important;
}
.head_search_input{
	border: none !important;
	background: none !important;
	height: auto !important;
	width: auto !important;
}

/*検索ボタン*/
.miniSearchSubmit,
#search_input .typeSubmit,
#search_submit > input,
#head_search_form > div > table > tbody > tr > td:last-child > input{
	background: transparent url("data:image/png;base64,") no-repeat !important;
}
.miniSearch:not(:hover) > form > .miniSearchSubmit,
#search_input:not(:hover) >.typeSubmit,
#b_input:not(:hover) #search_submit{
	opacity: 0 !important;
}
.miniSearchSubmit{
	margin-left: -25px !important;
}
#search_submit{
	height: 16px !important;
	width: 0 !important;
	overflow: hidden !important;
	background: none !important;
	margin-left: -23px !important;
	padding-left: 16px !important;
	background-position: 10px !important;
}
#search_submit > input{
	margin-left: -16px !important;
	padding-left: 16px !important;
	height: 16px !important;
	width: 16px !important;
}

/*検索タイプ*/
#head_search_form a{
	background: none !important;
	font-size: 0px !important;
	text-decoration: none !important;
	margin-right: 4px !important;
	outline: none !important;
	visibility: hidden;
}
.miniSearchTypeList li:not(.active) a:not(:hover),
#search_frm_a .tab_0:not(:hover),
#head_search_form .head_ssw_0:not(:hover){
	opacity: 0.4 !important;
}
.msTypeVideoKW > a:before,
#search_frm_a a[href^="/search/"]:before,
#head_search_form a[onclick="submitSearch('/search', this); return false;"]:before{
	content: url(data:image/png;base64,) !important;
	visibility: visible;
}
.msTypeVideoTag > a:before,
#PAGEBODY > table.font12 > tbody > tr > td:last-child > nobr:first-child > a[href*="/tag/"]:before,
#search_frm_a a[href*="/tag/"]:before,
#head_search_form a[onclick="submitSearch('/tag', this); return false;"]:before{
	content: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAQAAAD8fJRsAAAApElEQVQYGQXBMS4GQQAG0Dezk9i/IHEKtQOoFQ6g10rEEVxCdMIRJApxhT8RhUSj04ne2h37ea8EPB69nIAyvl/cj0FE5CZJkiRf2YtowFz/FJA21oIG9MwKkB0FDVhMCih2QQMWPyqiAhrQTSpiENCAbjIoVg3QgG7WsFoFVKBbfbxtnxZLBSrQNp/fd+fXZ6/PdVMqiIiHy+PTAYcHV7e/+xH/gFRFWseiDucAAAAASUVORK5CYII=) !important;
	visibility: visible;
}
.msTypeMylist > a:before,
#search_frm_a a[href*="/mylist_search/"]:before,
#head_search_form a[onclick="submitSearch('/mylist_search', this); return false;"]:before{
	content: url(data:image/png;base64,) !important;
	visibility: visible;
}

/*動画アイコン*/
.mylistThumbs > img,
.mylist_usericon,
img.uad_img,
img.img_sq128,
img.img_sq96,
img.img_sq64,
img.img_sq48,
img.img_std128,
img.img_std96,
img.img_std64,
img.img_std48{
	border: 1px solid #aaa !important;
	background-color: #fff !important;
	padding: 2px !important;
	display: inline-block;
	font-size: 9px !important;
	vertical-align: top;
}

/*動画コメント*/
.vinfo_last_res2,
.vinfo_last_res{
	border-top: 1px solid rgba(180, 180, 180, 0.5) !important;
	border-bottom: none !important;
	border-right: none !important;
	border-left: none !important;
	background-color: rgba(205, 205, 205, 0.5) !important;
	padding: 12px !important;
	font-weight: normal !important;
	font-size: 10px !important;
	line-height: 1.4 !important;
}

/*宣伝動画サムネイル*/
.uad_thumbfrm,
.uad_thumbfrm_1,
.uad_thumbfrm_2{
	border: none !important;
	background: none !important;
}
.uad_thumbfrm_1,
.uad_thumbfrm_2{
	padding: 6px 2px 2px 6px !important;
}
.uad_thumbfrm_1 img{
	background-color: #E1E6EE !important;
	border: 2px solid #96ACD1 !important;
	padding: 3px !important;
	border-radius: 3px 3px 3px 0;
}
.uad_thumbfrm_2 img{
	background-color: #FBE9B0 !important;
	border: 2px solid #E6AB5B !important;
	padding: 3px !important;
	border-radius: 3px 3px 3px 0;
}
.uad_thumbfrm_1 .vinfo_length{
	background-color: #96ACD1 !important;
}
.uad_thumbfrm_2 .vinfo_length{
	background-color: #E0B16B !important;
}

/*宣伝コメント*/
div[id^="thumb_uad_msg"]:hover{
	display: block !important;
}
.uad_comment{
	background: rgba(0, 0, 0, 0.75) !important;
	border-radius: 3px;
	box-shadow: 0 0 3px #333;
	padding: 7px 13px !important;
	border: none !important;
	margin-left: 15px !important;
	z-index: 1;
	top: 0;
}
.uad_comment:after{
	content : "";
	border-width : 9px 7px;
	border-style : solid;
	border-color : #000 transparent transparent;
	opacity: 0.75;
	position: absolute;
	bottom: -18px;
	left: 5px;
}

/*時間*/
.vinfo_length > span{
	font-size: 9px !important;
	background: none !important;
	margin-top: -1px !important;
}
.vinfo_length:last-child{
	width: 38px !important;
	height: 13px !important;
	margin-top: 0px !important;
	color: #fff !important;
	background: #aaa;
	text-align: center !important;
	border-radius: 0 0 4px 4px;
}

/*ページナビゲーションボタン*/
.pager a{
	background: url("http://res.nimg.jp/img/recommendations/more_bg.png") !important;
}
.pager a,
.pager span{
	border-radius: 2px;
	padding: 3px 8px 2px 8px !important;
	border: 1px solid #d5d5d5 !important;
	color: #777 !important;
	font-size: 11px !important;
}
.pager a:hover,
.pager span{
	background-image: none !important;
	background-color: #ddd !important;
	border: 1px solid #ccc !important;
	color: #777 !important;
	background-image: -moz-linear-gradient(top, #dcdcdc, #e6e6e6 8%, #d0d0d0 98%) !important;
}

/*見出し*/
H1{
	font-size: 18px !important;
	line-height: 1.5 !important;
}

/*リンク色変更*/
a.watch:link{
	color: #59698E !important;
}
a.watch:visited{
	color: #35414F !important;
}
a.watch:hover{
	background-color: #B6C6D6 !important;
}
a.watch:active{
	color: #DE822E !important;
	background-color: #F4DC9B !important;
}

/*AutoPagerize*/
hr.autopagerize_page_separator{
	border: 1px dotted #ccc !important;
	margin: 9px 0 !important;
}
.autopagerize_page_info{
	width: 60px !important;
	text-align: center !important;
	padding: 1px 7px 2px !important;
	margin-bottom: 13px !important;
	background: #737E93 !important;
	color: #fff !important;
	font-size: 11px !important;
	border-radius: 8px;
	box-shadow: 0 1px 2px rgba(0, 0, 0, 0.25);
	text-shadow: 0 -1px 1px rgba(0, 0, 0, 0.25);
}
.autopagerize_page_info > .autopagerize_link{
	color: #fff !important;
	font-weight: bold;
	text-decoration: none !important;
}
#autopagerize_icon{
	width: 8px !important;
	height: 8px !important;
	margin-right: 5px !important;
	margin-top: 6px !important;
	border-radius: 5px;
	box-shadow: 0 0 2px #aaa;
