css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("drupalmodules.com") {

*{
  background:transparent !important;
  border-color:#EEEEEE !important;
  font-family: "Helvetica Neue",Helvetica,Arial,sans-serif !important;
  font-weight: normal !important;
  vertical-align:baseline !important;
  line-height: 2em !important;
}

body{
  background: -moz-linear-gradient(center top , #BDBDBD 48%, #E6E6E6 90%) repeat scroll 0 0 transparent !important;

}

#c1{
background: -moz-linear-gradient(center top , #DDDDDD 25%, #FFFFFF 90%) repeat scroll 0 0 transparent !important;
}

#mlist .text{
  padding:10px !important;
}

#target #top-pagers .apager,
#target #bottom-pagers .apager,
#target .dropshadow .text,
#mlist .text{
  background:white !important;
  -moz-box-shadow: 5px 5px 5px #BBBBBB;
  -webkit-box-shadow: 3px 3px 3px #BBBBBB;
}

#c2 *, #c3 *{
  font-size: 100% !important;
}

#c2 h2,
#c3 h2{
  color:#BBBBBB !important;
  font-size:150% !important;
}

#c2 .block div.title,
#c3 .block div.title{
  border:0px !important;
  background:#FAFAFA !important;
  margin-left:10px !important;
  margin-right:0px !important;
  margin-top:15px !important;
  border-bottom:1px solid #EFEFEF !important;
  border-right:1px solid #EFEFEF !important;
}

#c1{
  margin-top:10px !important;
  border-bottom:1px solid #EFEFEF !important;
  border-right:1px solid #EFEFEF !important;
}

#c2 .content,
#c3 .content{
  border:0px !important;
}

#nav *,
#nav{
  color:black !important;
  position:static !important;
}

#nav{
text-align:center;
  margin-bottom:20px !important;
  color:#CCCCCC!important;
}

#c1 div.title{
  border-bottom:0px !important;
}

#nav a{
  color:black !important;
}

#c1 div.title h2 a, .block div.title h2 a{
  color:#000000 !important;
  padding:10px !important;
  display:block !important;
}

#block-block-5{
  display:none !important;
}

#root{
  border:1px solid silver;
  background:white !important;
  -moz-box-shadow: 0px 0px 100px #333333;
  -webkit-box-shadow: 3px 3px 3px #BBBBBB;
}

#bignav{
  background:white !important;
  margin:0px !important;
  text-indent: 0px !important;
  -moz-box-shadow: 10px 10px 10px #BBBBBB;
  -webkit-box-shadow: 3px 3px 3px #BBBBBB;
}

#c2 .block,
#c4 .block{
  -moz-box-shadow: 3px 3px 10px #EFEFEF;
  -webkit-box-shadow: 3px 3px 3px #BBBBBB;
}

#bignav a{
  color:black !important;
  text-indent: 0px !important;
  text-align:center !important;
  line-height:30px !important;
  font-size:120% !important;
}

.view-content div.text{
  background: white !important;
}

}
