css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("pivotaltracker.com") {    

   body,td,p,div { font-family: Lucida Grande !important}
   
   #header { padding: 10px 10px 10px 6px !important }
   #header #projects_button { margin-right: 15px !important }
   #header .logo_and_projects_area { padding: 6px 8px !important }     

   #header .welcome_message { display: none }          

	#my_projects_menu li a { padding: 10px 8px !important }
   
   .nav_links .separator { display: none !important }
   .navigation .nav_links a { margin: 8px 5px !important; padding: 6px 8px !important; border-radius: 4px; -moz-border-radius: 4px; -webkit-border-radius: 4px } 

   .velocityLabel, .projectVelocity { 
		font-size: 12px !important;
		text-transform: uppercase; }
	#project_tabs li a, #project_tabs li.current
		{ padding: 7px 10px; font-size: 12px; margin-right: 8px;
			border-top-left-radius: 4px;  border-top-right-radius: 4px;
		 	-moz-border-top-left-radius: 4px;  -moz-border-top-right-radius: 4px;
		 	-webkit-border-top-left-radius: 4px;  -webkit-border-top-right-radius: 4px;
			}   
	#project_tabs li a { background-color: #5a8fbd !important; color: #e3f1fa !important }
	#project_tabs li a:hover { background-color: #2a5683 !important; color: #fff !important }

	#project_tabs li.current { font-weight: bold; color: #1c2e40 !important }
	#project_tabs ul { position: relative; left: -3px } 
	#project_tabs { background-color: #88C2E5 !important; padding-top: 5px }
	
	#main { padding-bottom: 0 !important} 
	
	.panelHeader { padding: 0 2px !important; }    
	.panelTitle { text-transform: uppercase; font-size: 10px! important; font-weight: bold; letter-spacing: 1px }
	
	.storyPreviewText span { font-size: 11px }
	.storyPreviewText { line-height: 14px !important; padding-top: 4px !important; padding-bottom: 4px !important }
	
	.storyPreviewInput { padding-left: 35px !important }
	.titleInputField { font-size: 12px; padding: 3px; width: 84%; position: relative; top: -8px }
	
	.storyPreviewButtons { padding-top: 6px !important }
	
	.storyDetails { padding: 10px !important }
	.storyDetailsTable { padding-top: 0 }
	.storyDetailsTable td { font-size: 10px; vertical-align: top; padding: 4px 0 }
	.storyDetailsTable td label { line-height: 12px !important; padding: 4px 0; display: block}
	.storyDetailsTable td select { width: 95px; font-size: 11px; margin-right: 5px }
	.storyDetailsTable td .storyDetailsButton input { font-size: 11px; margin-right: 3px }
	.noteTableDiv .noteText { margin-top: 5px }
	.emphasizedFieldLabel { margin-left: 0 !important}
		
	#controls_search_velocity_row { padding: 5px 0}   
	
	.currentTheoryLabel { text-transform: uppercase; display: block; padding-top: 3px; font-size: 8px }

}
