css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4chan.org") {


/* Remove Ads */
form[name="post"] > div{
	display: none !important
}
.bf{ /* Hurr durr buzzfeed */
	display: none !important;
}


/* Postblock */
input[type="checkbox"], .deletebuttons{ /* Might need to remove this for spoiler images */
	display: none !important;
}
input[name="spoiler"]{
display: inline !important;}
.postarea{
	border: none !important;
	color: #FFFFFF !important;
	padding: 0px !important;
	width: auto !important;
	height: auto !important;
	margin: auto 0px 0px auto !important;
	position: fixed !important;
	bottom: 0% !important;
	right: 0% !important;
	opacity: 0 !important;/*
display: none !important;*/
}
div.postareas{
display:none !important;}
div.postarea:hover{
	opacity: 0.8 !important;
}
td.rules{ /* Remove Rules */
	display: none !important;
}
input[type="file"]{
}
/*<hr> work*/
hr:nth-child(4),hr:nth-child(6),hr:nth-last-of-type(2){
display:none !important;
}
form hr{
	display: block !important;
}
th{
	display: none !important;
}
.doubledash{
	visibility: hidden !important;
}
/* Remove Logo */
.logo>img{
	display: none !important;
}
span#r{
	display: none !important;
}
/* Captacha  */
#recaptcha_switch_audio_btn, #recaptcha_whatsthis, #recaptcha_logo, #recaptcha_tagline{
	display: none !important;
}
/* Nav  */
.pages{
	margin-left: auto !important;
	margin-right: auto !important;
	width: 100% !important;
	text-align: center !important;
}
table.pages > tbody > tr > td:first-child{
	display: none !important;
}
input[accesskey="x"], input[accesskey="z"]{ /* Remove "Next" and "Prev" Buttons */
	display: none !important;
}
#header{
	margin-bottom: 5px !important;
	position: static !important;
}
span[style="left: 5px; position: absolute;"]{ /* Removes the annoying "Return" botton that has an absolute position */
	display: none !important;
}
/*#footer{
	display: none !important;
}*/

table[align="right"]{
	display: none !important;
}
/* Subject */
.filetitle, .replytitle{
	background: none !important;
}
/* Posts */
td.replyhl{ /* Highlighted Posts */
	border: none !important;
width: 100% !important;
padding: .2em !important;
}
td.postblock{
border: 1px solid #666 !important;}
td.reply, div#backwash_tooltip{ /* Replies, hl-replies, and the popups that come with backwash (for borders, padding, and curved edges) */

	border: none !important;
border: 1px outset #333 !important;
}
td.reply{
width: 100% !important;
padding: .2em !important;
}
div#backwash_tooltip{
}
form[name="delform"] > blockquote{ /* OP */
	width: auto !important;
	margin-right: auto !important;
	border: none !important;
}
form[name="delform"] > table{
	/*width: 100% !important;*/
        /*padding-left:10% !important;*/
}
.commentpostername, .postername{ /* Name */

	background: none !important;
}
.postertrip{
	background: none !important;
}
tr[class="replyhl"]{/* For /f/ */
	background: none !important;
}

/* remove ads for anyone not using ABP */
.bf, a[href*="affiliates.jlist.com"], img[src*="http://static.4chan.org/support/"] {display:none}
.postarea form div {border: none !important;}
div.postarea {padding: 0 !important;}

/*rs stuff may or may not work*/
@-moz-document url-prefix('http://rs.4chan.org'){
table.pages{
	display: none !important;
}
td[align="right"]{
	display: none !important;
}
/*end*/
}}
