css:
@-moz-document domain('tumblr.com'), url-prefix('http://www.tumblr.com/') , url-prefix("https://www.tumblr.com/") {



*{text-shadow:none !important; color:#7A7A7A !important;}



#dashboard_index{background: url('http://i55.tinypic.com/29xg845.png') center right #000000 repeat fixed !important;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#f9f9f9!important;}

ol#posts li.notification{background-color: #f9f9f9 !important; border-bottom: 2px solid #9d92a5 !important; color: #f9f9f9 !important;}

#right_column a#dashboard_switch_blog_menu_current:hover {background:#f9f9f9;}

#nav .nav_item.active{background-color:#f9f9f9}

#left_column #posts .post .permalink{background-color:#f9f9f9;}

#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background-color:#f9f9f9 !important;

background-image:none !important;

border:0px !important}

#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current

{background-image:none !important}

.notification_type_icon{display:none !important}

#left_column #posts .notification {

background-color:#f9f9f9 !important;

color:#666 !important;

border-bottom-color: #eee !important}

#new_post_notice_container{

color:#fff !important}

#new_post_notice_container a{

color:#fff !important}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i53.tinypic.com/wt9ir6.jpg') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}



}
