css:
/* AGENT_SHEET */
@namespace html url(http://www.w3.org/1999/xhtml); @-moz-document domain("iichan.hk"),domain("iichan.ru"),domain("410chan.ru") {
/** background **/
html {background: white repeat-x !important; background-image: url("https://lh5.googleusercontent.com/-9mPfq_bTK4o/T4qBmWVF8eI/AAAAAAAAAMQ/ZojfswVTVrQ/I/backgrnd777.jpg"),url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAVCAYAAABsZT8HAAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAANsAAADbABfWVZ+gAAAAd0SU1FB9oGAxEqHfMNphEAAAA5SURBVAjXY2SQUvnPwMDAwMLAyMQAYTAwQhmMDAwMDIzIUoyMOBkMmFIk6SJCMYxRH+rKwMDAwAAAGzcCz5PMGZsAAAAASUVORK5CYII=") !important; background-position: center 21px,0 0 !important; /*overflow-y: scroll !important;*/ overflow: visible !important;}
body {margin: 25px 8px 8px !important; padding: 0 !important; background: transparent !important; color: black !important; width: auto !important;}
html,body {font-size: 1em !important;} body>img[style*="moz-zoom-in"] {max-width: 100% !important; height: auto !important;}
hr {border-color: black !important; opacity: 0.15 !important; z-index: -1 !important;}

/** logo **/
.logo a,.logo {position: absolute !important; top: 0 !important; left: 0 !important; font-size: 11px !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important; color: #ccc !important; line-height: 21px !important; padding: 0 8px !important; text-decoration: none !important; width: auto !important; font-weight: bold !important;}
.logo a:hover {color: white !important; text-decoration: none !important;}

/** delform **/
#delform {padding: 10px 22px !important;}
table,td,tr {border: 0 none !important;}
.thread {display: block !important;}
.reply+.thread {display: none !important;}

/** text **/
.banner,.logo br,.logo img,.adminbar br,.adminbar select,body>hr,.thumbnailmsg,.doubledash,.rules,.theader {display: none !important;}
a {color: #034 !important; background-color: transparent !important; font-size: 1em !important;}
h2,p,label,ul,li,.reply {color: black !important;}
.postertrip {color: #567 !important;}
.filesize,.omittedposts {color: #666 !important; font-weight: normal !important; font-size: 1em !important; text-decoration: none !important;}
input,textarea,select,button,.postblock,.content {-moz-appearance: none !important; color: black !important; font-size: 12px !important; border: 1px solid #ccc !important; background: #eee !important;}
.unkfunc {color: #539 !important; background: none transparent !important;}
.filetitle {color:#002244 !important; font-size:18px !important;}
.postername,.commentpostername {color:#004A99 !important; font-weight: bold !important;}
.reflink {font-size: 0.8em !important; font-weight: bold !important;}
.reflink a {color: black !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important;}
.filesize {margin: 0 0 0 20px !important; padding: 0 !important; font-size: 0.8em !important;}
.spoiler {background: black !important; color: black !important;}
.spoiler:hover,.spoiler:hover a {color: white !important;}
.spoiler a {color: black !important;}
span.reply {padding: 0 !important;}

/** input fields **/
input[type="checkbox"] {border: 1px solid #A6A697 !important; background-color: white !important;}
input[type="file"] input[type="button"] {margin: 0 5px !important;}
input[type="file"] div,p,.unkfunc {margin: 0 !important;}
select input {border: 0 none !important;}
#postform input[name="captcha"] {float: left !important;}
#txt_btns input {padding: 0 !important; margin: 0 1px !important;}
input[type="password"] {width: 100px !important;}
input[type="checkbox"] {border: 1px solid #A6A697 !important; background-color: white !important;}
input[type="file"] input[type="button"] {margin: 0 5px !important;}

/** postform **/
#postform {
	display: inline-block !important;
	padding: 10px 20px !important;
	margin: 0 0 15px !important;
	background: rgba(255,255,255,0.4) !important;
	border: 1px solid #A6A697 !important;
	border-radius: 5px !important;
	width: auto !important;}
#postform table {margin: 0 !important;}
textarea {min-height: 100px !important;}

/** reply **/
.reply,#delform .reply,.userdelete {border: 1px solid #ccc !important;}
.reply,#delform .reply,.reflinkpreview,.userdelete {background: none #eee !important; border-radius: 5px !important;}
.highlight,#delform .highlight {background: #ddd !important; border: 1px solid #A6A697 !important; padding: 0 !important; margin: 0 !important; border-radius: 5px !important;}
blockquote {margin: 5px 5px 0 !important;}
.reflinkpreview {opacity: 1 !important;}

/** DCET **/
#DESU_panel input {margin:1px !important;}
.DESU_content {border: 1px solid #ccc !important; background: none #eee !important;}
.DESU_content .reply {border-radius: 0 !important; border: 0 none !important; margin: 0 !important;}
.DESU_content hr:first-child,.DESU_content hr:last-child {display: none !important;}
.DESU_content .reply>div,.DESU_cfgTabBack {border: 0 none !important;}
.DESU_cfgTabBack,.DESU_cfgTab,#DESU_cfgHead {border-radius: 0 !important;}
.DESU_content {position: absolute !important; z-index: 9000 !important; width: auto !important;}
#DESU_qarea {border-radius: 0 !important; border: 0 none !important; background: none transparent !important; width: auto !important;}
.DESU_fullImg {float: left !important;}
#DESU_select a {font-size: inherit !important; background: transparent !important; padding: 2px 6px 2px 4px !important;}
#DESU_select a:hover {text-decoration: underline !important;}

.adminbar a,.adminbar {text-decoration: none !important; font-size: 13px !important; float: none !important;}
.adminbar {padding: 3px 0 5px !important; text-align: center !important;}

li {background: transparent !important;}
ul {list-style: none !important;}
.category　ul a {background: none transparent !important;}
ul,li,h1,.list {margin: 0 !important; border: 0 none !important; padding: 0 !important;}
}

@-moz-document domain("iichan.ru"),domain("iichan.hk") {
.rfmap a {font-size: 70% !important;}

.category td {border: 0 none !important;}
.category a,.sublogo a,.adminbar a {line-height: 100% !important; text-decoration: none !important; color: #034 !important; background: transparent !important; padding: 0 !important; font-size: 13px !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important;}
.sublogo {color: #034 !important; text-align: left !important;}
.category a:hover {text-decoration: underline !important;}
.category,.sublogo,.adminbar {border: 1px solid #A6A697 !important; background: rgba(255,255,255,0.4) !important; margin: 2px 0 !important; padding: 2px 4px !important; border-radius: 5px !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important; font-size: 13px !important; clear: none !important;}
.sublogo {width: auto !important;}
.category .header {text-align: right !important; position: absolute !important; display: block !important; width: 169px !important; padding: 0 !important; color: #666 !important; background: transparent !important; z-index: -1 !important; font-size: 11px !important;}
.news {background: none transparent !important;}

}


@-moz-document domain("410chan.ru") {
.logo {font-weight: bold !important; cursor: default !important;}
.logo:after {content: "410chan";}
#sitestyles,.plus,.adminbar+.navbar,.adminbar~.adminbar {display: none !important;}

.adminbar a {font-weight: bold !important;}
.adminbar {position: absolute !important; right: 0; top: 0;}
.adminbar,.adminbar a {font-size: 11px !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important; color: #ccc !important; line-height: 21px !important; padding: 0 8px !important; text-decoration: none !important; font-style: normal !important;}
.adminbar a:hover {color: white !important;}
.adminbar~table td {padding: 0 !important;}

h2 {background: none transparent !important;}

body>h1~div,body>h1~ul {border: 1px solid #A6A697 !important; background: rgba(255,255,255,0.4) !important; margin: 2px 0 !important; padding: 2px 4px !important; border-radius: 5px !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important; font-size: 13px !important; clear: none !important;}
body>h1~div a:hover,body>h1~ul a:hover {text-decoration: underline !important;}
body>h1~h2 {
	line-height:	100%	!important;
	text-align:	right	!important;
	position:     relative	!important;
	display:	block	!important;
	width:		auto	!important;
	padding:	0 4px	!important;
	color:		#666	!important;
	background: transparent !important;
	z-index:	-1	!important;
	font-size:	11px	!important;
	margin:	5px 0 -16px 	!important;
	vertical-align: bottom	!important;
	font-family: tahoma,verdana,arial,sans-serif,Lucida Sans !important;
	text-decoration: none !important;
}

body>h1:only-of-type {position: absolute !important; top: 0 !important; left: 0 !important; font-size: 11px !important; font-family:tahoma,verdana,arial,sans-serif,Lucida Sans !important; color: #ccc !important; line-height: 21px !important; padding: 0 8px !important; text-decoration: none !important; width: auto !important; font-weight: bold !important; background: none transparent !important;}

.menu {margin: 2px auto !important; min-width: 300px !important; width: 50% !important;}
}
