css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gizmodo.com"), domain("lifehacker.com"), domain("kotaku.com"), domain("gawker.com"), domain("deadspin.com"), domain("jezebel.com"), domain("io9.com"), domain("jalopnik.com")
{


body
{
    background: url("http://i.imgur.com/vC21L.gif") !important;
}

#content .contentCollapsed, #content .excerpt, #content .entry, #comment_list .content, #comment_preview_body, #comments .comment, #sidebar, #content .post blockquote, #footer, #comments .commenttext, #content .post-permalink div.galleryThumbnails div.caption, .post h1, #commenterHeader, #miniFeed, .box, #main, .content, .rp_item, .cb_l, .ctext, .galleryThumbnails .caption, .gallery_related strong, #popTags, #latestPost, #commenter_submenu, .presence_control_external, .quicklink, .commenterpost, .friend_list p, .friend_list h4
{
    color: #d5d5d5 !important;
}

#content_container, #splashPosts, #contentSwitch div.selected, #footer, li.event blockquote, #container
{
    background: #000000 !important;
}

#sidebar, #sidebar .spacer, #rightbar, #rightbar table, #rightbar table tbody tr td, #comments .comment, #comments .entryContainer, .postBigPic .excerpt, #content .post blockquote, .postBigPicRight .excerpt, #content li.dateHeadline, #community_sidebar h3, .dateHeadline, .bigExcerptHolder, .postBigPic, .postmeta_permalink_wrapper, .commentbox_widget, #postgallery_container .postmeta
{
    background: #000 !important;
}

#popTags
{
    background: rgba(39, 39, 39, .5) !important;
    height: 40px !important;
    margin-top: 4px !important;
    padding: 5px !important;
}

.entryContainer textarea, .entryContainer .inputHolder .inner, blockquote, .cb_wrapper
{
    background: #222222 !important;
    color: #d5d5d5 !important;
}

#footer
{
    border: none !important;
    padding: 5px !important;
    width: 989px !important;
    -moz-border-radius-bottomleft: 0px;
    -moz-border-radius-bottomright: 0px;
    margin-bottom: 0px !important;
}

img[src$="footer-sitelist.png"]
{
    margin-left: -5px !important;
}

#container
{
    margin-top: 0px !important;
}

#wrapper
{
    padding-top: 10px !important;
}

.rp_link, .rp_link:hover
{
    width: 125px !important;
}

.bigExcerptHolder
{
    margin: 0px !important;
    padding: 5px !important;
    margin-top: -6px !important;
}

blockquote
{
    padding-top: 0px !important;
}

.quicklink blockquote
{
    padding: 5px !important;
}

blockquote p
{
    padding-top: 8px !important;
    margin: 0px !important;
}

#menubar
{
    z-index: 10000 !important;
}

.comment_box_container .extension_list
{
    border: none !important;
}

.postmeta_permalink_wrapper
{
    margin-top: -10px !important;
    padding: 10px !important;
    -moz-border-radius-bottomleft: 0px;
    width: 280px !important;
}

.postmeta
{
    margin-top: -10px !important;
    -moz-border-radius-bottomleft: 10px;
}

.postmeta_permalink, .related_posts, .pm_tools
{
    width: 280px !important;
    background: none !important;
}

.rp_item
{
    width: 260px !important;
    background: none !important;
}

.galleryThumbnails
{
    width: 280px !important;
}

#comments .commentbox_widget
{
    width: 97% !important;
    padding: 10px !important;
}

.cb_inputarea
{
    padding-right: 75px !important;
}

.cb_textarea
{
    width: 100% !important;
}

.comment, .threadWrapper
{
    width: 100% !important;
}

.ctext, .avatarspace
{
    padding-left: 10px !important;
    width: 98% !important;
}

.avatarspace
{
    padding-top: 5px !important;
}

#comments .entryContainer
{
    width: 430px !important;
}

#comments .showthreadcomments_box
{
    width: 595px !important;
}

#comments .comment, #content .post blockquote, blockquote
{
    -moz-border-radius: 0px !important;
}

a:link, a:visited
{
    color: #af9977 !important;
}

.topStory h1 a, #userLinks a, a.topTag, #popTags a, #rightbar table tbody tr td, .entryContainer .bttn, #content li.dateHeadline, .dateHeadline
{
    color: darkgrey !important;
}

.entryContainer .cn_collapseform
{
    color: black !important;
}

#splashPosts .topStory .tags a
{
    color: #555555 !important;
}

#sidebar
{
    -moz-border-radius-bottomright: 0px;
}

#skyscraper
{
    margin-top: -10px !important;
}

#header
{
    height: 255px !important;
}

#topMenu
{
    margin-bottom: -1px !important;
    background: none !important;
}

#contentSwitch div.selected
{
    border: 0px !important;
    -moz-border-radius-topright: 0px;
    -moz-border-radius-topleft: 0px;
}

#splashPosts
{
    padding-left: 15px !important;
    width: 700px !important;
    padding-bottom: 10px !important;
    -moz-border-radius-topleft: 0px;
    -moz-border-radius-topright: 0px;
}

#splashPosts .topStory
{
    padding-right: 10px !important;
}

#content_container
{
    padding-top: 10px !important;
}

#container
{
    padding-top: 0px !important;
}

#sidebar 
{
    display: none !important;
}

img[src$="clock.png"], img[src$="flame-gray.png"], img[src$="flame.png"], img[src$="comment.png"], img[src$="comment3.png"], img[src$="flame2.png"], img[src$="tool.reply.png"], img[src$="comment.heart.empty.png"], img[src$="clock-empty.png"], img[src$="flame-orange.png"], img[src$="comment-empty.png"], img[src$="comment-filled.png"]
{
    width: 0 !important;
    height: 15px !important;
}

img[src$="clock.png"], img[src$="clock-empty.png"]
{
    background-image: url(data:image/png;base64,) !important;
    padding-left: 15px !important;
}

img[src$="flame-gray.png"]
{
    background-image: url(data:image/png;base64,) !important;
    padding-left: 10px !important;
}

img[src$="flame.png"], img[src$="flame2.png"], img[src$="flame-orange.png"]
{
    background-image: url(data:image/png;base64,) !important;
    padding-left: 10px !important;
}

img[src$="comment.png"], img[src$="comment-empty.png"]
{
    background-image: url(data:image/png;base64,) !important;
    padding-left: 17px !important;
}

img[src$="comment3.png"], img[src$="comment-filled.png"]
{
    background-image: url(data:image/png;base64,) !important;
    padding-left: 15px !important;
}

.commenticon, .commenttools .cc, .commenttools .ca, .inflowNotification .downArrow, .inflowNotification .grayAlert, .notification .grayAlert, .comment_tool_reply, a.ganja_icon, a.ganja_icon:link, a.ganja_icon:visited, a.ganja_icon:active
{
    background-image: url(data:image/png;base64,) !important;
}

}

@-moz-document url-prefix("http://gizmodo.com/tag/"), url-prefix("http://lifehacker.com/tag/"), url-prefix("http://kotaku.com/tag/"), url-prefix("http://gawker.com/tag/"), url-prefix("http://deadspin.com/tag/"), url-prefix("http://jezebel.com/tag/"), url-prefix("http://io9.com/tag/"), url-prefix("http://jalopnik.com/tag/"), url-prefix("http://fleshbot.com/tag/")
{

#comments
{
    width: 800px !important;
}

.post
{
    width: 840px !important;
}

.postmeta
{
    float: right !important;
}

.dotted
{
    width: 790px !important;
}
}
