@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#PersonalToolbar .bookmark-item > .toolbarbutton-text-stack, 
#PersonalToolbar .bookmark-item > .toolbarbutton-text {
display: none !important;
}
#PersonalToolbar .bookmark-item:hover > .toolbarbutton-text {
display: block !important;
color: #00AF33 !important;
}
#PersonalToolbar .bookmark-item {
margin: 1px 0px !important;
padding: 0px 3px !important;
}

.bookmark-item[container="true"][label="Update Scanner's Pages"] {
list-style-image: url(data:image/png;base64,) !important;
-moz-image-region: auto !important;
}

.bookmark-item[container="true"][label="FIREFOX"] {
list-style-image: url(data:image/png;base64,%3D%3D) !important;
-moz-image-region: auto !important;
}

.bookmark-item[container="true"][label="WATCH TV"] {
list-style-image: url(data:image/png;base64,) !important;
-moz-image-region: auto !important;
}

.bookmark-item[container="true"][label="JUSTINA"] {
list-style-image: url(data:image/png;base64,) !important;
-moz-image-region: auto !important;
}

.bookmark-item[container="true"][livemark="true"][label="Ultra-Vid.com"] {
list-style-image: url(data:image/jpg;base64,) !important;
-moz-image-region: rect(.5px 15px 15px .5px) !important;
}

.bookmark-item[container="true"][livemark="true"][label="Fast Pass - TV"] {
list-style-image: url(data:image/x-icon;base64,) !important;
-moz-image-region: auto !important;
}

.bookmark-item[container="true"][livemark="true"][label="MyVideoLinks - Movies"] {
list-style-image: url(data:image/x-icon;base64,) !important;
-moz-image-region: auto !important;
}

.bookmark-item[container="true"][livemark="true"][label="MyVideoLinks - TV"] {
list-style-image: url(data:image/png;base64,) !important;
-moz-image-region: auto !important;
}

.bookmark-item[container="true"][livemark="true"][label="YIFY-Torrents RSS"] {
list-style-image: url(data:image/x-icon;base64,) !important;
-moz-image-region: auto !important;
}

.bookmark-item[container="true"][livemark="true"][label="Watchseries US - TV"] {
list-style-image: url(data:image/x-icon;base64,) !important;
-moz-image-region: auto !important;
}

/* Twistys OPENED  */
#bookmarksPanel treechildren::-moz-tree-twisty(open)
 {

height: 0px !important;


padding: 0px 0px 0px 0px !important;
margin: 0px 0px 0px 0px !important;
 }

/*******************************************************************************************/
/* Twistys CLOSED  */


#bookmarksPanel treechildren::-moz-tree-twisty(closed)
 {
height: 0px !important;


padding: 0px 0px 0px 0px !important;
margin: 0px 0px 0px 0px !important;
 }


/***********************************************************************************************/


#bookmarksPanel
{
margin: 0px 0px 0px -16px !important; 
}

#bookmarksPanel treechildren::-moz-tree-image(container) {
list-style-image: none !important;
}

#bookmarksPanel treechildren::-moz-tree-image(leaf) {
margin: 0px 0px 0px -16px !important;
}

/***********************************************************************************************/


/* MOVES THE WORD SEARCH in BOOKMARKS Sidebar  */

#bookmarksPanel label[value='Search:']
{
margin: 0px -4px 0px 32px !important;
}

/***********************************************************************************************/


/* Iconize Toolbar Buttons on Bookmarks Toolbar */

#PersonalToolbar toolbarbutton,
#PersonalToolbar dropmarker,
#PersonalToolbar #zoom-controls,
#PersonalToolbar .searchbar-textbox {
       border: none 0 transparent !important;
       border-radius: 0 !important;
       -moz-border-radius: 0 !important;/*Fx3.6*/
       box-shadow: none !important;
       -moz-box-shadow: none !important;/*Fx3.6*/
       height: 18px !important;
       -moz-appearance: none !important;
   }

#PersonalToolbar toolbarbutton[type="menu"] dropmarker, 
#PersonalToolbar #status-bar #firefm-statusbar-toolbar dropmarker,
#PersonalToolbar #scriptish-button dropmarker {
       visibility: collapse !important;
   }

#PersonalToolbar toolbarbutton[type="menu-button"] .toolbarbutton-icon {
       margin-right: -2px !important;
   }

#PersonalToolbar toolbarbutton[type="menu-button"] dropmarker {
       margin-left: -2px !important;
   }

#PersonalToolbar #stylish-toolbar-button {
       padding: 3px !important;
   }

#PersonalToolbar toolbarbutton,
#PersonalToolbar dropmarker {
       background: transparent none !important;
   }

/* Restores [Ext]Firefm old statusbar icon buttons borders */

#navigator-toolbox:not([customizing]) #PersonalToolbar #status-bar #firefm-statusbar-toolbar .toolbarbutton-icon {
       margin-top: -3px !important;
       margin-bottom: -2px !important;
       margin-right: 1px !important;
       margin-left: 1px !important;
       padding: 0.5px 1px !important;
       border-radius: 3px !important;
       -moz-border-radius: 3px !important;/*Fx3.6*/
       background: rgba(151,152,153,.05)
       -moz-linear-gradient(rgba(251,252,253,.95), rgba(246,247,248,.47) 49%, rgba(231,232,233,.45) 51%, rgba(225,226,229,.3)) !important;
       background-clip: padding-box !important;
       border: 1px solid !important;
       border-color: rgba(0,0,0,.12) rgba(0,0,0,.19) rgba(0,0,0,.38) !important;
   }

/* Prevents [Ext]firefm old statusbar Icons from moving when mouse hovered */
#PersonalToolbar #status-bar #firefm-statusbar-toolbar toolbarbutton {
      padding: 0px !important;
      border: none 0 transparent !important;
      height: 18px !important;
      -moz-appearance: none !important;
   }


/* Fixes [Ext]UI Fixer "Movable Status Bar Button" spacing */

#PersonalToolbar #fixer-status-bar {
      margin-top: 0px !important;
      padding: 0px 2px !important;
   }

/* [Ext]UI Fixer - Remove the resizer from the statusbar compatibility shim */

#PersonalToolbar #status-bar > .statusbar-resizerpanel {
      display: none !important;
   }
