css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://www.awkwardzombie.com/), url(http://awkwardzombie.com/), url-prefix(http://www.awkwardzombie.com/index.php), url-prefix(http://awkwardzombie.com/index.php) {
body {background:#ffffbe !important;position:relative;width:1000px!important;margin:auto!important;}
#content {width:auto!important;padding-top:0px !important;}
#archive, #flash_nav, #faq {margin-top:60px;background:#cc0000!important;border:3px solid #000000;-moz-border-radius:5px;}
#archive .title, #flash_nav .title, #faq .title {color:#000000 !important;}
#archive a, #flash_nav a, #faq a{color:#000000 !important;text-decoration:underline!important;}
#banner {position:absolute;left:-60px;}
#banner img {width:400px!important;height:50px!important;}
#site_content {padding:0 20px;}
#navbar {margin-top:105px!important;float:left;background:#ed1c24!important;border-width:7px 7px 7px 7px;border-color:#000000;border-style:solid;black;-moz-border-radius-topright:40px;-moz-border-radius-bottomright:40px;}
#navbar a {display:block;color:#ffffbe!important;}
#imgctrls:first-child {display:none !important;}
#comic_title {position:absolute!important;top:20px;left:440px;color:#cc0000!important;font-size:22px!important;font-weight:bold;}
.title.centered {font-size:22px !important; color:#cc0000 !important;font-weight:bold;}
#comic {border:2px solid #000000;-moz-border-radius:4px;}
#blarg {background:#ed1c24!important;border:3px solid #000000;-moz-border-radius:4px;}
#blarg a{color:#000000!important;}
.lineclear {clear:none!important;}
}
